import React, { useEffect, useState } from "react";
import { shallowEqual, useSelector } from 'react-redux';
import { showError } from "../../pages/Alert";
import Autocomplete from "@material-ui/lab/Autocomplete";
import { TextField } from "@material-ui/core";

export default function SalesOfficeList(props) {
    const companyId = useSelector((state) => state.auth.company, shallowEqual);
    const [salesOfficer, setSalesOfficer] = useState([]);
    const [salesOfficerValue, setSalesOfficerValue] = useState();

    useEffect(() => {
        document.getElementById('reportShowIframe').style.display = "none";
    }, [companyId]);

    const getSalesOfficerListByLocation = () => {
        const temp = [
            {
                "id": 1,
                "name": "sayma"
            },
            {
                "id": 2,
                "name": "Liton"
            },
            {
                "id": 3,
                "name": "Sunipa"
            },
        ]
        setSalesOfficer(temp)
    }
      const deleteSalesOfficer = (data) => {
        const temp = [...props.salesOfficerListPass]
        const index = temp.findIndex(obj => obj.saleOfficerId === data.saleOfficerId);
        temp.splice(index, 1);
        props.setSalesOfficerListPass(temp)
    }
    return (
        <>
            <div className="mt-5">
                <label className='level-title'>Sales Officer</label><br />
                <Autocomplete
                    name="salesOfficerId"
                    options={salesOfficer}
                    onKeyDown={getSalesOfficerListByLocation}
                    getOptionLabel={(option) => option.name}
                    value={salesOfficerValue}
                    onChange={(event, newValue) => {
                        setSalesOfficerValue(newValue)
                        if (newValue !== null) {
                            if (props.salesOfficerListPass.length > 0) {
                                let temp = [...props.salesOfficerListPass]
                                const index = temp.findIndex(data => data.saleOfficerId === newValue.id);
                                if (index > -1) {
                                    showError("This user already used");
                                    return;
                                } else {
                                    temp.push({
                                        "saleOfficerId": newValue.id,
                                        "name": newValue.name

                                    })
                                    props.setSalesOfficerListPass(temp)
                                }
                            } else {
                                const temp = [...props.salesOfficerListPass]
                                temp.push({
                                    "saleOfficerId": newValue.id,
                                    "name": newValue.name

                                })
                                props.setSalesOfficerListPass(temp)
                            }
                        }
                    }}
                    renderInput={(params) => (
                        <TextField {...params} label="Select Sales Officer*" />
                    )}
                />
            </div>

            <div
                style={{ border: "none", padding: "0 3px 3px 3px" }}>
                {props.salesOfficerListPass.length > 0 ? props.salesOfficerListPass.map(data => {
                    return (
                        <>
                            {
                                data.id !== "" ?
                                    <span className='mt-3 mr-5 areaLevel'
                                    >
                                        {data.name}
                                        <span className='deleteIcon'>
                                            <a
                                                className='fa fa-times text-white delete-file'
                                                onClick={() => deleteSalesOfficer(data)}
                                            ></a>
                                        </span>
                                    </span> : ""
                            }
                        </>
                    )
                }) : ""}
            </div>
        </>);
}
