import React from "react";
import { useHistory } from "react-router-dom";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import { ActionsColumnFormatter } from "./ActionsColumnFormatter";
import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../../../../_metronic/_helpers";
import { useIntl } from "react-intl";
const data = [
  {
    "id": 1,
    "code": "12535689",
    "duration": "12 Days Ago",
    "date": "10 APR 2022 10:23 AM",
    "distributor": "Bhai Bhai Enterprize",
    "balance": "570",
    "user": "Mmh Shohagh",
    "designation": "Accountant, Babylon Group",
    "collectionamount": "570,168,000",
    "collectionType": "Cheque",
    "status": "Settle",
    "invoiceNo": "Invoice No. 12345678"
  },
  {
    "id": 2,
    "code": "12535689",
    "duration": "12 Days Ago",
    "date": "10 APR 2022 10:23 AM",
    "distributor": "Bhai Bhai Enterprize",
    "balance": "57",
    "user": "Mmh Shohagh",
    "designation": "Accountant, Babylon Group",
    "collectionamount": "570,168,000",
    "collectionType": "Cash",
    "status": "Settled",
    "invoiceNo": "Invoice No. 12345678"
  },
  {
    "id": 3,
    "code": "12535689",
    "duration": "12 Days Ago",
    "date": "10 APR 2022 10:23 AM",
    "distributor": "Bhai Bhai Enterprize",
    "balance": "57",
    "user": "Mmh Shohagh",
    "designation": "Accountant, Babylon Group",
    "collectionamount": "570,168,000",
    "collectionType": "Online",
    "status": "Settled",
    "invoiceNo": "Invoice No. 12345678"
  },
]
export default function StockDataList({ setSingleAll, singleAll }) {
  const intl = useIntl();
  let history = useHistory();

  const openViewPage = (data) => {
    history.push('/inventory/stock/stock-view', { state: data, status:true });
  }
  const singleWiseSelectHandler = (data, isSelect) => {
    if (isSelect == true) {
      let temp = [...singleAll]
      temp.push(data)
      setSingleAll(temp)
    } else {
      if (singleAll.length >= 0) {
        let temp = [...singleAll]
        const index = temp.findIndex(obj => obj.id == data.id);
        temp.splice(index, 1);
        setSingleAll(temp)
      }
    }

  }
  const allSelectHandler = (allData, isSelect) => {
    if (isSelect == true) {
      setSingleAll(allData)
    }
    else {
      if (allData.length >= 0) {
        for (let i = 0; i < allData.length; i++) {
          const index = singleAll.findIndex(obj => obj.id == allData[i].id);
          singleAll.splice(index, 1);
          setSingleAll(singleAll)
        }
      }
    }

  }
  const columns = [
    {
      dataField: "code",
      text: "PRODUCTS INFO",
      formatter: (cellContent, row) => (
        <>
            <span className="text-muted">SKU X1253689</span><br />
            <strong>Seaconazole&nbsp;5SC 50 ml&nbsp;*&nbsp;40</strong><br />
            <span className="text-muted">Fungicide/</span>
        </>
      )
    },
    {
      dataField: "sales_officer",
      text: "QTY",
      headerClasses:"text-center",
      classes:"text-center",
      formatter: (cellContent, row) => (
        <>
          <span className="light-success-bg mr-5 p-2 rounded dark-success-color"><strong>54,551</strong></span>
          <span className="light-blue-bg mr-5 p-2 rounded dark-blue-color"><strong>1,599</strong></span>
          <span className="light-warning-bg mr-5 p-2 rounded dark-warning-color"><strong>500</strong></span>
          <span className="light-danger-bg mr-5 p-2 rounded dark-danger-color"><strong>100</strong></span>
        </>
      )
    },
    {
      dataField: "sales_officer",
      text: "W.A. Rate",
      formatter: (cellContent, row) => (
        <>
          <strong>{row.balance}</strong><br />
        </>
      )
    },
    {
      dataField: "action",
      text: intl.formatMessage({ id: "MENU.ACTION" }),
      formatter: ActionsColumnFormatter,
      formatExtraData: {
        openViewPage: openViewPage,
      },
      classes: "text-center",
      headerClasses: "text-center",
      style: {
        minWidth: "100px",
      },
    },
  ];

  return (
    <>
      <BootstrapTable
        wrapperClasses="table-responsive"
        classes="table table-head-custom table-vertical-center overflow-hidden"
        bootstrap4
        bordered={false}
        keyField="id"
        data={data}
        columns={columns}
        selectRow={
          {
            mode: 'checkbox',
            onSelect: (row, isSelect, rowIndex, e) => {
              singleWiseSelectHandler(row, isSelect);
            },
            onSelectAll: (isSelect, rows, e) => {
              allSelectHandler(rows, isSelect);

            }

          }
        }

        pagination={paginationFactory({ sizePerPage: 10, showTotal: true })}
      />
    </>
  );
}
