import React, { useEffect, useState } from "react";
import ReportTabsHeader from "../../tabs/ReportsTabsHeader";
import { Card } from "react-bootstrap";
import Button from '@material-ui/core/Button';
import { CardBody } from "../../../../../_metronic/_partials/controls";
import ReportProductCategoryTreeView from '../../../Common/ReportProductCategoryTreeView';
import CommonDateComponent from '../../../Common/CommonDateComponent';
import { shallowEqual, useSelector } from 'react-redux';
import { showError, showSuccess } from "../../../../pages/Alert";
import axios from "axios";
import MisReportBreadCrum from "../MisReportBreadCrum";
import moment from "moment";
import Flatpickr from "react-flatpickr";
import "flatpickr/dist/themes/material_green.css";

import { useIntl } from "react-intl";
import { toAbsoluteUrl } from "../../../../../_metronic/_helpers";
import LocationTreeView from '../../../SalesCollection/CommonComponents/LocationTreeView';
import Autocomplete from "@material-ui/lab/Autocomplete";
import { TextField } from "@material-ui/core";

const fields = {
    distributorId: "",
    storeId: "",
    depotId:"",
    companyId:""
}
export default function StockValuation() {
    const depotId = useSelector((state) => state.auth.company, shallowEqual);
    const userId = useSelector((state) => state.auth.user.userId, shallowEqual);
    const [distributors, setDistributors] = useState([]);
    const companyId = useSelector((state) => state.auth.company, shallowEqual);
    const [inputs, setInputs] = useState(fields);
    const [allDepot, setDepot] = useState([]);
    const [allStore, setStore] = useState([]);
    const [producCategoryTree, setProductCategoryTree] = useState([]);

    const [categoryLevel, setCategoryLevel] = useState("");

    const [locationTree, setLocationTree] = useState([]);
    const [allAccountingYear, setAllAccountingYear] = useState([]);
    const [salesOfficer, setSalesOfficer] = useState([]);
    const [salesOfficerValue, setSalesOfficerValue] = useState(null);

    // useEffect(() => {
    //     document.getElementById('pills-receivable-invoice-statement-cash-tab').classList.add('active')
    // }, []);;

    useEffect(() => {
        getLocationTreeList(companyId, userId)
    }, [companyId, userId]);

    useEffect(() => {
        document.getElementById('reportShowIframe').style.display = "none";
        getAllDepot(companyId);
        getStore();
        getProductCategoryTreeList(companyId);
        getAccountingYear(companyId)
    }, [companyId]);

    const getAccountingYear = (companyId) => {
        const URL = `${process.env.REACT_APP_API_URL}/api/accounting-year/companyWise/${companyId}`;
        axios.get(URL).then(response => {
            setAllAccountingYear(response.data.data);
        }).catch(err => {
            //showError(intl.formatMessage({ id: "COMMON.ERROR_STATUS" }));
        });
    }

    const getStore = () => {
        const URL = `${process.env.REACT_APP_API_URL}/api/store`;
        axios.get(URL).then(response => {
            setStore(response.data.data);
        });
    }

    const getAllDepot = (companyId) => {
        const URL = `${process.env.REACT_APP_API_URL}/api/depot/all-of-a-company/${companyId}`;
        axios.get(URL).then(response => {
            console.log(response.data.data);
            setDepot(response.data.data);
        });
    }
    const handleDepotChange = (e) =>{
        if (e.target.value !== null || e.target.value !== "") {
            setInputs({ ...inputs, depotId: parseInt(e.target.value) });
        }
    } 
    const handleStoreIdChange = (e) => {
        if (e.target.value !== null || e.target.value !== "") {
            setInputs({ ...inputs, storeId: parseInt(e.target.value)});
        }
    }
    const validate = () => {
        if (!inputs.startDate) {
            showError('Start date is required.');
            return false;
        }else if (!inputs.endDate) {
            showError('End date is required.');
            return false;
        }else if (inputs.endDate < inputs.startDate) {
            showError("End Date should be greater than Start Date");
            return false;
        }
        return true;
    }
    const download = () =>{
        if(!validate()){
            return false;
        }

        let queryString = '?';
        queryString += '&companyId=' + companyId;
        queryString += '&startDate=' +inputs.startDate;
        queryString += '&endDate=' + inputs.endDate;
        const URL = `${process.env.REACT_APP_API_URL}/api/reports/stock-valuation`+queryString;
        axios.get(URL, {responseType: 'blob'}).then(response => {
            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', "stockValuation.pdf");
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);

        }).catch(err => {
            showError();
        });

    }

    const preview = () =>{
        if(!validate()){
            return false;
        }

        let queryString = '?';
        queryString += '&companyId=' + companyId;
        queryString += '&startDate=' +inputs.startDate;
        queryString += '&endDate=' + inputs.endDate;
        const data = `${process.env.REACT_APP_API_URL}/api/reports/stock-valuation`+queryString;
        axios.get(data, {responseType: 'blob'}).then(response => {
            const file = new Blob([response.data], { type: "application/pdf" });
            const fileURL = URL.createObjectURL(file);
            const iframe = document.querySelector("iframe");
            if (iframe?.src) iframe.src = fileURL;
            document.getElementById('reportShowIframe').style.display = "inline-block";
        }).catch(err => {
            showError();
        });

    }
    const handlePaste = (e) => {
        e.preventDefault()
        return false;
    }
    const getLocationTreeList = (companyId, userId) => {
        const URL = `${process.env.REACT_APP_API_URL}/api/location-tree/locationTree/${userId}/${companyId}`;
        axios.get(URL).then(response => {
            const locationTree = response.data.data;
            setLocationTree(locationTree);
        }).catch(err => {
            showError("Cannot get Location Tree data.");
        });
    }

    const selectLocationTreeNode = (node) => {
        let id = "summary-id-" + node.id;
        const getId = document.getElementById(id);
        const getElements = document.getElementsByClassName('tree-nav__item_demo tree-nav__item-title');

        for (var i = 0; i < getElements.length; i++) {
            getElements[i].classList.remove('tree-nav-item');
        }
        if (getId) {
            getId.classList.add('tree-nav-item');
            // setSelectedLocation(node);
            // setLocationId(node.id);
            // setInputs({ ...inputs, "locationId": node.id })
        }
        // if (locationIds.length !== 0) { // for clear state
        //     locationIds.length = 0;
        // }
        //getLocationIds(node);
    }


    // const getCompanySelect = (row) => {
    //   getAllDepot(depotId);
    // }


    const getProductCategoryTreeList = (params) => {
        const URL = `${process.env.REACT_APP_API_URL}/api/product-category/list-info/` + params;
        axios.get(URL).then(response => {
            setProductCategoryTree(response.data.data.childProductCategoryDtoList);
        }).catch(err => {
            showError("Can not get product category tree data.");
        });
    }
    const selectTreeNode = (node) => {
        if(categoryLevel != ""){
            setCategoryLevel(node.treeLevel)
            if(categoryLevel.split('-').length == node.treeLevel.split('-').length){
                let id = "report-product-category-tree-id-"+node.id;
                const getId = document.getElementById(id);
                console.log(getId.className)
                if (getId.className == "d-flex justify-content-between tree-nav__item_demo tree-nav__item-title material-planner-tree-nav-item"){
                    getId.classList.remove('material-planner-tree-nav-item');
                }else {
                    getId.classList.add('material-planner-tree-nav-item');   
                }
            }else{
                let id = "report-product-category-tree-id-"+node.id;
                const getId = document.getElementById(id);
                const getElements = document.getElementsByClassName('tree-nav__item_demo tree-nav__item-title');
                for (var i = 0; i < getElements.length; i++) {
                    getElements[i].classList.remove('material-planner-tree-nav-item');
                }
                if (getId) {
                    getId.classList.add('material-planner-tree-nav-item'); 
                } 
            }

        }else{
            setCategoryLevel(node.treeLevel)
            let id = "report-product-category-tree-id-"+node.id;
            const getId = document.getElementById(id);
            if (getId.className == "d-flex justify-content-between tree-nav__item_demo tree-nav__item-title material-planner-tree-nav-item"){
                getId.classList.remove('material-planner-tree-nav-item');
            }else {
                getId.classList.add('material-planner-tree-nav-item');   
            }
        }


    }
    const getSalesOfficerListByLocation = () =>{
        const temp =[
            {
                "name":"sayma"
            },
            {
                "name":"Liton"
            },
            {
                "name":"Sunipa"
            },
        ]
        setSalesOfficer(temp)
    }
    return (
        <>
        <div>
            <MisReportBreadCrum menuTitle="Stock Valuation"/>
        </div>
            <div>
            <Card>
                <CardBody>
                    <div className="row">
                        {/* LOCATION TREE */}
                        <div className="col-lg-3" style={{ borderRight: "1px solid #F2F2F2" }}>
                        <div style={{ borderBottom: "1px solid #F2F2F2" }}>
                                    <label>
                                        <img src={toAbsoluteUrl("/images/loc3.png")}
                                            style={{ width: "20px", height: "20px", textAlign: "center" }}
                                            alt='Company Picture' />
                                        <strong style={{ marginLeft: "10px", color: "#828282" }}>Location (All)</strong>
                                    </label>
                                </div>
                                {/* TREE */}
                                <LocationTreeView tree={locationTree} selectLocationTreeNode={selectLocationTreeNode} />
                        </div>
                        {/* PRODUCT CATEGORY TREE */}
                        <div className="col-lg-3" style={{ borderRight: "1px solid #F2F2F2" }}>
                        <div style={{ borderBottom: "1px solid #F2F2F2" }}>
                                    <label>
                                        <img src={toAbsoluteUrl("/images/loc3.png")}
                                            style={{ width: "20px", height: "20px", textAlign: "center" }}
                                            alt='Company Picture' />
                                        <strong style={{ marginLeft: "10px", color: "#828282" }}>Product Category (All)</strong>
                                    </label>
                                </div>
                            <ReportProductCategoryTreeView
                            tree={producCategoryTree}
                            selectProductCategoryTreeNode={selectTreeNode} 
                            />
                        </div>
                        <div className="col-lg-6">
                            <span className="create-field-title">Stock Valuation Report</span>
                            {/* <div className='row'>
                                    <div className='form-group col-lg-8 first-level-top'>
                                        <label className='level-title'>Start Date<i style={{ color: "red" }}>*</i></label>
                                        <Flatpickr className="form-control" id="startDate" placeholder="dd-MM-yyyy"
                                            name='startDate'
                                            value={inputs.startDate ? moment(inputs.startDate, "YYYY-MM-DD").format("DD-MMM-YYYY") : ''}
                                            options={{ dateFormat: "Y" }}
                                            onChange={(value) => {
                                                setInputs({
                                                    ...inputs, "startDate": moment(new Date(value)).format("YYYY-MM-DD")
                                                })

                                            }}
                                        />
                                    </div>
                                </div>


                                <div className='row'>
                                    <div className='form-group col-lg-8 first-level-top'>
                                        <label className='level-title'>End Date<i style={{ color: "red" }}>*</i></label>
                                        <Flatpickr className="form-control" id="endDate" placeholder="dd-MM-yyyy"
                                            name='endDate'
                                            value={inputs.endDate ? moment(inputs.endDate, "YYYY-MM-DD").format("DD-MMM-YYYY") : ''}
                                            options={{ dateFormat: "d-M-Y" }} required
                                            onChange={(value) => {
                                                setInputs({
                                                    ...inputs, "endDate": moment(new Date(value)).format("YYYY-MM-DD")
                                                })
                                            }}
                                        />
                                    </div>
                                </div> */}
                                <div className="mt-5">
                                            <label className='level-title'>Sales Officer</label><br />
                                            <Autocomplete
                                                name="salesOfficerId"
                                                options={salesOfficer}
                                                onKeyDown={getSalesOfficerListByLocation}
                                                getOptionLabel={(option) => option.name}
                                                value={salesOfficerValue}
                                                onChange={(event, newValue) => {
                                                    setSalesOfficerValue(newValue)
                                                }}
                                                renderInput={(params) => (
                                                    <TextField {...params} label="Select Sales Officer*" />
                                                )}
                                            />
                                        </div>

                                        <div className='mt-5'>
                                        <label className='level-title'><span className="mr-1">Distributor</span></label>
                                        <Autocomplete
                                            name="distributorId"
                                            options={salesOfficer}
                                                onKeyDown={getSalesOfficerListByLocation}
                                                getOptionLabel={(option) => option.name}
                                                value={salesOfficerValue}
                                                onChange={(event, newValue) => {
                                                    setSalesOfficerValue(newValue)
                                                }}
                                            renderInput={(params) => (
                                                <TextField {...params} label="Select Distributor*" />
                                            )}
                                        />
                                    </div>
                                    <div className="mt-5 first-level-top">
                                        <label className='level-title'><span className="mr-1">Timeline</span><span className="text-danger">*</span></label>
                                        <select className="form-control" name="fiscalYearId">
                                            <option value="" selected>Select Fiscal Year</option>
                                            {allAccountingYear.map((accYear) => (
                                                <option key={accYear.fiscalYearName} value={accYear.id}>
                                                    {accYear.fiscalYearName}
                                                </option>
                                            ))}
                                        </select>
                                    </div>

                                <div className='mt-5'>
                                     <CommonDateComponent />
                                </div>
                                <div className="mt-5 first-level-top">
                                        <label className='level-title'><span className="mr-1">Report Format</span></label>
                                        <select className="form-control" id="reportFormate" name="reportFormate">
                                            <option value="">Select Report Format</option>
                                            <option value="PDF">Pdf</option>
                                            <option value="EXCEL">Excel</option>
                                        </select>
                                    </div>

                            {/* <div className='mt-5 row'>
                                <div className='col-xl-6'>
                                <label className='level-title'><span className="mr-1">Distributor</span></label>
                                <Autocomplete
                                    id="distributorId"
                                    name="distributorId"
                                    options={distributors}
                                    onKeyDown={getAllDistributorList}
                                    getOptionLabel={(option) => option.distributorName}
                                    onChange={(event, newValue) => {
                                        handleChangeDistributor(event, newValue)
                                    }}
                                    renderInput={(params) => (
                                        <TextField {...params} label="Select Distributor" />
                                    )}
                                />
                                </div>
                            </div> */}

                            <div>
                            <Button className="float-right mt-5" id="gotItBtn" variant="contained" 
                                color="primary"  //onClick={download}
                                >
                                Download
                            </Button>
                            <div className="float-right">
                                    <Button className="mt-5 mr-5" id="gotItBtn" variant="contained" color="primary" //onClick={preview}
                                    >
                                        Preview
                                    </Button>
                            </div>
                            </div>

                        </div>
                    </div>
                    <div className='mt-5'>
                        <iframe src="" className='w-100' id="reportShowIframe" height="500px"/>
                    </div>
                </CardBody>
            </Card>
            </div>
        </>);
}
