import React, { useState } from "react";
import { Card } from "react-bootstrap";
import { CardBody } from "../../../../../../../../../_metronic/_partials/controls";
import { toAbsoluteUrl } from "../../../../../../../../../_metronic/_helpers";
import SVG from "react-inlinesvg";
import { data } from "jquery";

export default function TicketsGenerate({ setList, list, data, addBookingCard }) {

    const handleTicketsGenerate = (id) => { 
        const temp = [...list]
        const index = temp.findIndex(obj => obj.id == id);
        temp[index].ticketsStatus= "REQUESTED";
        setList(temp)
    }

    const handleRejectChange = (id) => { 
        const temp = [...list]
        const index = temp.findIndex(obj => obj.id == id);
        temp[index].preStatus = temp[index].status;
        temp[index].status = "REJECTED"
        temp[index].preTicketsStatus = temp[index].ticketsStatus;
        temp[index].ticketsStatus= "REJECTED";
        setList(temp)
    }

    const handleUndoChange = (id) => { 
        const temp = [...list]
        const index = temp.findIndex(obj => obj.id == id);
        temp[index].status = temp[index].preStatus;
        temp[index].ticketsStatus=temp[index].preTicketsStatus
        setList(temp)
    }
    return (
        <>
            {/* FOR TICKETS ROW */}
            <Card className="mt-3">
                <CardBody>
                    <div className="row no-gutters">
                        <div className="col-4">
                            <span className="text-muted">{data.product_sku}</span><br />
                            <strong>{data.product_name} </strong><br />
                            <span className="text-muted">{data.category_name}</span>
                        </div>
                        <div className="col-4 text-center">
                            <span className="border rounded pl-5 pr-5 pt-5 pb-5"><strong style={{ fontSize: "1.5rem", fontWeight: "700" }}>{data.booking_quantity}</strong></span>
                        </div>
                        <div className="col-4">
                            {
                                data.item_status === "SALES_BOOKED" ? 
                                <div>
                                    {
                                        data.added === "ADDED"? "":
                                        <button className="btn float-right" style={{ background: "#F9F9F9", color: "#0396FF" }} onClick={()=>handleRejectChange(data.id)}>
                                        <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/red-delete.svg")} />
                                        </button>
                                    }
                                    {data.ticketsStatus === "CONFIRMED" || data.ticketsStatus === "REQUESTED" ? "" :
                                     <button className="btn float-right mr-5" style={{ background: "#F9F9F9", color: "#0396FF" }} onClick={()=>handleTicketsGenerate(data.id)}>
                                     <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/blue-ticket.svg")} />
                                     &nbsp;Tickets
                                 </button>
                                    }

                                </div> :
                                data.item_status === "REJECTED"?
                                <div>
                                <button className="btn float-right" style={{ background: "#6FCF97" }} onClick={()=>handleUndoChange(data.id)}>
                                    <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/undo-white.svg")} />
                                </button>
                                <span className="light-gray-bg dark-danger-color rounded mr-3 p-3 float-right">
                                    <strong>
                                        <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/red-delete.svg")} />
                                        &nbsp;Rejected
                                    </strong>
                                </span>
                            </div>:""
                            }    
                        </div>
                    </div>
                    <div className="mt-5" style={{ border: "1px solid rgba(235, 87, 87, 0.45)", width: "100%" }}></div>
                    <div className="mt-3">
                        <span className="dark-success-color">
                            <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/total-gray.svg")} />&nbsp;
                            <span className="text-muted mr-3">Stock</span>
                            <span className="text-danger mr-3"><strong>Qty. {data.stock_quantity}  (UOM 578 Kg)</strong></span>
                            <span className="text-warning"><strong>Short Qty. 100</strong></span>
                        </span>
                        <span className="float-right">
                            <span>
                                <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/price-gray.svg")} />
                                <span className="text-muted mr-2">&nbsp;Price</span>
                                {data.prePrice === "" ? "":
                                <del className="mr-2 text-muted">530</del>
                                }
                                <strong>500</strong>
                            </span>
                        </span>
                    </div>
                </CardBody>
            </Card>
        </>
    );
}
