import React , {useState, useEffect} from "react";
import axios from "axios";
import Popper from '@material-ui/core/Popper';
import { useIntl } from "react-intl";
import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../../../_metronic/_helpers";
import { SalesBookingList } from "../table/SalesBookingList";
import {Card, CardBody} from "../../../../../../_metronic/_partials/controls";
import { useHistory } from 'react-router-dom';
import {showError} from '../../../../../pages/Alert';
import LocationTreeView from '../../../CommonComponents/LocationTreeView';
import { BreadCrum } from "../../common/BreadCrum";
import { shallowEqual, useSelector } from "react-redux";
import SalesTabsHeader from "../../common/SalesTabsHeader"

export function SalesBooking(props) {
    const selectedCompany = useSelector((state) => state.auth.company, shallowEqual);
    let history = useHistory();
    let [singleAll, setSingleAll] = useState([]);
    const [anchorEl, setAnchorEl] = useState(null);
    //const [searchParams, setSearchParams] = useState({ companyId: 2, userLoginId: 4, locationId: null, accountingYearId: 1, semesterId: null });
    const [sessionData, setSessionData] = useState({userLoginId: 1, companyId: 2, accountingYearId: 1});
    const [searchParams, setSearchParams] = useState({...sessionData, locationId: '', semesterId: ''});
    const [locationId, setLocationId] = useState();
    const [bookingList, setBookingList] = useState([]);
    const [bookingSummary, setBookingSummary] = useState([]);
    const [locationTree, setLocationTree] = useState([]);
    const [selectedLocation, setSelectedLocation] = useState({});

    const intl = useIntl();
    useEffect(() => {
        document.getElementById('pills-sales-booking-tab').classList.add('active')
        getLocationTreeList();
        getBookingList(searchParams);
    }, []);

    const handleClick = (event) => {
        setAnchorEl(anchorEl ? null : event.currentTarget);
    };
    const open = Boolean(anchorEl);
    const id = open ? 'simple-popper' : undefined;
    const handleExport = () => {
        const exportData = [...singleAll]
        console.log(exportData)
    }


    const getBookingList = (obj) => { 
        const URL = `${process.env.REACT_APP_API_URL}/api/sales-booking/filtered-list`;
        axios.post(URL, JSON.stringify(obj), { headers: { "Content-Type": "application/json" } }).then(response => {
            let bookingList = response.data.data.salesBookingList;
            let bookingSummary = response.data.data.bookingSummary;
            setBookingList(bookingList);
            setBookingSummary(bookingSummary);
        }).catch(err => {
            showError(intl.formatMessage({id: "COMMON.ERROR_SALESBOOKING_DATA"}));
        });
    }

    const getLocationTreeList = () => {
        const URL = `${process.env.REACT_APP_API_URL}/api/location-tree/locationTree/${sessionData.userLoginId}/${sessionData.companyId}`;
        axios.get(URL).then(response => {
            const locationTree = response.data.data;
            setLocationTree(locationTree);
        }).catch(err => {
            showError(intl.formatMessage({id: "COMMON.ERROR_LOCATION_TREE"}));
        });
    }

    const selectLocationTreeNode = (node) => {
        let searchObj={...searchParams,locationId:node.id.toString()};
        setSearchParams(searchObj);
        setSelectedLocation(node);
        getBookingList(searchObj);
    }

    return (
        <>
            <div>
                {/* BREAD CRUM ROW */}
                <BreadCrum />
                {/* TODAY SALE ROW */}
                <SalesTabsHeader />
            </div>
            <div>
                <Card>
                    <CardBody>
                        <div>
                            <div className='row'>
                                {/* LEFT SIDE TREE ROW */}
                                <div className='col-xl-3' style={{ borderRight: "1px solid #F2F2F2" }}>
                                    <div style={{ borderBottom: "1px solid #F2F2F2" }}>
                                        <label>
                                            <img src={toAbsoluteUrl("/images/loc3.png")}
                                                style={{ width: "20px", height: "20px", textAlign: "center" }}
                                                alt='Company Picture' />
                                            <strong style={{ marginLeft: "10px", color: "#828282" }}>Location (All)</strong>
                                        </label>
                                    </div>
                                    {/* TREE */}
                                    <LocationTreeView tree={locationTree}
                                                      selectLocationTreeNode={selectLocationTreeNode}/>
                                </div>
                                {/* RIGHT SIDE LIST ROW */}
                                <div className='col-xl-9'>
                                    {/* SEARCHING AND FILTERING ROW */}
                                    <div className="row">
                                        <div className="col-xl-3">
                                            <div style={{ position: "absolute", padding: "7px", marginTop: "3px" }}>
                                                <img src={toAbsoluteUrl("/images/search.png")} width="20px" height="20px" />
                                            </div>
                                            <form className="form form-label-right">
                                                <input type="text" className="form-control" name="searchText"
                                                    placeholder="Search Here"
                                                    style={{ paddingLeft: "28px" }}
                                                />
                                            </form>
                                        </div>
                                        <div className="col-xl-9 d-flex justify-content-end">
                                            <div>
                                                <button className='btn' aria-describedby={id} onClick={handleClick} style={{ color: "#828282" }}>
                                                    Fiscal Year 2022-23&nbsp;
                                                    <i className="bi bi-chevron-down"></i>
                                                </button>
                                                <Popper id={id} open={open} anchorEl={anchorEl}>
                                                    <div className='dropdown-content'>
                                                        <div className="row">
                                                            <div className="column">
                                                                <h6>Category 1</h6>
                                                                <a href="#">Link 1</a>
                                                                <a href="#">Link 2</a>
                                                                <a href="#">Link 3</a>
                                                            </div>
                                                            <div className="column">
                                                                <h6>Category 2</h6>
                                                                <a href="#">Link 1</a>
                                                                <a href="#">Link 2</a>
                                                                <a href="#">Link 3</a>
                                                            </div>
                                                            <div className="column">
                                                                <h6>Category 3</h6>
                                                                <a href="#">Link 1</a>
                                                                <a href="#">Link 2</a>
                                                                <a href="#">Link 3</a>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </Popper>
                                            </div>
                                            <div>
                                                <button className="btn filter-btn">
                                                    <i className="bi bi-funnel"></i>&nbsp;Filter
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                    {/* ALL SUMMARY ROW */}
                                    <div className='row ml-2'>
                                        <div className='col-xl-3 sales-data-chip' style={{ borderRadius: "5px 0px 0px 5px" }}>
                                            <div className="d-flex">
                                                <div className="dark-gray-color">
                                                    <i className="bi bi-geo-alt"></i>
                                                </div>
                                                <div className="ml-2">
                                                    <span>
                                                        <span className="dark-gray-color"
                                                            style={{ fontWeight: "500" }}>{intl.formatMessage({id: "LOCATION.AREA"})}</span>
                                                        <p><strong>{selectedLocation.locationName?selectedLocation.locationName:'All'}</strong></p>
                                                    </span>
                                                </div>
                                            </div>
                                        </div>
                                        <div className='col-xl-3 sales-data-chip'>
                                            <div className="d-flex">
                                                <div className="dark-gray-color">
                                                    <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/box.svg")} width="15px" height="15px" />
                                                </div>
                                                <div className="ml-2">
                                                    <span>
                                                        <span className="dark-gray-color"
                                                            style={{ fontWeight: "500" }}>{intl.formatMessage({id: "SALES_BOOKING.TOTAL_BOOKING"})}</span>
                                                        <p><strong>{bookingSummary.total_booking_quantity}</strong></p>
                                                    </span>
                                                </div>
                                            </div>
                                        </div>
                                        <div className='col-xl-3 sales-data-chip' style={{ borderRadius: "0px 5px 5px 0px" }}>
                                            <div className="d-flex">
                                                <div className="dark-gray-color">
                                                    <img src={toAbsoluteUrl("/images/LineChart.png")} width="24px" height="24px" />
                                                </div>
                                                <div className="ml-2">
                                                    <span>
                                                        <span className="dark-gray-color"
                                                            style={{ fontWeight: "500" }}>{intl.formatMessage({id: "SALES_BOOKING.BOOKING_AMOUNT"})}</span>
                                                        <p><strong>à§³{bookingSummary.total_booking_amount}</strong></p>
                                                    </span>
                                                </div>
                                            </div>
                                        </div>
                                        <div className='col-xl-3 sales-data-chip' style={{ background: "#F9F9F9" }}>
                                            <button className="btn float-right export-btn" onClick={handleExport}>
                                                <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/up-arrow.svg")} width="15px" height="15px" />
                                            </button>
                                        </div>
                                    </div>
                                    {/* TABLE ROW */}
                                    <div className='mt-5'>
                                        <SalesBookingList setSingleAll = {setSingleAll} singleAll={singleAll} bookingList = {bookingList}/>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </CardBody>
                </Card>
            </div>
        </>
    );
}
