import React, {useState, useEffect} from 'react';
import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../../../_metronic/_helpers";
import { useHistory, useLocation } from "react-router-dom";
import {
    Card,
    CardBody,
} from "../../../../../../_metronic/_partials/controls";
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import clsx from 'clsx';
import { useIntl } from "react-intl";
import axios from 'axios';
import {showError} from '../../../../../pages/Alert';
import { CollectionSummaryTable } from "../collection-data-table/CollectionSummaryTable"
import { CollectionAmountTable } from "../collection-data-table/CollectionAmountTable"
import { InprogressPendingView } from "./InprogressPendingView"
import { VerifiedInprogresView } from "./VerifiedInprogresView"
import { RejectToRejectView } from "./RejectToRejectView"
import { InprogrssPending } from "../card-reports/InprogrssPending"
import { VerifiedInprogress } from "../card-reports/VerifiedInprogress"
import { RejecToReject } from "../card-reports/RejecToReject"
import CollectionBreadCrum from '../../common/CollectionBreadCrum'
import CollectionTodaySales from "../../common/CollectionTodaySales"
const useStyles = makeStyles(() => ({
    root: {
        backgroundColor: 'white',
        boxShadow: "0px 3px 5px rgba(0, 0, 0, 0.03)",
        padding: 8,
        borderRadius: '50%'
    },
}));
export function CollectionDataView() {
    const history = useHistory();
    const location = useLocation();
    const paymentInfo = location.state.state;

    const [activeStep, setActiveStep] = React.useState(0);
    const [paymentAdjustmentStatus, setPaymentAdjustmentStatus] = useState();

    const intl = useIntl();

    useEffect(() => {
        document.getElementById('pills-payment-collection-data-tab').classList.add('active');
        getPaymentAdjustmentStatus();
    }, []);

    let paymentVerification = paymentInfo.approvalStatus;
    let paymentAdjustment = paymentAdjustmentStatus;


    const getPaymentAdjustmentStatus = () => {
        const URL = `${process.env.REACT_APP_API_URL}/api/payment-collection/payment-adjustment-status/${paymentInfo.id}`;
        axios.get(URL).then(response => {
            const paymentAdjustmentStatusa = response.data.data;
            setPaymentAdjustmentStatus(paymentAdjustmentStatusa);
        }).catch(err => {
           showError(intl.formatMessage({id: "COMMON.ERROR_PAYMENTADJUSTMENT_STATUS_DATA"}));
        });
    }

    const CustomStepIcon = (props) => {
        const classes = useStyles();
        const stepIcons = {
            1: <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/ok.svg")} width="15px" height="15px" />,
            2: paymentVerification == ("DRAFT" && "PENDING") ? <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/status.svg")} width="15px" height="15px" /> : paymentVerification == "APPROVED" ? <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/ok.svg")} width="15px" height="15px" /> : <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/reject.svg")} width="15px" height="15px" />,
            3: paymentAdjustment == "Pending" ? <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/payment.svg")} width="15px" height="15px" /> : paymentAdjustment == "In Progress" ? <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/payment-blue.svg")} width="15px" height="15px" /> : <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/reject.svg")} width="15px" height="15px" />,
        };

        return (
            <div className={clsx(classes.root)}>
                {stepIcons[String(props.icon)]}
            </div>
        );
    };
    const paymentCollectionDiv = <div className='text-center' style={{ fontSize: "0.83rem" }}>
        <span style={{ color: "#828282" }}>Payment Collection</span><br />
        <span className='text-muted'>Collected On</span>&nbsp;<strong>{paymentInfo.paymentDate}</strong><br />
        <span style={{ color: "#828282" }}>Payment Nature</span>&nbsp;<strong>{paymentInfo.paymentNature}</strong>
    </div>

    const paymentVerificationDiv = <div className='text-center' style={{ fontSize: "0.83rem" }}>
        <span style={{ color: "#828282" }}>Payment Verification</span><br />
        {/* <span className='text-muted'>{paymentInfo.approvalStatus}</span>&nbsp; */}
        <strong
            style={
                    paymentVerification !== "REJECTED" ? { color: "#0396FF" } :
                    paymentVerification === "REJECTED" ?  { color: "red" } :{  color: "#6fcf97" }
            }
        >{paymentInfo.approvalStatus}</strong>
    </div>

    const paymentAdjustmentDiv = <div className='text-center' style={{ fontSize: "0.83rem" }}>
        <span style={{ color: "#828282" }}>Payment Adjustment</span><br />
        <span className='text-muted'>Payment Adjustment</span>&nbsp;
        <strong
            style={
                paymentAdjustment == "In Progress" ? { color: "#0396FF" } :
                paymentAdjustment == "Pending" ? { color: "none" } : { color: "red" }
            }
        >{paymentAdjustmentStatus}.toString</strong>
    </div>

    const getSteps = () => {
        return [paymentCollectionDiv, paymentVerificationDiv, paymentAdjustmentDiv];
    }
    const steps = getSteps();
    const handleBackCollectionDataChange = () => {
        history.push("/salescollection/payment-collection/collection-data")
    }
    return (
        <>
            <div>
                {/* BREAD CRUM ROW */}
                <CollectionBreadCrum />
                {/* TODAY SALE ROW */}
                <CollectionTodaySales />
            </div>
            {/* CONTENT ROW */}
            <div>
                <Card>
                    <CardBody>
                        {/* HEADER ROW */}
                        <div className='row'>
                            <div className='col-xl-8 row'>
                                <span className=" mr-5">
                                    <button className='btn' 
                                    onClick={handleBackCollectionDataChange}
                                    >
                                        <strong>
                                            <i class="bi bi-arrow-left-short sales-booking-view-icon"></i></strong>
                                    </button>
                                </span>
                                <span className="sales-booking-view-span mr-5">
                                    <span className="dark-gray-color"
                                        style={{ fontWeight: "500" }}>Payment No.</span>
                                    <p><strong>13213141</strong></p>
                                </span>
                                <span className="sales-booking-view-span  mr-5">
                                    <span className="dark-gray-color"
                                        style={{ fontWeight: "500" }}>Nature</span>
                                    <p><strong>Advance</strong></p>
                                </span>
                                <span className="sales-booking-view-span mr-5">
                                    <span className="dark-gray-color"
                                        style={{ fontWeight: "500" }}>Teritory</span>
                                    <p><strong>Cox’s Bazar</strong></p>
                                </span>
                            </div>
                            <div className='col-xl-4 d-flex justify-content-end'>
                                <div className="d-flex  mr-5">
                                    <div>
                                        <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/Lays.svg")} width="35px" height="35px" />
                                    </div>
                                    <div className="ml-3">
                                        <span>
                                            <span style={{ fontWeight: "500" }}><strong>Lay’s Seed Enterprize</strong></span>
                                            <p className="dark-gray-color"><i class="bi bi-telephone-fill" style={{ fontSize: "10px" }}></i>&nbsp;+880 1779 911 488</p>
                                        </span>
                                    </div>
                                </div>
                                <div>
                                    <button className='btn sales-credit-btn' style={{ padding: "0px 15px", borderRadius: "13px" }}>
                                        <span className='text-white' style={{ fontSize: "0.83rem" }}>
                                            Credit Balance<br />
                                            ৳55,606,521
                                        </span>
                                    </button>
                                </div>
                            </div>
                        </div>
                        {/* STEPPER ROW */}
                        <div className='sales-booking-view-stepper row'>
                            <div className='col-xl-12'>
                                <Stepper activeStep={activeStep} alternativeLabel>
                                    {steps.map(label => (
                                        <Step key={label}>
                                            <StepLabel StepIconComponent={CustomStepIcon}>{label}</StepLabel>
                                        </Step>
                                    ))}
                                </Stepper>
                            </div>
                        </div>
                        <div className="mt-5">
                            <ul class="nav nav-pills mb-3" id="pills-tab-order" role="tablist">
                                <li class="nav-item" role="presentation">
                                    <a class="nav-link active" id="pills-collection-summary-tab" data-toggle="pill" href="#pills-collection-summary" role="tab" aria-controls="pills-collection-summary" aria-selected="true">Collection Summary</a>
                                </li>
                                <li class="nav-item" role="presentation">
                                    <a class="nav-link" id="pills-payment-activities-reports-tab" data-toggle="pill" href="#pills-payment-activities-reports" role="tab" aria-controls="pills-payment-activities-reports" aria-selected="false">Activities & Reports</a>
                                </li>
                            </ul>
                            <div class="tab-content" id="pills-tab-orderContent">
                                {/* ORDER SUMMARY */}
                                <div class="tab-pane fade show active" id="pills-collection-summary" role="tabpanel" aria-labelledby="pills-collection-summary-tab">
                                    <div className='row order-table'>
                                        <div className='col-xl-6'>
                                            <CollectionSummaryTable />
                                        </div>
                                        <div className='col-xl-6 mt-5'>
                                            <Card style={{ borderRadius: "25px" }}>
                                                <CardBody>
                                                    <CollectionAmountTable />
                                                </CardBody>
                                            </Card>
                                        </div>
                                    </div>
                                </div>

                                {/* ACTIVITIES AND REPORTS */}
                                <div class="tab-pane fade" id="pills-payment-activities-reports" role="tabpanel" aria-labelledby="pills-payment-activities-reports-tab">
                                    <div className='order-table row p-5'>
                                        <div className='col-xl-6'>
                                            {
                                                paymentVerification == "inprogress" && paymentAdjustment == "pending" ?
                                                    <InprogressPendingView />
                                                    : paymentVerification == "verified" && paymentAdjustment == "inprogress" ?
                                                        <VerifiedInprogresView paymentVerification={paymentVerification} />
                                                        : paymentVerification == "reject" && paymentAdjustment == "reject" ?
                                                            <RejectToRejectView paymentVerification={paymentVerification} /> : ""
                                            }
                                        </div>
                                        <div className='col-xl-6'>
                                            <div>
                                                <div className='mt-3'>
                                                    <p className='report-title'>Reports</p>
                                                </div>
                                                {
                                                    paymentVerification == "inprogress" && paymentAdjustment == "pending" ?
                                                        <InprogrssPending />
                                                        : paymentVerification == "verified" && paymentAdjustment == "inprogress" ?
                                                            <VerifiedInprogress />
                                                            : paymentVerification == "reject" && paymentAdjustment == "reject" ?
                                                                <RejecToReject /> : ""
                                                }
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </CardBody>
                </Card>
            </div>
        </>
    );
}
