import React, { createContext, useContext, useState, useCallback } from "react";
import { isEqual, isFunction } from "lodash";
import { initialFilter } from "./DepotUIHelpers";

const DepotUIContext = createContext();

export function useDepotUIContext() {
  return useContext(DepotUIContext);
}

export const DepotUIConsumer = DepotUIContext.Consumer;

export function DepotUIProvider({ depotUIEvents, children }) {
  const [queryParams, setQueryParamsBase] = useState(initialFilter); 
  const setQueryParams = useCallback((nextQueryParams) => {
    setQueryParamsBase((prevQueryParams) => {
      if (isFunction(nextQueryParams)) {
        nextQueryParams = nextQueryParams(prevQueryParams);
      }

      if (isEqual(prevQueryParams, nextQueryParams)) {
        return prevQueryParams;
      }

      return nextQueryParams;
    });
  }, []);

  const value = {
    queryParams,
    setQueryParamsBase,
    setQueryParams,
    newDepotButtonClick: depotUIEvents.newDepotButtonClick,
    openEditDepotPage: depotUIEvents.openEditDepotPage,
    openDeleteDepotDialog: depotUIEvents.openDeleteDepotDialog,
  };

  return (
    <DepotUIContext.Provider value={value}>
      {children}
    </DepotUIContext.Provider>
  );
}
