import React from "react";
import { useHistory } from "react-router-dom";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import { ActionsColumnFormatter } from "./ActionsColumnFormatter";
import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../../../../../_metronic/_helpers";
import {useIntl} from "react-intl";
const data = [
  {
    "id": 1,
    "code": "12535689",
    "duration": "12 Days Ago",
    "date": "10 APR 2022 10:23 AM",
    "distributor":"Bhai Bhai Enterprize",
    "balance": "570,168,000",
    "user": "Mmh Shohagh",
    "designation": "Accountant, Babylon Group",
    "collectionamount": "570,168,000",
    "collectionType": "Cheque",
    "status": "Settle",
    "invoiceNo": "Invoice No. 12345678"
  },
  {
    "id": 2,
    "code": "12535689",
    "duration": "12 Days Ago",
    "date": "10 APR 2022 10:23 AM",
    "distributor":"Bhai Bhai Enterprize",
    "balance": "570,168,000",
    "user": "Mmh Shohagh",
    "designation": "Accountant, Babylon Group",
    "collectionamount": "570,168,000",
    "collectionType": "Cash",
    "status": "Settled",
    "invoiceNo": "Invoice No. 12345678"
  },
  {
    "id": 3,
    "code": "12535689",
    "duration": "12 Days Ago",
    "date": "10 APR 2022 10:23 AM",
    "distributor":"Bhai Bhai Enterprize",
    "balance": "570,168,000",
    "user": "Mmh Shohagh",
    "designation": "Accountant, Babylon Group",
    "collectionamount": "570,168,000",
    "collectionType": "Online",
    "status": "Settled",
    "invoiceNo": "Invoice No. 12345678"
  },
]
export function CreditDebitNoteAllList({setSingleAll, singleAll}) {
  const intl = useIntl();
  let history = useHistory();

  const openViewPage = (data) => {
    history.push('/salescollection/payment-adjustment/credit-debit-note/view', { state: data });
  }
  const openCreditPage = (data) => {
    history.push('/salescollection/payment-adjustment/credit-debit-note/credit-add-page', { state: data });
  }
  const openDebitPage = (data) => {
    history.push('/salescollection/payment-adjustment/credit-debit-note/debit-add-page', { state: data });
  }

  const singleWiseSelectHandler = (data, isSelect) => {
    if (isSelect == true) {
      let temp = [...singleAll]
      temp.push(data)
      setSingleAll(temp)
    } else {
      if (singleAll.length >= 0) {
        let temp = [...singleAll]
        const index = temp.findIndex(obj => obj.id == data.id);
        temp.splice(index, 1);
        setSingleAll(temp)
      }
    }

  }
  const allSelectHandler = (allData, isSelect) => {
    if (isSelect == true) {
      setSingleAll(allData)
    }
    else {
      if (allData.length >= 0) {
        for (let i = 0; i < allData.length; i++) {
          const index = singleAll.findIndex(obj => obj.id == allData[i].id);
          singleAll.splice(index, 1);
          setSingleAll(singleAll)
        }
      }
    }

  }
  const columns = [
    {
      dataField: "code",
      text:"DISTRIBUTOR",
      formatter: (cellContent, row) => (
        <>
           <div className="d-flex" style={{ marginBottom: "-10px" }}>
            <div>
              <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/Lays.svg")} width="50px" height="50px" />
            </div>
            <div className="ml-3">
              <span>
                <span style={{ fontWeight: "500" }}><strong>{row.distributor}</strong></span>
                <p className="dark-gray-color"> 
                  <span className="text-muted">
                  <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/CreditLimit.svg")} width="15px" height="15px" />&nbsp;
                    Credit Limit</span>&nbsp;{row.collectionamount}
                </p>
              </span>
            </div>
          </div>
        </>
      )
    },
    {
      dataField: "sales_officer",
      text: "BALANCE",
      formatter: (cellContent, row) => (
        <>
          <strong>{row.balance}</strong><br />
        </>
      )
    },
    {
      dataField: "action",
      text: intl.formatMessage({id: "MENU.ACTIONS"}),
      formatter: ActionsColumnFormatter,
      formatExtraData: {
        openViewPage: openViewPage,
        openCreditPage:openCreditPage,
        openDebitPage:openDebitPage
      },
      classes: "text-center",
      headerClasses: "text-center",
      style: {
        minWidth: "100px",
      },
    },
  ];

  return (
    <>
      <BootstrapTable
        wrapperClasses="table-responsive"
        classes="table table-head-custom table-vertical-center overflow-hidden"
        bootstrap4
        bordered={false}
        keyField="id"
        data={data}
        columns={columns}
        selectRow={
          {
            mode: 'checkbox',
            onSelect: (row, isSelect, rowIndex, e) => {
              singleWiseSelectHandler(row, isSelect);
            },
            onSelectAll: (isSelect, rows, e) => {
              allSelectHandler(rows, isSelect);

            }

          }
        }

        pagination={paginationFactory({ sizePerPage: 10, showTotal: true })}
      />
    </>
  );
}
