import React , {useState, useEffect} from "react";
import axios from "axios";
import Popper from '@material-ui/core/Popper';
import { useIntl } from "react-intl";
import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../_metronic/_helpers";
import { SalesBookingList } from "./sales-table/SalesBookingList";
import {
    Card,
    CardBody,
} from "../../../../_metronic/_partials/controls";
import { useHistory } from 'react-router-dom';
import {showError} from '../../../pages/Alert';
import LocationTreeView from '../../SalesCollection/CommonComponents/LocationTreeView';
import { BreadCrum } from "./common/BreadCrum";

export function SalesBooking(props) {
    let history = useHistory();
    let [singleAll, setSingleAll] = React.useState([]);
    const [anchorEl, setAnchorEl] = React.useState(null);
    const [searchParams, setSearchParams] = useState({ companyId: 2, userLoginId: 4, locationId: '', accountingYearId: 1, semesterId: '' });
    const [sessionData, setSessionData] = useState({userLoginId: 4, companyId: 2, accountingYearId: 1});
    const [locationId, setLocationId] = useState();
    const [bookingList, setBookingList] = useState([]);
    const [bookingSummary, setBookingSummary] = useState([]);
    const [locationTree, setLocationTree] = useState([]);
    const [selectedLocation, setSelectedLocation] = useState({});

    const intl = useIntl();

    useEffect(() => {
        getLocationTreeList();
        getBookingList(searchParams);
    }, []);

    const handleClick = (event) => {
        setAnchorEl(anchorEl ? null : event.currentTarget);
    };
    const open = Boolean(anchorEl);
    const id = open ? 'simple-popper' : undefined;
    const handleSalesDataChange = () => {
        history.push("/salescollection/sales/slaes-data")
    }
    const handleBookingChange = () => {
        history.push("/salescollection/sales/slaes-booking")
    }
    const handleOrderChange = () => {
        history.push("/salescollection/sales/slaes-order")
    }
    const handleExport = () => {
        const exportData = [...singleAll]
        console.log(exportData)
    }


    const getBookingList = (obj) => { 
        const URL = `${process.env.REACT_APP_API_URL}/api/sales-booking/filtered-list`;
        axios.post(URL, JSON.stringify(obj), { headers: { "Content-Type": "application/json" } }).then(response => {
            let bookingList = response.data.data.salesBookingList;
            let bookingSummary = response.data.data.bookingSummary;
            setBookingList(bookingList);
            setBookingSummary(bookingSummary);
        });
    }

    const getLocationTreeList = () => {
        const URL = `${process.env.REACT_APP_API_URL}/api/location-tree/locationTree/${sessionData.userLoginId}/${sessionData.companyId}`;
        axios.get(URL).then(response => {
            const locationTree = response.data.data;
            setLocationTree(locationTree);
        }).catch(err => {
            showError("Cannot get Location Tree data.");
        });
    }

    const selectLocationTreeNode = (node) => {
        let searchObj={...searchParams,locationId:node.id.toString()};
        setSearchParams(searchObj);
        setSelectedLocation(node);
        getBookingList(searchObj);
    }

    return (
        <>
            <div>
                {/* BREAD CRUM ROW */}
                <BreadCrum />
                {/* TODAY SALE ROW */}
                <div className="mt-3">
                    <Card>
                        <CardBody style={{ marginBottom: "-36px" }}>
                            <div>
                                <p className="create-field-title">Today’s Sale</p>
                            </div>
                            <div className="mt-5 ml-2 row">
                            <span className="sales-chip mt-5 mr-5"><i className="bi bi-arrow-up-short text-primary"></i>&nbsp;৳428,030&nbsp;<span className='text-muted'>Sale</span></span>
                            <span className="sales-chip mt-5 mr-5"><i className="bi bi-arrow-down-short text-danger"></i>&nbsp;৳428,030&nbsp;<span className='text-muted'>Booking</span></span>
                            <span className="sales-chip mt-5 mr-5"><i className="bi bi-arrow-up-short text-primary"></i>&nbsp;৳428,030&nbsp;<span className='text-muted'>Sales Order</span></span>
                            <span className="sales-chip mt-5 mr-5"><i className="bi bi-arrow-up-short text-primary"></i>&nbsp;QTY. 428&nbsp;<span className='text-muted'>Sales Return</span></span>
                            </div>
                            <div className="mt-5">
                                <ul className="nav nav-pills mb-3" id="pills-tab" role="tablist">
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link" id="pills-overview-tab" data-toggle="pill" href="#pills-overview" role="tab" aria-controls="pills-overview" aria-selected="true">Overview</a>
                                    </li>
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link" id="pills-sales-data-tab" data-toggle="pill" href="#pills-sales-data" role="tab" aria-controls="pills-sales-data" aria-selected="false" onClick={handleSalesDataChange}>Sales Data</a>
                                    </li>
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link active" id="pills-sales-booking-tab" data-toggle="pill" href="#pills-sales-booking" role="tab" aria-controls="pills-sales-booking" aria-selected="false" onClick={handleBookingChange}>Sales Booking</a>
                                    </li>
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link" id="pills-sales-order-tab" data-toggle="pill" href="#pills-sales-order" role="tab" aria-controls="pills-sales-order" aria-selected="false" onClick={handleOrderChange}>Sales Order</a>
                                    </li>
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link" id="pills-sales-return-tab" data-toggle="pill" href="#pills-sales-return" role="tab" aria-controls="pills-sales-return" aria-selected="false">Sales Return</a>
                                    </li>
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link" id="pills-trade-price-tab" data-toggle="pill" href="#pills-trade-price" role="tab" aria-controls="pills-trade-price" aria-selected="false">Trade Price</a>
                                    </li>
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link" id="pills-trade-discount-tab" data-toggle="pill" href="#pills-trade-discount" role="tab" aria-controls="pills-trade-discount" aria-selected="false">Trade Discount</a>
                                    </li>
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link" id="pills-sales-budget-tab" data-toggle="pill" href="#pills-sales-budget" role="tab" aria-controls="pills-sales-budget" aria-selected="false">Sales Budget</a>
                                    </li>
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link" id="pills-report-tab" data-toggle="pill" href="#pills-report" role="tab" aria-controls="pills-report" aria-selected="false">Report</a>
                                    </li>
                                </ul>
                            </div>
                        </CardBody>
                    </Card>
                </div>
            </div>
            <div>
                <Card>
                    <CardBody>
                        <div>
                            <div className='row'>
                                {/* LEFT SIDE TREE ROW */}
                                <div className='col-xl-3' style={{ borderRight: "1px solid #F2F2F2" }}>
                                    <div style={{ borderBottom: "1px solid #F2F2F2" }}>
                                        <label>
                                            <img src={toAbsoluteUrl("/images/loc3.png")}
                                                style={{ width: "20px", height: "20px", textAlign: "center" }}
                                                alt='Company Picture' />
                                            <strong style={{ marginLeft: "10px", color: "#828282" }}>Location (All)</strong>
                                        </label>
                                    </div>
                                    {/* TREE */}
                                    <LocationTreeView tree={locationTree}
                                                      selectLocationTreeNode={selectLocationTreeNode}/>
                                </div>
                                {/* RIGHT SIDE LIST ROW */}
                                <div className='col-xl-9'>
                                    {/* SEARCHING AND FILTERING ROW */}
                                    <div className="row">
                                        <div className="col-xl-3">
                                            <div style={{ position: "absolute", padding: "7px", marginTop: "3px" }}>
                                                <img src={toAbsoluteUrl("/images/search.png")} width="20px" height="20px" />
                                            </div>
                                            <form className="form form-label-right">
                                                <input type="text" className="form-control" name="searchText"
                                                    placeholder="Search Here"
                                                    style={{ paddingLeft: "28px" }}
                                                />
                                            </form>
                                        </div>
                                        <div className="col-xl-9 d-flex justify-content-end">
                                            <div>
                                                <button className='btn' aria-describedby={id} onClick={handleClick} style={{ color: "#828282" }}>
                                                    Fiscal Year 2022-23&nbsp;
                                                    <i className="bi bi-chevron-down"></i>
                                                </button>
                                                <Popper id={id} open={open} anchorEl={anchorEl}>
                                                    <div className='dropdown-content'>
                                                        <div className="row">
                                                            <div className="column">
                                                                <h6>Category 1</h6>
                                                                <a href="#">Link 1</a>
                                                                <a href="#">Link 2</a>
                                                                <a href="#">Link 3</a>
                                                            </div>
                                                            <div className="column">
                                                                <h6>Category 2</h6>
                                                                <a href="#">Link 1</a>
                                                                <a href="#">Link 2</a>
                                                                <a href="#">Link 3</a>
                                                            </div>
                                                            <div className="column">
                                                                <h6>Category 3</h6>
                                                                <a href="#">Link 1</a>
                                                                <a href="#">Link 2</a>
                                                                <a href="#">Link 3</a>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </Popper>
                                            </div>
                                            <div>
                                                <button className="btn filter-btn">
                                                    <i className="bi bi-funnel"></i>&nbsp;Filter
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                    {/* ALL SUMMARY ROW */}
                                    <div className='row ml-2'>
                                        <div className='col-xl-3 sales-data-chip' style={{ borderRadius: "5px 0px 0px 5px" }}>
                                            <div className="d-flex">
                                                <div className="dark-gray-color">
                                                    <i className="bi bi-geo-alt"></i>
                                                </div>
                                                <div className="ml-2">
                                                    <span>
                                                        <span className="dark-gray-color"
                                                            style={{ fontWeight: "500" }}>{intl.formatMessage({id: "LOCATION.AREA"})}</span>
                                                        <p><strong>{selectedLocation.locationName?selectedLocation.locationName:'All'}</strong></p>
                                                    </span>
                                                </div>
                                            </div>
                                        </div>
                                        <div className='col-xl-3 sales-data-chip'>
                                            <div className="d-flex">
                                                <div className="dark-gray-color">
                                                    <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/box.svg")} width="15px" height="15px" />
                                                </div>
                                                <div className="ml-2">
                                                    <span>
                                                        <span className="dark-gray-color"
                                                            style={{ fontWeight: "500" }}>{intl.formatMessage({id: "SALES_BOOKING.TOTAL_BOOKING"})}</span>
                                                        <p><strong>{bookingSummary.total_booking_quantity}</strong></p>
                                                    </span>
                                                </div>
                                            </div>
                                        </div>
                                        <div className='col-xl-3 sales-data-chip' style={{ borderRadius: "0px 5px 5px 0px" }}>
                                            <div className="d-flex">
                                                <div className="dark-gray-color">
                                                    <img src={toAbsoluteUrl("/images/LineChart.png")} width="24px" height="24px" />
                                                </div>
                                                <div className="ml-2">
                                                    <span>
                                                        <span className="dark-gray-color"
                                                            style={{ fontWeight: "500" }}>{intl.formatMessage({id: "SALES_BOOKING.BOOKING_AMOUNT"})}</span>
                                                        <p><strong>৳{bookingSummary.total_booking_amount}</strong></p>
                                                    </span>
                                                </div>
                                            </div>
                                        </div>
                                        <div className='col-xl-3 sales-data-chip' style={{ background: "#F9F9F9" }}>
                                            <button className="btn float-right export-btn" onClick={handleExport}>
                                                <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/up-arrow.svg")} width="15px" height="15px" />
                                            </button>
                                        </div>
                                    </div>
                                    {/* TABLE ROW */}
                                    <div className='mt-5'>
                                        <SalesBookingList setSingleAll = {setSingleAll} singleAll={singleAll} bookingList = {bookingList}/>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </CardBody>
                </Card>
            </div>
        </>
    );
}
