import React, {useEffect, useState} from 'react';
import {toAbsoluteUrl} from "../../../../../_metronic/_helpers";
import {Card, CardBody} from "../../../../../_metronic/_partials/controls";
import {showError} from '../../../../pages/Alert';
import axios from "axios";
import {useIntl} from "react-intl";
import {shallowEqual, useSelector} from "react-redux";
import Button from '@material-ui/core/Button';
import SalesOfficeList from "../../../Common/SalesOfficeList"
import DistributorList from "../../../Common/DistributorList"
import CommonReportType from "../../../Common/CommonReportType"
import CommonReportFormat from "../../../Common/CommonReportFormat"
import MisReportBreadCrum from '../MisReportBreadCrum';
import CommonDateComponent from '../../../Common/CommonDateComponent';
import ReportLocationTreeView from '../../../Common/ReportLocationTreeView';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import IOSSwitch from '../../../../pages/IOSSwitch';

export default function SalesReturnReport() {
    const fields = {
        locationId: "",
        salesOfficerId: "",
        distributorId: "",
        startDate: "",
        endDate: "",
        report: "",
        reportType: ""
    };
    const intl = useIntl();
    const [inputs, setInputs] = useState(fields);
    const selectedCompany = useSelector((state) => state.auth.company, shallowEqual);
    const userLoginId = useSelector((state) => state.auth.user.userId, shallowEqual);
    const [locationTree, setLocationTree] = useState([]);
    const [locationIds, setLocationIds] = useState([]);
    const [nodes, setNodes] = useState([]);

    const [categoryLevel, setCategoryLevel] = useState("");

    const [salesOfficerList, setSalesOfficerList] = useState([]);
    const [distributorList, setDistributorList] = useState([]);

    const [checked, setChecked] = useState(false);
    const [test, setTest] = useState([]);

    useEffect(() => {
        document.getElementById('reportShowIframe').style.display = "none";
    }, [userLoginId, selectedCompany]);

    useEffect(() => {
        getLocationTreeList(selectedCompany)
    }, [selectedCompany]);

    useEffect(() => {
        nodes.map(node=>getLocationIds(node))         
    }, [nodes]);

    // useEffect(() => {
    //     getLocationIdData()       
    // }, []);

    // const getLocationIdData = () =>{
    //     console.log(locationIds)
    // }

    const getLocationIds = (node) => {
        let temp = [...locationIds]
        let index = temp.findIndex(id => id === node.id)
        if (index === -1) {
            locationIds.push(node.id);
        }
        node.children.map(nodeChild => {
            getLocationIds(nodeChild)
        })
    }

    // const getNodesIds = (node) => {
    //     let temp = [...nodes]
    //     let index = temp.findIndex(id => id === node.id)
    //     if (index === -1) {
    //         nodes.push(node.id);
    //     }
    //     node.children.map(nodeChild => {
    //         getNodesIds(nodeChild)
    //     })
    // }

    const handleActive = () => {
        let checked = document.getElementById('nationalId').checked;
        setChecked(checked)
        if(checked){
            nodes.length = 0;
            locationIds.length = 0;
            const getElements = document.getElementsByClassName('tree-nav__item_demo tree-nav__item-title');
            for (var i = 0; i < getElements.length; i++) {
                getElements[i].classList.remove('report-location-tree-selected');
            }
        }
    }
    const getLocationTreeList = (companyId) => {
        const URL = `${process.env.REACT_APP_API_URL}/api/location-tree/report-location-tree-info/${companyId}`;
        axios.get(URL).then(response => {
            const locationTree = response.data.data.locationAsTree;
            setLocationTree(locationTree);
        }).catch(err => {
            showError("Cannot get Location Tree data.");
        });
    }
    const selectLocationTreeNode = (node) => {
        locationIds.length = 0;
        let temp = [...nodes]
        let index = temp.findIndex(data=> data.treeLevel==node.treeLevel)
        if(index > -1){
            temp.splice(index, 1);
            setNodes(temp)
        }else{
            temp.push(node)
            setNodes(temp)
        }
        // nodes.length = 0;
        // let temp = [...locationIds]
        // let index = temp.findIndex(data=> data ==node.id)
        // if(index > -1){
        //     temp.push(getNodesIds(node));
        // }else{
        //     temp.push(getLocationIds(node));
        // }
        // console.log("locationIds ==============",locationIds)
        // console.log("nodes ids ==============",nodes)
        // console.log("main ids ======", locationIds.filter(n => !nodes.includes(n)))
        if (categoryLevel != "") {
            setCategoryLevel(node.treeLevel)
            if (categoryLevel.split('-').length == node.treeLevel.split('-').length) {
                let id = "report-location-tree-view-id-" + node.id;
                const getId = document.getElementById(id);
                if (getId.className == "d-flex justify-content-between tree-nav__item_demo tree-nav__item-title report-location-tree-selected") {
                    getId.classList.remove('report-location-tree-selected');
                } else {
                    getId.classList.add('report-location-tree-selected');
                    setChecked(false)
                }
            } else {
                let id = "report-location-tree-view-id-" + node.id;
                const getId = document.getElementById(id);
                const getElements = document.getElementsByClassName('tree-nav__item_demo tree-nav__item-title');
                for (var i = 0; i < getElements.length; i++) {
                    getElements[i].classList.remove('report-location-tree-selected');
                }
                if (getId) {
                    getId.classList.add('report-location-tree-selected');
                    setChecked(false)
                }
            }
            //getLocationIds(node);

        } else {
            //getLocationIds(node);
            setCategoryLevel(node.treeLevel)
            let id = "report-location-tree-view-id-" + node.id;
            const getId = document.getElementById(id);
            if (getId.className == "d-flex justify-content-between tree-nav__item_demo tree-nav__item-title report-location-tree-selected") {
                getId.classList.remove('report-location-tree-selected');
            } else {
                getId.classList.add('report-location-tree-selected');
                setChecked(false)
            }
        }
    }

    const validate = () => {
        if (locationIds.length === 0) {
            showError('Location is required');
            return false;
        } else if (!inputs.salesOfficerId) {
            showError('Sales Officer is required');
            return false;
        } else if (!inputs.distributorId) {
            showError('Distributor Name is required.');
            return false;
        } else if (!inputs.startDate) {
            showError("Start date  is required");
            return false;
        } else if (!inputs.endDate) {
            showError("End date is required");
            return false;
        } else if (inputs.endDate < inputs.startDate) {
            showError("End Date should be greater than Start Date");
            return false;
        } else if (!inputs.reportFormate) {
            showError('Report Format is required');
            return false;
        } else if (!inputs.reportType) {
            showError('Report Type is required.');
            return false;
        }
        return true;
    }
    const previewValidate = () => {
        if (locationIds.length === 0) {
            showError('Location is required');
            return false;
        } else if (!inputs.salesOfficerId) {
            showError('Sales Officer is required');
            return false;
        } else if (!inputs.distributorId) {
            showError('Distributor Name is required.');
            return false;
        } else if (!inputs.startDate) {
            showError("Start date  is required");
            return false;
        } else if (!inputs.endDate) {
            showError("End date is required");
            return false;
        } else if (inputs.endDate < inputs.startDate) {
            showError("End Date should be greater than Start Date");
            return false;
        } else if (!inputs.reportType) {
            showError('Report Type is required.');
            return false;
        }
        return true;
    }

    const preview = () => {
        if (!previewValidate()) {
            return false;
        }
        let queryParams = '?locationId=' + inputs.locationId;
        queryParams += '&reportFormate=' + "PDF";
        queryParams += '&companyId=' + selectedCompany;
        queryParams += '&locationIds=' + locationIds;
        queryParams += '&salesOfficerId=' + inputs.salesOfficerId;
        queryParams += '&distributorId=' + inputs.distributorId;
        queryParams += '&startDate=' + inputs.startDate;
        queryParams += '&endDate=' + inputs.endDate;
        queryParams += '&reportType=' + inputs.reportType;
        const data = `${process.env.REACT_APP_API_URL}/api/report/sales-return` + queryParams;
        axios.get(data, { responseType: 'blob' }).then(response => {
            const file = new Blob([response.data], { type: "application/pdf" });
            const fileURL = URL.createObjectURL(file);
            const iframe = document.querySelector("iframe");
            if (iframe?.src) iframe.src = fileURL;
            document.getElementById('reportShowIframe').style.display = "inline-block";
        }).catch(err => {
            showError();
        });
    }
    const download = () => {
        console.log("locationIds====",locationIds)
        if (!validate()) {
            return false;
        }
        let queryParams = '?locationId=' + inputs.locationId;
        queryParams += '&reportFormate=' + inputs.reportFormate;
        queryParams += '&companyId=' + selectedCompany;
        queryParams += '&locationIds=' + locationIds;
        queryParams += '&salesOfficerId=' + inputs.salesOfficerId;
        queryParams += '&distributorId=' + inputs.distributorId;
        queryParams += '&startDate=' + inputs.startDate;
        queryParams += '&endDate=' + inputs.endDate;
        queryParams += '&reportType=' + inputs.reportType;
        const data = `${process.env.REACT_APP_API_URL}/api/report/sales-return` + queryParams;
        axios.get(data, { responseType: 'blob' }).then(response => {
            if (inputs.reportFormate === "PDF") {
                const url = window.URL.createObjectURL(new Blob([response.data]));
                const link = document.createElement('a');
                link.href = url;
                link.setAttribute('download', "SalesReturn.pdf");
                document.body.appendChild(link);
                link.click();
                document.body.removeChild(link);
            } else {
                const url = window.URL.createObjectURL(new Blob([response.data]));
                const link = document.createElement('a');
                link.href = url;
                link.setAttribute('download', "SalesReturn.xlsx");
                document.body.appendChild(link);
                link.click();
                document.body.removeChild(link);
            }
        }).catch(err => {
            showError();
        });
    }

    return (
        <>
            <div>
                <MisReportBreadCrum menuTitle="Sales Return Report" />
            </div>
            <div>
                <Card>
                    <CardBody>
                        <div className='row'>
                            {/* LEFT SIDE TREE ROW */}
                            <div className='col-xl-4' style={{ borderRight: "1px solid #F2F2F2" }}>
                                <div style={{ borderBottom: "1px solid #F2F2F2" }}>
                                    <label>
                                        <img src={toAbsoluteUrl("/images/loc3.png")}
                                            style={{ width: "20px", height: "20px", textAlign: "center" }}
                                            alt='Company Picture' />
                                        <strong style={{
                                            marginLeft: "10px",
                                            color: "#828282"
                                        }}>{intl.formatMessage({ id: "COMMON.LOCATION_ALL" })}</strong>
                                    </label>
                                </div>
                                <div>
                                <FormControlLabel
                                    control={<IOSSwitch
                                        id="nationalId"
                                        checked={checked}
                                        onClick={handleActive}
                                    />}
                                    label="National"
                                />
                            </div>
                                {/* TREE */}
                                <ReportLocationTreeView tree={locationTree} selectLocationTreeNode={selectLocationTreeNode} />
                            </div>


                            {/* RIGHT SIDE LIST ROW */}
                            <div className='col-xl-8'>

                                <SalesOfficeList
                                companyIdPass = {selectedCompany}
                                locationsIdsPass = {locationIds}
                                setSalesOfficerListPass = {setSalesOfficerList}
                                salesOfficerListPass = {salesOfficerList}
                                />

                                <DistributorList 
                                companyIdPass = {selectedCompany}
                                setDistributorListPass = {setDistributorList}
                                distributorListPass = {distributorList}
                                />

                                <div className='mt-5'>
                                    <CommonDateComponent />
                                </div>

                                <div className='mt-5'>
                                    <CommonReportType />
                                </div>


                                <div className='mt-5'>
                                    <CommonReportFormat />
                                </div>
                                <Button className="float-right mt-5" id="gotItBtn" variant="contained"
                                    color="primary"
                                    onClick={download}
                                >
                                    Download
                                </Button>
                                <div className="float-right">
                                    <Button className="mt-5 mr-5" id="gotItBtn" variant="contained" color="primary" onClick={preview}>
                                        Preview
                                    </Button>
                                </div>
                            </div>
                        </div>
                        <div className='mt-5'>''
                            <iframe src="" className='w-100' id="reportShowIframe" height="500px" title="" />
                        </div>
                    </CardBody>
                </Card>
            </div>
        </>
    );
}
