import React, { useEffect, useState } from "react";
import { showError } from "../../pages/Alert";
import axios from "axios";
import Autocomplete from "@material-ui/lab/Autocomplete";
import { TextField } from "@material-ui/core";

export default function StockValuation(props) {
    const [distributors, setDistributors] = useState([]);
    const [distributorValue, setDistributorValue] = useState(null);

    useEffect(() => {
        document.getElementById('reportShowIframe').style.display = "none";
    }, [props.companyIdPass]);
    const getAllDistributors = (event) => {
        let queryString = "?";
        queryString += "companyId=" + props.companyIdPass;
        queryString += event.target.value ? "&searchString=" + event.target.value : "&searchString=" + ''
        const URL = `${process.env.REACT_APP_API_URL}/api/distributor/get-distributor-list-company` + queryString;
        axios.get(URL).then(response => {
            setDistributors(response.data.data);
        });
    }
    const deleteDistributor = (data) => {
        const temp = [...props.distributorListPass]
        const index = temp.findIndex(obj => obj.distributorId === data.distributorId);
        temp.splice(index, 1);
        props.setDistributorListPass(temp)
    }
    return (
        <>
            <div className='mt-5'>
                <label className='level-title'><span className="mr-1">Distributor</span></label>
                <Autocomplete
                    name="distributorId"
                    options={distributors}
                    onKeyDown={getAllDistributors}
                    getOptionLabel={(option) => option.distributorName}
                    value={distributorValue}
                    onChange={(event, newValue) => {
                        setDistributorValue(newValue)
                        if (newValue !== null) {
                            if (props.distributorListPass.length > 0) {
                                let temp = [...props.distributorListPass]
                                const index = temp.findIndex(data => data.distributorId === newValue.distributorId);
                                if (index > -1) {
                                    showError("This user already used");
                                    return;
                                } else {
                                    temp.push({
                                        "distributorId": newValue.distributorId,
                                        "distributorName": newValue.distributorName

                                    })
                                    props.setDistributorListPass(temp)
                                }
                            } else {
                                const temp = [...props.distributorListPass]
                                temp.push({
                                    "distributorId": newValue.distributorId,
                                    "distributorName": newValue.distributorName

                                })
                                props.setDistributorListPass(temp)
                            }
                        }
                    }}
                    renderInput={(params) => (
                        <TextField {...params} label="Select Distributor*" />
                    )}
                />
            </div>

            <div
                className="mt-3"
                style={{ border: "none", padding: "0 3px 3px 3px" }}>
                {props.distributorListPass.length > 0 ? props.distributorListPass.map(data => {
                    return (
                        <>
                            {
                                data.id !== "" ?
                                    <span className='mt-3 mr-5 areaLevel'
                                    >
                                        {data.distributorName}
                                        <span className='deleteIcon'>
                                            <a
                                                className='fa fa-times text-white delete-file'
                                                onClick={() => deleteDistributor(data)}
                                            ></a>
                                        </span>
                                    </span> : ""
                            }
                        </>
                    )
                }) : ""}
            </div>
        </>);
}
