import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import { Card, CardBody } from "../../../../../../../../../_metronic/_partials/controls";
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { NavLink } from 'react-router-dom';

import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../../../../../../_metronic/_helpers";
import PaymentAdjustmentBreadCrum from "../../../../../common/PaymentAdjustmentBreadCrum";

const useStyles = makeStyles(theme => ({
    root: {
        width: '90%',
    },
    button: {
        marginTop: theme.spacing(1),
        marginRight: theme.spacing(1),
    },
    actionsContainer: {
        marginBottom: theme.spacing(2),
    },
    resetContainer: {
        padding: theme.spacing(3),
    },
}));

const getSteps = () => {
    return ['Initialize', 'Reason', 'Completed'];
}

export default function DebitAdd(props) {
    const classes = useStyles();
    const [successImg, setSuccessImg] = useState(toAbsoluteUrl("/images/success2.png"));
    const [activeStep, setActiveStep] = useState(0);
    const steps = getSteps();
    const subsTitle = ["Debit Note Proposal Initialize", "Setup Your Account Settings", "Woah, we are here"];
    useEffect(() => {
        if (activeStep === 0) {
            document.getElementById('backBtn').style.visibility = 'hidden';
        }
    }, []);
    const handleNext = () => {
        if (activeStep === 1) {
            document.getElementById('nextBtn').style.visibility = 'hidden';
            document.getElementById('backBtn').style.display = 'none';
            document.getElementById('gotItBtn').style.visibility = 'visible';
        }

        if (activeStep >= 0) {
            document.getElementById('backBtn').style.visibility = 'visible';
        }
        setActiveStep(prevActiveStep => prevActiveStep + 1);
    }

    const handleBack = () => {
        if (activeStep === 1) {
            document.getElementById('backBtn').style.visibility = 'hidden';
        }
        setActiveStep(prevActiveStep => prevActiveStep - 1);
    }

    const getStepContent = (step) => {
        switch (step) {
            case 0:
                return <div className='steper-height ml-5'>
                    <div>
                        <span className="create-field-title">Initialize</span>
                        <p style={{ color: "#B6B6B6" }}>If you need more info, please check out <NavLink to="/salescollection/payment-adjustment/credit-debit-note/all-list" className="faq-title"><u>FAQ Page</u></NavLink></p>
                    </div>

                    {/* DEBIT AMOUNT ROW */}
                    <div className='mt-5'>
                        <label className='level-title'>Debit Amount <span className='text-danger'>*</span></label>
                        <input placeholder='55,606,521' min={0} id="debitAmount" type="number" className='form-control' name="debitAmount" />
                    </div>

                    <div className='row mt-5'>
                        {/* DATE ROW */}
                        <div className='col-6'>
                            <label className='level-title'>Date <span className='text-danger'>*</span></label>
                            <input placeholder='1/07/2022'  id="date" type="date" className='form-control' name="date" />
                        </div>

                        {/* PICK A INVOICE ROW */}
                        <div className='col-6'>
                            <label className='level-title'>Pick a Invoice</label>
                            <select id="pickInvoice" className='form-control' name="pickInvoice">
                                <option value="1">3ADSF3242343</option>
                                <option value="2">3ADSF3242343</option>
                                <option value="3">3ADSF3242343</option>
                            </select>
                        </div>
                    </div>
                </div>;
            case 1:
                return <div className='steper-height ml-5'>
                    <div>
                        <span className="create-field-title">Reason</span>
                        <p style={{ color: "#B6B6B6" }}>If you need more info, please check out <NavLink to="/salescollection/payment-adjustment/credit-debit-note/all-list" className="faq-title"><u>FAQ Page</u></NavLink></p>
                    </div>

                    {/* REASON ROW */}
                    <div className='mt-5'>
                        <label className='level-title'>Reason <span className='text-danger'>*</span></label>
                        <textarea placeholder='Write Here' id="reason" type="text" className='form-control' name="reason" row={5} />
                    </div>

                     {/* ATTACHMENT ROW */}
                     <div className='mt-5'>
                            <label className='level-title'>Attachment</label>
                            <div className='border-dash text-center'>
                            <input id="file" type="file" name="file" />
                            </div>
                    </div>

                    {/* REMARKs ROW */}
                    <div className='mt-5'>
                        <label className='level-title'>Remarks(If any)</label>
                        <textarea placeholder='Write Here' id="reason" type="text" className='form-control' name="reason" row={5} />
                    </div>
                </div>;
            default:
                return <div className='row steper-height'>
                    <div className='col-lg-12' style={{ textAlign: "center" }}>
                        <img
                            src={successImg}
                            style={{ width: "84px", height: "84px", textAlign: "center" }} alt='Company Picture' />
                        <div className='mt-5'>
                            <p style={{ marginBottom: "0px" }} className="create-field-title">Thank You</p>
                            <span className='text-muted'>Debit note proposal submitted successfully</span>
                        </div>
                    </div>
                    <div className='col-xl-12'>
                        <div className='thanks-sub-title'>
                            <strong style={{ fontSize: "17px" }}>New Debit note proposal Info</strong>
                        </div>
                    </div>
                    <div className='col-lg-12'>
                        <div className='row'>
                            <div className='offset-xl-4 col-xl-4 text-center fiscal-year-thanks-div'>
                                <div>
                                    <span className="fiscal-year-title">Note No. 123578956785</span>
                                </div>
                                <div className="mt-1">
                                    <span className="daysCount mr-3">Amount 55,606,521 </span>
                                </div>
                                <div className='mt-2'>
                                    <div>
                                        <span className="text-muted">Date 20 APR 2022 10:23 AM</span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>;
        }
    }
    const backToListPage = () => {
        props.history.push("/salescollection/payment-adjustment/credit-debit-note/all-list");
    }
    const handleBackToAllListPage = () => {
        props.history.push('/salescollection/payment-adjustment/credit-debit-note/all-list');
    }
    return (
        <>
            {/* BREAD CRUM ROW */}
            <div>
                <PaymentAdjustmentBreadCrum />
            </div>
            <div>
                <Card>
                    <CardBody>
                        {/* BACK AND TITLE ROW */}
                        <div className="row">
                            <div className="col-4">
                                <span>
                                    <button className='btn' onClick={handleBackToAllListPage}>
                                        <strong>
                                            <i className="bi bi-arrow-left-short sales-booking-view-icon"></i>
                                        </strong>
                                    </button>
                                </span>
                            </div>
                            <div className="col-4 text-center mt-3">
                                <strong>Debit Note Proposal</strong>
                            </div>
                            <div className="col-4 text-xl-right text-muted mt-3">
                                <strong>20 APR 2022 10:23 AM</strong>
                            </div>
                        </div>

                        {/* HEADER ROW */}
                        <div className='row mt-5'>
                            <div className='col-xl-8'>
                                <div className="row">
                                    <div className="col-3">
                                        <span className="dark-gray-color"
                                            style={{ fontWeight: "500" }}>Region</span><br />
                                        <span><strong>East</strong></span>
                                    </div>
                                    <div className="col-3">
                                        <span className="dark-gray-color"
                                            style={{ fontWeight: "500" }}>Area</span><br />
                                        <span><strong>Chittagong</strong></span>
                                    </div>
                                    <div className="col-3">
                                        <span className="dark-gray-color"
                                            style={{ fontWeight: "500" }}>Territory</span><br />
                                        <span><strong>Cox’s Bazar</strong></span>
                                    </div>
                                    <div className="col-3">
                                        <span className="dark-gray-color"
                                            style={{ fontWeight: "500" }}>Timeline</span>
                                        <span>
                                            <select className="form-control border-0 ml-n5 payment-collection-invoices-select">
                                                <option value="1" selected>2022-23</option>
                                                <option value="2">2022-23</option>
                                                <option value="3">2022-23</option>
                                            </select>
                                        </span>
                                    </div>
                                </div>
                            </div>
                            <div className='col-xl-4 d-flex justify-content-end'>
                                <div className="d-flex  mr-5">
                                    <div>
                                        <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/Lays.svg")} width="35px" height="35px" />
                                    </div>
                                    <div className="ml-3">
                                        <span>
                                            <span style={{ fontWeight: "500" }}><strong>Lay’s Seed Enterprize</strong></span>
                                            <p><SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/phone.svg")} width="14px" height="14px" />&nbsp;+880 1779 911 488</p>
                                        </span>
                                    </div>
                                </div>
                                <div>
                                    <button className='btn sales-credit-btn' style={{ padding: "0px 15px", borderRadius: "13px" }}>
                                        <span className='text-white' style={{ fontSize: "0.83rem" }}>
                                            Balance<br />
                                            55,606,521
                                        </span>
                                    </button>
                                </div>
                            </div>
                        </div>

                    </CardBody>
                </Card>
            </div>
            <Card>
                <CardBody>
                    <div className="row">
                        <div className="col-sm-12 col-xl-3  col-lg-3 col-md-3border border-left-0 border border-light border-primary border-bottom-0 border-top-0">
                            <div className={classes.root}>
                                <Stepper activeStep={activeStep} orientation="vertical">
                                    {steps.map((label, index) => (
                                        <Step key={label}>
                                            <StepLabel optional={<Typography variant="caption">{subsTitle[index]}</Typography>}>{label}</StepLabel>
                                        </Step>
                                    ))
                                    }
                                </Stepper>
                            </div>
                        </div>
                        <div className="col-sm-12 col-xl-9 col-lg-9 col-md-9">
                            <Typography>{getStepContent(activeStep)}</Typography>
                            <div className={classes.actionsContainer}>
                                <div className='mt-5'>
                                    <Button
                                        id="backBtn"
                                        onClick={handleBack}
                                        className={classes.button}
                                    >
                                        Back
                                    </Button>
                                    <Button
                                        id="nextBtn"
                                        disabled={activeStep === steps.length}
                                        variant="contained"
                                        color="primary"
                                        onClick={handleNext}
                                        className="float-right btn-gradient-blue"
                                    >
                                        {activeStep === steps.length - 2 ? 'Complete' : 'Next'}
                                    </Button>
                                    <div style={{ textAlign: 'center', marginTop: '119px', marginLeft: "50px" }}>
                                        <Button
                                            style={{ visibility: "hidden" }}
                                            className='gotIt-btn'
                                            id="gotItBtn"
                                            variant="contained"
                                            color="primary"
                                            onClick={backToListPage}
                                        >
                                            Got it
                                        </Button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </CardBody>
            </Card>
        </>
    );
}
