import React, { useEffect, useState } from "react";
import ReportTabsHeader from "../../tabs/ReportsTabsHeader";
import { Card } from "react-bootstrap";
import Button from '@material-ui/core/Button';
import { CardBody } from "../../../../../_metronic/_partials/controls";
import Autocomplete from "@material-ui/lab/Autocomplete";
import { TextField } from "@material-ui/core";
import { NavLink } from "react-router-dom";
import { shallowEqual, useSelector } from 'react-redux';
import { showError, showSuccess } from "../../../../pages/Alert";
import axios from "axios";
import MisReportBreadCrum from "../MisReportBreadCrum";

const fields = {
    distributorId: "",
    storeId: "",
    depotId:"",
    companyId:""
}
export default function StockValuation() {
    const depotId = useSelector((state) => state.auth.company, shallowEqual);
    const userId = useSelector((state) => state.auth.user.userId, shallowEqual);
    const [distributors, setDistributors] = useState([]);
    const companyId = useSelector((state) => state.auth.company, shallowEqual);
    const [inputs, setInputs] = useState(fields);
    const [allDepot, setDepot] = useState([]);
    const [allStore, setStore] = useState([]);

    // useEffect(() => {
    //     document.getElementById('pills-receivable-invoice-statement-cash-tab').classList.add('active')
    // }, []);

    useEffect(() => {
        getAllDepot(companyId);
        getStore();
    }, [companyId]);

    const getStore = () => {
        const URL = `${process.env.REACT_APP_API_URL}/api/store`;
        axios.get(URL).then(response => {
            setStore(response.data.data);
        });
    }

    const getAllDepot = (companyId) => {
        const URL = `${process.env.REACT_APP_API_URL}/api/depot/all-of-a-company/${companyId}`;
        axios.get(URL).then(response => {
            console.log(response.data.data);
            setDepot(response.data.data);
        });
    }
    const handleDepotChange = (e) =>{
        if (e.target.value !== null || e.target.value !== "") {
            setInputs({ ...inputs, depotId: parseInt(e.target.value) });
        }
    } 
    const handleStoreIdChange = (e) => {
        if (e.target.value !== null || e.target.value !== "") {
            setInputs({ ...inputs, storeId: parseInt(e.target.value)});
        }
    }
    const validate = () => {
        if (!inputs.depotId) {
            showError('Depot is required.');
            return false;
        }

        if (!inputs.storeId) {
            showError('Store is required.');
            return false;
        }
        return true;
    }

    const previewStockValuation = () =>{
        if(!validate()){
            return false;
        }
        let queryString = '?';
        queryString += 'depotId=' + parseInt(inputs.depotId);
        queryString += '&companyId=' + companyId;
        queryString += '&storeId=' + parseInt(inputs.storeId);
        const URL = `${process.env.REACT_APP_API_URL}/api/reports/stock-valuation`+queryString;
        axios.get(URL, {responseType: 'blob'}).then(response => {
            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', "stockValuation.pdf");
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);

        }).catch(err => {
            showError();
        });

    }
    const handlePaste = (e) => {
        e.preventDefault()
        return false;
    }

    // const getCompanySelect = (row) => {
    //   getAllDepot(depotId);
    // }
    return (
        <>
        <div>
            <MisReportBreadCrum menuTitle="Stock Valuation"/>
        </div>
            <div>
            <Card>
                <CardBody>
                    <div className="row">
                        <div className="col-lg-12">
                            <span className="create-field-title">Stock Valuation Report</span>

                            <div className='mt-5 row'>
                                <div className='col-xl-6'>
                                    <div>
                                    <label className='level-title'><span className="mr-1">Depot</span><span className="text-danger ">*</span></label>
                                        <select className="form-control" id="depotId"  name="depotId" onChange={(e)=>handleDepotChange(e)}>
                                            <option value="">Select Depot</option>
                                            {
                                                allDepot.map((depot)=>(
                                                <option key={depot.id} value={depot.id}>{depot.depot_name}</option> 
                                                ))
                                            }   
                                        </select>
                                    </div>
                                </div>
                            </div>

                            <div className='mt-5 row'>
                                <div className='col-xl-6'>
                                    <div>
                                    <label className='level-title'><span className="mr-1">Store</span><span className="text-danger ">*</span></label>
                                        <select className="form-control" id="storeId"  name="storeId" 
                                            onChange={(e)=>handleStoreIdChange(e)}>
                                            <option value="">Select Store</option>
                                            {
                                                allStore.map((store)=>(
                                                <option key={store.id} value={store.id}>
                                                    {store.name}
                                                </option> 
                                                ))
                                            }   
                                        </select>
                                    </div>
                                </div>
                            </div>

                            {/* <div className='mt-5 row'>
                                <div className='col-xl-6'>
                                <label className='level-title'><span className="mr-1">Distributor</span></label>
                                <Autocomplete
                                    id="distributorId"
                                    name="distributorId"
                                    options={distributors}
                                    onKeyDown={getAllDistributorList}
                                    getOptionLabel={(option) => option.distributorName}
                                    onChange={(event, newValue) => {
                                        handleChangeDistributor(event, newValue)
                                    }}
                                    renderInput={(params) => (
                                        <TextField {...params} label="Select Distributor" />
                                    )}
                                />
                                </div>
                            </div> */}

                            <Button className="float-right mt-5" id="gotItBtn" variant="contained" 
                                color="primary"  onClick={previewStockValuation}>
                                Submit
                            </Button>

                        </div>
                    </div>
                </CardBody>
            </Card>
            </div>
        </>);
}
