import React, { useEffect, useState } from "react";
import { useHistory, useLocation } from "react-router-dom";
import { toAbsoluteUrl } from "../../../../../../../../../_metronic/_helpers";
import SVG from "react-inlinesvg";
import { Card } from "react-bootstrap";
import { CardBody } from "../../../../../../../../../_metronic/_partials/controls";
import CircularProgress from '@material-ui/core/CircularProgress';
import { allowOnlyNumeric } from '../../../../../../../Util';
import { showError } from "../../../../../../../../pages/Alert";
import axios from "axios";

export default function NewPickingList() {
    let history = useHistory();
    const location = useLocation();
    const [orderList, setOrderList] = useState([])
    const [productList, setProductList] = useState([])
    const [cartList, setCartList] = useState([]);
    let [count, setCount] = useState(0);
    const [depot, setdepot] = useState("");
    const [distributor, setDistributor] = useState("");
    const [stockQty, setStockQty] = useState(0);
    let [remainQty, setRemainQty] = useState(0);

    useEffect(() => {
        document.getElementById('full-screen-close-icon').style.display = "none";
        if (location.state != undefined) {
            console.log(location.state.state)
            setdepot(location.state.state.depot);
            setOrderList(location.state.state.orderList)
            setDistributor(location.state.state);
        }
    }, [])
    const handleBackToListPage = () => {
        history.push('/inventory/stock/sales-order/picking-list');
    }
    const openFullscreen = () => {
        const elem = document.getElementById("myFullScreen");
        elem.classList.add("scroll-product-search");
        elem.requestFullscreen();
        document.getElementById('full-screen-icon').style.display = "none"
        document.getElementById('full-screen-close-icon').style.display = "inline-block"
    }
    const closeFullscreen = () => {
        const elem = document.getElementById("myFullScreen");
        elem.classList.remove("scroll-product-search");
        document.exitFullscreen();
        document.getElementById('full-screen-icon').style.display = "inline-block"
        document.getElementById('full-screen-close-icon').style.display = "none"
    }

    const handleSelectOrderList = (data, number) => {
        setProductList([])
        setStockQty(0)
        setRemainQty(0)
        let id = "id-" + number;
        const getId = document.getElementById(id);
        const getElements = document.getElementsByClassName('order-list-div');
        for (var i = 0; i < getElements.length; i++) {
            getElements[i].classList.remove('select-order-list');
        }

        let radioId = "radio-id-" + number;
        const getRadioId = document.getElementById(radioId);
        var radioElements = document.getElementsByClassName("all-radio");
        for (var i = 0; i < radioElements.length; i++) {
            radioElements[i].checked = false;
        }
        if (getId) {
            getId.classList.add('select-order-list');
            getRadioId.checked = true;
            getProductList(data.id);
        }
    }

    const selectBatch = (e, id) => {
        const getPickingQty = document.getElementById("pickingQty-id-" + id)
        getPickingQty.value = ""
        setStockQty(0)
        setRemainQty(0)
        const temp = [...productList]
        const index = temp.findIndex(obj => obj.id === id);
        const batchListTemp = temp[index].batchList;
        const batchIndex = batchListTemp.findIndex(obj => obj.id === parseInt(e.target.value))
        setRemainQty(temp[index].order_quantity)
        setStockQty(batchListTemp[batchIndex].stock_quantity)
    }

    const handleAddToCart = (pData) => {
        // let cartId = count + 1;
        // setCount(cartId)
        const getBatch = document.getElementById("batch-id-" + pData.id);
        const getBatchId = getBatch.options[getBatch.selectedIndex].value;
        const getBatchName = getBatch.options[getBatch.selectedIndex].text;

        if (!getBatchId) {
            showError('Batch is required.');
            return false;
        }
        const getRemainQty = document.getElementById("remainingQty-id-" + pData.id)
        const getPickingQty = document.getElementById("pickingQty-id-" + pData.id)

        const temp = [...productList]
        const index = temp.findIndex(obj => obj.id === pData.id);
        const batchListTemp = temp[index].batchList;
        const batchIndex = batchListTemp.findIndex(obj => obj.id === parseInt(getBatchId));
        batchListTemp.splice(batchIndex, 1)

        const tempCart = [...cartList]
        tempCart.push({
            "id": pData.sales_order_details_id,
            "pId": pData.id,
            "orderId": pData.orderId,
            "orderQty":pData.order_quantity,
            "pSku": pData.pSku,
            "pName": pData.pName,
            "pCategory": pData.pCategory,
            "batchId": getBatchId,
            "batchName": getBatchName,
            "pickingQty": getPickingQty.value
        })

        setCartList(tempCart)
        getRemainQty.value = ""

    }
    const handleRemoveToCart = (pData) => {
        const cartTemp = [...cartList]
        const index = cartTemp.findIndex((cart) => cart.id === pData.sales_order_details_id)
        cartTemp.splice(index, 1)
        setCartList(cartTemp)

        const orderListTemp = [...orderList]
        const orderIndex = orderListTemp.findIndex((order) => order.id === parseInt(pData.orderId))
        const productList = orderList[orderIndex].productList;
        const productIndex = productList.findIndex((product) => product.id === parseInt(pData.pId))
        productList[productIndex].batchList.push({
            "id": parseInt(pData.batchId),
            "pId": pData.pId,
            "batchNo": pData.batchName,
            "batchQty": pData.remainingQty
        })
    }

    const handleCancelPicking = () => {
        const temp = [...cartList]
        const cartListMap = temp.map((cart) => {
            const orderListTemp = [...orderList]
            const orderIndex = orderListTemp.findIndex((order) => order.id === parseInt(cart.orderId))
            const productList = orderList[orderIndex].productList;
            const productIndex = productList.findIndex((product) => product.id === parseInt(cart.pId))
            productList[productIndex].batchList.push({
                "id": parseInt(cart.batchId),
                "pId": cart.pId,
                "batchNo": cart.batchName,
                "batchQty": cart.remainingQty
            })
        })
        setCartList([])
        setProductList([])
    }

    const getProductList = (order_id) => {
        const URL = `${process.env.REACT_APP_API_URL}/api/delivery-challan/order-product-list/${order_id}/${depot.id}`
        axios.get(URL, JSON.stringify(), { headers: { "Content-Type": "application/json" } }).then(response => {
            console.log("data",response.data.data)
            setProductList(response.data.data)
        }).catch(err => {
            console.log(err);
        });
    }
    const handlePicking = (e, id) => {
        const getPickingQty = document.getElementById("pickingQty-id-" + id).value;
        const getBatch = document.getElementById("batch-id-" + id);
        const getBatchId = getBatch.options[getBatch.selectedIndex].value;
        const temp = [...productList]
        const index = temp.findIndex(obj => obj.id === id);
        const batchListTemp = temp[index].batchList;
        const batchIndex = batchListTemp.findIndex(obj => obj.id === parseInt(getBatchId))
        let getRemainQty = batchListTemp[batchIndex].stock_quantity - parseInt(getPickingQty);
        setRemainQty(getRemainQty)
    }
    const handleSave = () => {
        // console.log(pickingList)
    }
console.log("cart", cartList)
    return (
        <>
            <div className="container-fluid" id="myFullScreen" style={{ background: "#f3f6f9" }}>
                {/* HEADER ROW */}
                <div className="approval-view-header">
                    {/* BACK AND TITLE ROW */}
                    <div className="row">
                        <div className="col-3">
                            <span>
                                <button className='btn' onClick={handleBackToListPage}>
                                    <strong>
                                        <i className="bi bi-arrow-left-short" style={{ fontSize: "30px" }}></i>
                                    </strong>
                                </button>
                            </span>
                        </div>
                        <div className="col-6 text-center mt-4">
                            <strong>Picking List</strong>
                        </div>
                        <div className="col-3 text-right text-muted">
                            <button id="full-screen-icon" className="btn text-white" onClick={openFullscreen}>
                                <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/full-screen.svg")} />
                            </button>
                            <button id="full-screen-close-icon" className="btn text-white" onClick={closeFullscreen}>
                                <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/full-screen-close.svg")} />
                            </button>
                        </div>
                    </div>
                </div>
                <div className="bg-white">
                    <div className="container-fluid">
                        {/* FROM AND TO ROW */}
                        <div className="row">
                            {/* FROM ROW */}
                            <div className="col-xl-3 mt-5">
                                <strong className="mt-5 dark-gray-color">From</strong><br />
                                <div className="card mb-3 mt-3 border-radius-20">
                                    <div className="card-body">
                                        <div style={{ fontWeight: "500" }} className="dark-gray-color"><strong>54656dgfdgfsdf</strong></div>
                                        <div className="mt-1"><SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/phone.svg")}
                                            width="12px" height="12px" />&nbsp;<small className="text-muted">+4656535</small></div>
                                        <div className="mt-2"><SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/location.svg")} width="15px" height="15px" />
                                            <small className="text-muted">tyrytrytryty
                                            </small>
                                        </div>
                                    </div>
                                </div>
                            </div>


                            {/* TO ROW */}
                            <div className="offset-xl-6 col-xl-3 mt-5">
                                <strong className="mt-5 dark-gray-color">To</strong><br />
                                <div className="card  mb-3 mt-3 border-radius-20">
                                    <div className="card-body">
                                        <div style={{ fontWeight: "500" }} className="dark-gray-color"><strong>ghjhjh</strong></div>
                                        <div className="mt-1"><SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/phone.svg")}
                                            width="12px" height="12px" />&nbsp;<small className="text-muted">+64767</small></div>
                                        <div className="mt-2"><SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/location.svg")}
                                            width="15px" height="15px" /><small className="text-muted">ghfghgfh</small></div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="mt-5 mb-5">
                    {/* MAIN CONTENT BODY ROW */}
                    <div className="row">
                        {/* RIGHT SIDE CONTENT */}
                        <div className="col-xl-3">
                            {/* TITLE ROW */}
                            <div>
                                {/* SALES ORDER LIST TITLE CARD */}
                                <Card style={{ borderTopRightRadius: "30px", borderTopLeftRadius: "30px" }}>
                                    <CardBody>
                                        <div className="row no-gutters">
                                            <div className="col-10">
                                                <span className="text-muted">Title</span><br />
                                                <strong>Sales Order</strong>
                                            </div>
                                            <div className="col-2">
                                                <div className="position-absolute font-12" style={{ left: "15px", top: "20px" }}>
                                                    <strong>{orderList.length}</strong></div>
                                                <CircularProgress className="mt-2" variant="static" value={100} />
                                            </div>
                                        </div>
                                        <div>
                                            <span className="text-muted"><strong>ORDER LIST</strong></span>
                                        </div>
                                    </CardBody>
                                </Card>
                            </div>
                            {/* ORDER LIST VIEW ROW */}
                            <div className="scroll-product-search">
                                {
                                    orderList.map((order, index) => (
                                        <div className="mt-5 order-list-div"
                                            style={{ cursor: "pointer" }} onClick={() => handleSelectOrderList(order, index)} id={"id-" + index}>
                                            <Card className="p-3" >
                                                <CardBody>
                                                    <div className="position-absolute" style={{ left: "17px" }}>
                                                        <span>
                                                            <input className="all-radio" id={"radio-id-" + index} type="radio" />
                                                        </span>
                                                    </div>
                                                    <div>
                                                        <span className="float-right mt-1"><SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/delivery.svg")} /></span>
                                                        <span className="text-muted">Order No</span><br />
                                                        <strong>{order.order_no}</strong>
                                                    </div>
                                                    <div className="mt-5">
                                                        <span className="text-muted">Booking No</span><br />
                                                        <strong>{order.booking_no}</strong>
                                                    </div>
                                                </CardBody>
                                            </Card>
                                        </div>
                                    ))
                                }
                            </div>
                        </div>

                        {/* MIDDLE SIDE CONTENT */}
                        <div className="col-xl-6">
                            {/* TITLE ROW */}
                            <Card style={{ borderTopLeftRadius: "30px", borderTopRightRadius: "30px" }}>
                                <CardBody>
                                    <div>
                                        <span className="text-muted">Title</span><br />
                                        <strong>Product List</strong>
                                    </div>
                                    <div className="mt-5">
                                        <span className="text-muted float-right"><strong>ACTION</strong></span>
                                        <span className="text-muted"><strong>PRODUCTS INFO</strong></span>
                                    </div>
                                </CardBody>
                            </Card>
                            <div className="scroll-product-search">
                                {
                                    productList.map((product, index) => (
                                        <Card className="mt-3">
                                            <CardBody>
                                                <div>
                                                    {/* PRODUCT INFO ROW */}
                                                    {
                                                        product.batchList.length === 0 ?
                                                            <span className="light-gray-bg dark-success-color rounded p-3 float-right">
                                                                <strong>
                                                                    <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/approved-done.svg")} />
                                                                    &nbsp;Added
                                                                </strong>
                                                            </span> :
                                                            <span className="float-right">
                                                                <button className="btn text-white mr-3 float-right" style={{ background: "#6FCF97" }} onClick={() => handleAddToCart(product)}>
                                                                    <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/more.svg")} />
                                                                    &nbsp;Add
                                                                </button>
                                                            </span>
                                                    }
                                                    <span className="text-muted">{product.product_sku}</span><br />
                                                    <strong>{product.product_name}</strong><br />
                                                    <span className="text-muted">{product.category_name}</span>
                                                </div>

                                                {/* PICKING QTY ADD ROW */}
                                                <div className="mt-5 row">
                                                    <div className="col-xl-8">
                                                        <label className='level-title'>Batch</label>
                                                        <select
                                                            id={"batch-id-" + product.id}
                                                            className="form-control"
                                                            onChange={(e) => selectBatch(e, product.id)}
                                                        >
                                                            <option value="">Please Select Batch</option>
                                                            {product.batchList.map((batch) => (
                                                                <option key={batch.id} id={"product-" + product.id + "batch-" + batch.id}
                                                                    value={batch.id} data={JSON.stringify(batch)}>
                                                                    {batch.batch_no}
                                                                </option>
                                                            ))}
                                                        </select>
                                                    </div>
                                                    <div className="col-xl-2">
                                                        <label className='level-title'>Picking Qty.</label><br />
                                                        <span><input onChange={(e) => handlePicking(e, product.id)} placeholder="Write here..." id={"pickingQty-id-" + product.id} type="text" className="text-center border p-2 w-75 rounded" onKeyPress={(e) => allowOnlyNumeric(e)} value={product.selectedQty} /></span>
                                                    </div>
                                                    <div className="col-xl-2">
                                                        <label className='level-title'>Order Qty.</label><br />
                                                        <span><input readOnly type="text" id={"remainingQty-id-" + product.id} className="text-center border p-2 w-75 rounded" value={product.order_quantity} /></span>
                                                    </div>
                                                </div>
                                                <div className="mt-5" style={{ border: "1px solid #6FCF97", width: "100%" }}></div>
                                                {/* STOCK QTY AND FREE QTY ROW */}
                                                <div className="mt-3">
                                                    {
                                                        product.free_quantity === "" ? "" :
                                                            <span className="float-right">
                                                                <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/free-gray.svg")} />
                                                                <span className="text-muted">&nbsp;Free Qty&nbsp;</span>
                                                                <strong>{product.free_quantity}</strong>
                                                            </span>
                                                    }
                                                    <span className="dark-success-color" >
                                                        <SVG className="mr-2" src={toAbsoluteUrl("/media/svg/icons/project-svg/total-green.svg")} />
                                                        <span className="mr-2">Stock Qty</span>
                                                        <strong>{stockQty}</strong><strong />
                                                    </span>

                                                </div>
                                            </CardBody>
                                        </Card>
                                    ))
                                }
                            </div>
                        </div>

                        {/* LEFT SIDE CONTENT */}
                        <div className="col-xl-3">
                            {/* NEW PICKING LIST TITLE CARD */}
                            <Card style={{ borderTopRightRadius: "30px", borderTopLeftRadius: "30px" }}>
                                <CardBody>
                                    <div>
                                        <span className="text-muted">Title</span><br />
                                        <strong>New Picking List</strong>
                                    </div>
                                    <div className="mt-5">
                                        <span className="text-muted"><strong>PRODUCTS</strong></span>
                                        <span className="text-muted float-right"><strong>ACTION</strong></span>
                                    </div>
                                </CardBody>
                            </Card>

                            {/* ADD DATA INTO CART ROW */}
                            {
                                cartList.map((obj, index) => (
                                    <Card className="mt-5">
                                        <CardBody>
                                            <div className="d-flex">
                                                <div className="ml-n3 mt-3"><span className="rounded light-gray-bg pl-2 pr-2">{index + 1}</span></div>
                                                <div className="w-100 pl-5">
                                                    <div>
                                                        <button className="btn float-right" style={{ background: "#F9F9F9", color: "#0396FF" }} onClick={() => handleRemoveToCart(obj)}
                                                        >
                                                            <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/red-delete.svg")} />
                                                        </button>
                                                    </div>
                                                    <div>
                                                        <span className="text-muted">{obj.pSku}</span><br />
                                                        <strong>{obj.pName}</strong><br />
                                                        <span className="text-muted">{obj.pCategory}</span><br />
                                                    </div>
                                                    <div className="mt-5">
                                                        <span>
                                                            <span className="mr-2 text-muted">Batch</span>
                                                            <strong>{obj.batchName}</strong>
                                                        </span>
                                                        <span className="float-right">
                                                            <span className="mr-2 text-muted">Qty.</span>
                                                            <strong>{obj.batchQty}</strong>
                                                        </span>
                                                    </div>
                                                    <div className="mt-5" style={{ border: "1px solid #F2F2F2", width: "100%" }}></div>

                                                    {/* TOTAL QTY ROW */}
                                                    <div className="mt-3">
                                                        <span>
                                                            <SVG className="mr-1" src={toAbsoluteUrl("/media/svg/icons/project-svg/price-gray.svg")} />
                                                            <span className="mr-2 text-muted">Current W.A. Rate</span>
                                                            <strong>{obj.war}</strong>
                                                        </span>
                                                        <span className="float-right">
                                                            <span className="mr-2 text-muted">Transfer Qty.</span>
                                                            <strong>{obj.tQ}</strong>
                                                        </span>
                                                    </div>
                                                </div>
                                            </div>
                                        </CardBody>
                                    </Card>
                                ))
                            }

                            {/* PICKING LIST CREATE CART ROW */}
                            <Card className="mt-5">
                                <CardBody>
                                    {/* NOTES ROW */}
                                    <div className="mt-3">
                                        <label className="dark-gray-color">Note</label>
                                        <textarea id="note" type="text" className="form-control" rows="5" placeholder="Write here..." />
                                    </div>
                                </CardBody>
                            </Card>
                            <Card className="mt-5" style={{ borderBottomRightRadius: "30px", borderBottomLeftRadius: "30px" }}>
                                <CardBody>
                                    {/* TOTAL SALES ORDER ROW */}
                                    <div className="mt-3">
                                        <span>Total Sales Order</span>
                                        <strong className="float-right">{orderList.length}</strong>
                                    </div>

                                    {/* TOTAL PRODUCT LIST ROW */}
                                    <div className="mt-3">
                                        <span>Total Product List</span>
                                        <span className="float-right dark-success-color">
                                            <strong>{productList.length}</strong>
                                        </span>
                                    </div>

                                    <div className="mt-5" style={{ border: "1px solid #4F4F4F", width: "100%" }}></div>

                                    {/* TOTAL PRODUCT QTY. ROW */}
                                    <div className="mt-3">
                                        <strong>Total Product Qty.</strong>
                                        <strong className="float-right">6,920</strong>
                                    </div>

                                    {/* CREATE OR CANCEL BTN ROW */}
                                    <div className="mt-5">
                                        <button className="btn dark-danger-color" style={{ background: "#F9F9F9", color: "#0396FF" }} onClick={handleCancelPicking}>
                                            <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/reject.svg")} />
                                            &nbsp;<strong>Cancel Picking</strong>
                                        </button>
                                        <button className="btn text-white mr-3 float-right" style={{ background: "#6FCF97" }} onClick={handleSave}>
                                            <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/more.svg")} />
                                            &nbsp;<strong>Create Picking List</strong>
                                        </button>
                                    </div>
                                </CardBody>
                            </Card>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}
