    const fileValidation = (file) => {
        const regex = /^([a-zA-Z0-9\s_\\.\-:])+(.jpg|.png|.jpeg)$/;
        const isCorrectFormat = regex.test(file.name.toLowerCase());
        const fileSize = 1; //MB

        if (!isCorrectFormat) {
            return false;
        }

        if ((file.size / 1024 / 1024) > fileSize) {
            return false;
        }

        return true;
    }

