import React, { useEffect, useState } from "react";
import { Card } from "react-bootstrap";
import { CardBody } from "../../../../../../../../_metronic/_partials/controls";
import PaymentAdjustmentBreadCrum from '../../../../common/PaymentAdjustmentBreadCrum';
import { useHistory } from 'react-router-dom';
import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../../../../../_metronic/_helpers";
import { shallowEqual, useSelector } from "react-redux";
import axios from "axios";
import moment from "moment";

export default function InvoicesAdjustView(props) {
    let history = useHistory();
    const companyId = useSelector((state) => state.auth.company, shallowEqual);  
    const [fiscalYear, setFiscalYear] = useState([]);
    const [distributor, setDistributor] = useState(null);
    const [distributorId, setDistributorId] = useState(null);
    const [data, setData] = useState(null);
    const [region, setRegion] = useState(null);
    const [area, setArea] = useState(null);
    const [territory, setTerritory] = useState(null);
    const [invoiceList, setInvoiceList] = useState(null);
    const [paymentList, setPaymentList] = useState(null);
    const [paymentSumAmount, setPaymentSumAmount] = useState(0);
    const [invoiceSumAmount, setInvoiceSumAmount] = useState(0);
    const [adjustablepayment, setAdjustablePayment] = useState(null);
    const [adjustedInvoiceList, setAdjustedInvoiceList] = useState([]);
    const [balanceAmount, setBalanceAmount] = useState(0);

    useEffect(() => {
        if(props.location.state.data){
            setDistributorId(props.location.state.data.id);
            setData(props.location.state.data);
        }
    },[]);

    useEffect(() => {
        if(distributorId){
            getDistributor();
            getPaymentAndInvoiceList();
        }     
        getFiscalYear();   
    },[companyId, distributorId]);

    useEffect(() => {
        setTerritory(distributor && distributor.depotAndLocationForDistributor);
        setArea(distributor && distributor.parentLocation);
        setRegion(distributor && distributor.parentLocation.parent);

    }, [distributor]);

    const getDistributor = () => {
        let URL = `${process.env.REACT_APP_API_URL}/api/distributor/info`;
        URL += '?companyId=' + companyId;
        URL += '&distributorId=' + Number(distributorId);
        axios.get(URL).then(response => {
            setDistributor(response.data.data);           
        });
    }

    const getFiscalYear = () => {
        const URL = `${process.env.REACT_APP_API_URL}/api/accounting-year/companyWise/` + companyId
        axios.get(URL).then(response => {
            setFiscalYear(response.data.data);            
        });
    }

    const getPaymentAndInvoiceList = () => {
        let URL = `${process.env.REACT_APP_API_URL}/api/payment-collection-adjustment/payment-and-invoice-list-by-distributor`;
        URL += '?companyId=' + companyId;
        URL += '&distributorId=' + Number(distributorId);
        axios.get(URL).then(response => {
            setPaymentList(response.data.data.paymentList);            
            setInvoiceList(response.data.data.invoiceList);            
            // console.log(response.data.data);     

            const paymentSum = response.data.data.paymentList.reduce((accumulator, object) => {
                return accumulator + object.remainingAmount;
            }, 0);
            setPaymentSumAmount(paymentSum);

            const invoiceSum = response.data.data.invoiceList.reduce((accumulator, object) => {
                return accumulator + object.remainingAmount;
            }, 0);
            setInvoiceSumAmount(invoiceSum);

        });
    }


    const handlePaymentCheck = (e) => {
        const isChecked = e.target.checked;
        const value = e.target.value;

        for(const paymentItem of document.querySelectorAll('.payment-check')){
            paymentItem.checked = false;
        }
        e.target.checked = isChecked;

        for(const invoiceItem of document.querySelectorAll('.invoice-check')){
            invoiceItem.checked = false;
        }

        const selectedPayment = isChecked && paymentList.find(obj => obj.id == value);
        setAdjustablePayment(selectedPayment);

        let adjustableAmount = isChecked ? selectedPayment.remainingAmount : 0;
        const invoiceAdjustmentList = [];
        for(const invoice of invoiceList){
            if(invoice.remainingAmount <= adjustableAmount){
                document.getElementById('invoiceCheck-'+invoice.id).checked = true;
                adjustableAmount -= invoice.remainingAmount;
                invoiceAdjustmentList.push(invoice);
            } else if(adjustableAmount > 0 && invoice.remainingAmount >= adjustableAmount){
                document.getElementById('invoiceCheck-'+invoice.id).checked = true;
                invoice.remainingAmount -= adjustableAmount;
                adjustableAmount = 0;
                invoiceAdjustmentList.push(invoice);
            }
        }
        setBalanceAmount(adjustableAmount);
        setAdjustedInvoiceList(invoiceAdjustmentList);

    }

    const handleInvoiceCheck = (e) => {
        const isChecked = e.target.checked;
        const value = e.target.value;

        alert('Invoice: '+isChecked+ ' = '+value);
    }

    const handleBackToInvoicesMapListPage = () => {
        history.push('/salescollection/payment-adjustment/payment-adjustment/invoices-map');
    }

    return (
        <>
            <div>
                {/* BREAD CRUM ROW */}
                <PaymentAdjustmentBreadCrum />
            </div>

            <div>
                <Card>
                    <CardBody>
                        {/* BACK AND TITLE ROW */}
                        <div className="row">
                            <div className="col-4">
                                <span>
                                    <button className='btn' onClick={handleBackToInvoicesMapListPage}>
                                        <strong>
                                            <i className="bi bi-arrow-left-short sales-booking-view-icon"></i>
                                        </strong>
                                    </button>
                                </span>
                            </div>
                            <div className="col-4 text-center mt-3">
                                <strong>Payment Adjustment</strong>
                            </div>
                            <div className="col-4 text-xl-right text-muted mt-3">
                                <strong>20 APR 2022 10:23 AM</strong>
                            </div>
                        </div>

                         {/* HEADER ROW */}
                         <div className='row mt-5'>
                            <div className='col-xl-8'>
                                <div className="row">
                                    <div className="col-3">
                                        <span className="dark-gray-color" style={{ fontWeight: "500" }}>
                                            {region && region.locationType.name}
                                        </span><br />
                                        <span><strong>
                                            {region && region.name}
                                        </strong></span>
                                    </div>
                                    <div className="col-3">
                                        <span className="dark-gray-color" style={{ fontWeight: "500" }}>
                                            {area && area.locationType.name}
                                        </span><br />
                                        <span><strong>
                                            {area && area.name}
                                        </strong></span>
                                    </div>
                                    <div className="col-3">                                        
                                        <span className="dark-gray-color" style={{ fontWeight: "500" }}>
                                            {territory && territory.location_type_name}
                                        </span><br />
                                        <span><strong>
                                        {territory && territory.location_name}
                                        </strong></span>
                                    </div>
                                    <div className="col-3">
                                        <span className="dark-gray-color"
                                            style={{ fontWeight: "500" }}>Timeline</span>
                                        <span>
                                            <select className="form-control border-0 ml-n5 payment-collection-invoices-select">
                                                <option value="" selected>All</option>
                                                {fiscalYear && fiscalYear.map((data) =>
                                                    (<option key={data.id} value={data.id}>{data.fiscalYearName}</option>)
                                                )}
                                            </select>
                                        </span>
                                    </div>
                                </div>
                            </div>
                            <div className='col-xl-4 d-flex justify-content-end'>
                                <div className="d-flex  mr-5">
                                    <div>
                                        <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/Lays.svg")} width="35px" height="35px" />
                                    </div>
                                    <div className="ml-3">
                                        <span>
                                            <span style={{ fontWeight: "500" }}><strong>{data && data.distributor_name}</strong></span>
                                            <p>
                                                <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/phone.svg")} width="14px" height="14px" />&nbsp;
                                                {distributor && distributor.info.contactNo}
                                            </p>
                                        </span>
                                    </div>
                                </div>
                                <div>
                                    <button className='btn sales-credit-btn' style={{ padding: "0px 15px", borderRadius: "13px" }}>
                                        <span className='text-white' style={{ fontSize: "0.83rem" }}>
                                            Balance<br />
                                            {data && data.ledger_balance}
                                        </span>
                                    </button>
                                </div>
                            </div>
                        </div>
                    </CardBody>
                </Card>
            </div>

            {/* CONTENT BODY ROW */}
            <div className="row mt-5">
                <div className="col-xl-8">
                    <div className="row">
                        <div className="col-xl-6">
                            <Card>
                                <CardBody>
                                    <span className="text-muted">PAYMENTS</span>
                                    <span className="float-right mt-n2">
                                        <i class="bi bi-arrow-down-short text-danger" style={{ fontSize: "20px" }}></i>
                                        <span className="mt-n3"><strong>{paymentSumAmount}</strong></span>
                                    </span>
                                </CardBody>
                            </Card>

                            {paymentList && paymentList.map((data) =>(
                            <Card className="mt-2" key={data.id}>
                                <CardBody>
                                    <div className="row">
                                        <div className="col-1">
                                            <div>
                                                <input class="text-primary payment-check" type="checkbox" name="paymentCheck" id={'paymentCheck-'+data.id} 
                                                value={data.id} aria-label="..." onClick={handlePaymentCheck}/>
                                            </div>
                                        </div>
                                        <div className="col-10">
                                            <span className="text-muted"><strong>Money Receipt No. {data.moneyReceiptNo}</strong></span><br />
                                            <span className="text-muted">{moment(data.paymentDate).format('DD MMM YYYY').toUpperCase()}</span><br />
                                            <span className="text-muted">Balance</span><br />
                                            <span><strong>{data.remainingAmount}</strong></span>
                                        </div>
                                        <div className="col-1">
                                        <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/vertical-dot.svg")} width="15px" height="15px" />
                                        </div>
                                    </div>
                                </CardBody>
                            </Card>
                            ))}

                        </div>
                        <div className="col-xl-6">
                            <Card>
                                <CardBody>
                                    <span className="text-muted">INVOICES</span>
                                    <span className="float-right mt-n2">
                                        <i class="bi bi-arrow-down-short text-danger" style={{ fontSize: "20px" }}></i>
                                        <span className="mt-n3"><strong>{invoiceSumAmount}</strong></span>
                                    </span>
                                </CardBody>
                            </Card>

                            {invoiceList && invoiceList.map((data) =>(
                            <Card className="mt-2" key={data.id}>
                                <CardBody>
                                    <div className="row">
                                        <div className="col-1">
                                            <div>
                                                <input class="text-primary invoice-check" type="checkbox" name="invoiceCheck" id={'invoiceCheck-'+data.id} 
                                                value={data.id} aria-label="..." onClick={handleInvoiceCheck}/>
                                            </div>
                                        </div>
                                        <div className="col-10">
                                            <span className="text-muted"><strong>No. {data.invoiceNo} ({data.invoiceNature.name})</strong></span><br />
                                            <span className="text-muted">{moment(data.invoiceDate).format('DD MMM YYYY').toLocaleUpperCase()}</span><br />
                                            <span className="text-muted">Balance</span><br />
                                            <span><strong>{data.remainingAmount}</strong></span>
                                        </div>
                                        <div className="col-1">
                                        <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/vertical-dot.svg")} width="15px" height="15px" />
                                        </div>
                                    </div>
                                </CardBody>
                            </Card>
                            ))}

                        </div>
                    </div>
                </div>
                <div className="col-xl-4">
                    <Card>
                        <CardBody>
                            <span className="text-muted">ADJUSTABLE</span>
                            <span className="float-right mt-n2">
                                <i class="bi bi-arrow-up-short text-primary" style={{ fontSize: "20px" }}></i>
                                <span className="mt-n3"><strong>{paymentSumAmount}</strong></span>
                            </span>
                        </CardBody>
                    </Card>

                    <Card>
                        <CardBody>
                            {/* MONEY RECEIPT NO ROW */}
                            <div>
                                <span  className="dark-gray-color font-15">
                                    <strong>Money Receipt No. {adjustablepayment && adjustablepayment.moneyReceiptNo}</strong></span><br />
                                <small className="text-muted">
                                    {adjustablepayment && moment(adjustablepayment.paymentDate).format('DD MMM YYYY').toUpperCase()}</small>
                                <span  className="float-right mt-n5"><SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/vertical-dot.svg")} width="15px" height="15px" /></span>
                            </div>

                            {/* BALANCE AND TOTAL BALANCE ROW */}
                            <div className="row mt-5">
                                <div className="col-6">
                                    <div>Balance</div>
                                    <div className="mt-3">Total Payment Amount </div>
                                </div>
                                <div className="col-6 text-right">
                                    <div><strong>{adjustablepayment ? adjustablepayment.remainingAmount : 0}  </strong></div>
                                    <div className="mt-3"><strong>- {adjustablepayment ? adjustablepayment.remainingAmount-balanceAmount : 0}  </strong></div>
                                </div>
                            </div>
                            <div className="mt-5" style={{border:"1px solid #828282",width:"100%"}}></div>
                            {/* TOTAL ROW */}
                            <div className="row mt-3">
                                    <div className="col-8">
                                        <strong>Balance(After Adj.)</strong>
                                    </div>
                                    <div className="col-4 text-right">
                                        <strong>{balanceAmount}</strong>
                                    </div>
                            </div>


                            {/* INVOICES NO DAY 1 ROW  */}
                            {adjustedInvoiceList && adjustedInvoiceList.map((data) => (                                
                                <>
                                <div className="mt-5">
                                    <span  className="dark-gray-color font-15"><strong>INVOICES NO. {data.invoiceNo}</strong></span><br />
                                    <small className="text-muted">12 APR 2022 10:23 AM <span>12 APR 2022 10:23 AM (Elapsed Day 1 days)</span></small>
                                </div>
                                <div className="row mt-3 ">
                                        <div className="col-8">
                                            <span className="dark-gray-color font-15"><strong>ORD</strong> <span className="dark-success-color">(+15%)</span></span>
                                        </div>
                                        <div className="col-4 text-right">
                                            <span className="dark-success-color">+227.47 </span>
                                        </div>
                                </div>

                                {/* BALANCE AND TOTAL BALANCE ROW */}
                                <div className="row mt-2 text-muted">
                                    <div className="col-6">
                                        <div>Balance</div>
                                        <div className="mt-3">Payment Amount </div>
                                    </div>
                                    <div className="col-6 text-right">
                                        <div>{data.remainingAmount}</div>
                                        <div className="mt-3">-4,500.00  </div>
                                    </div>
                                </div>
                                <div className="mt-5" style={{border:"1px solid #828282",width:"100%"}}></div>
                                {/* TOTAL ROW */}
                                <div className="row mt-3 text-muted">
                                        <div className="col-8">
                                            <span>Balance(After Adj.)</span>
                                        </div>
                                        <div className="col-4 text-right">
                                            <span>00.00 </span>
                                        </div>
                                </div>
                                </>                                
                            ))}                          



                            {/* INVOICES NO DAY 2 ROW  */}
                            <div className="mt-5">
                                <span  className="dark-gray-color font-15"><strong>INVOICES NO. 11921366</strong></span><br />
                                <small className="text-muted">12 APR 2022 10:23 AM <span>12 APR 2022 10:23 AM (Elapsed Day 2 days)</span></small>
                            </div>
                            <div className="row mt-3 ">
                                    <div className="col-8">
                                        <span className="dark-gray-color font-15"><strong>ORD</strong> <span className="dark-success-color">(+15%)</span></span>
                                    </div>
                                    <div className="col-4 text-right">
                                        <span className="dark-success-color">+2,472.53 </span>
                                    </div>
                            </div>

                            {/* BALANCE AND TOTAL BALANCE ROW */}
                            <div className="row mt-2 text-muted">
                                <div className="col-6">
                                    <div>Balance</div>
                                    <div className="mt-3">Payment Amount </div>
                                </div>
                                <div className="col-6 text-right">
                                    <div>30,000.00</div>
                                    <div className="mt-3">-29,500.00</div>
                                </div>
                            </div>
                            <div className="mt-5" style={{border:"1px solid #828282",width:"100%"}}></div>
                            {/* TOTAL ROW */}
                            <div className="row mt-3 text-muted">
                                    <div className="col-8">
                                        <span>Balance(After Adj.)</span>
                                    </div>
                                    <div className="col-4 text-right">
                                        <span>500.00</span>
                                    </div>
                            </div>

                            <div className="p-5 mt-4">
                                <button className="border-0 w-100 rounded p-3 text-white" style={{background: "#56CCF2"}}>
                                <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/adjustment.svg")} width="15px" height="15px" />
                                &nbsp;Payment Adjustment
                                </button>
                            </div>
                        </CardBody>
                    </Card>
                </div>
            </div>
        </>
    );
}
