import React, {useEffect, useState} from 'react';
import {toAbsoluteUrl} from "../../../../../_metronic/_helpers";
import {Card, CardBody} from "../../../../../_metronic/_partials/controls";
import LocationTreeView from '../../../SalesCollection/CommonComponents/LocationTreeView';
import {showError} from '../../../../pages/Alert';
import axios from "axios";
import {useIntl} from "react-intl";
import {shallowEqual, useSelector} from "react-redux";
import MisReportBreadCrum from '../MisReportBreadCrum';
import Autocomplete from "@material-ui/lab/Autocomplete";
import {TextField} from "@material-ui/core";
import Button from '@material-ui/core/Button';
import moment from "moment";
import Flatpickr from "react-flatpickr";
import "flatpickr/dist/themes/material_green.css";

export default function OrderToCashCycle() {
    const fields = {
        locationId: "",
        salesOfficerId: "",
        distributorId: "",
        startDate: "",
        endDate: "",
        report: "",
        reportType: ""
    };
    const intl = useIntl();
    const [inputs, setInputs] = useState(fields);
    const selectedCompany = useSelector((state) => state.auth.company, shallowEqual);
    const userLoginId = useSelector((state) => state.auth.user.userId, shallowEqual);
    const [locationTree, setLocationTree] = useState([]);
    const [locationIds, setLocationIds] = useState([]);
    const [salesOfficerList, setSalesOfficerList] = useState([]);
    const [salesOfficerValue, setSalesOfficerValue] = useState(null);
    const [distributorValue, setDistributorValue] = useState(null);
    const [distributorList, setDistributorList] = useState([]);
    const [showReport, setShowReport] = useState('');
    const [reportData, setReportData] = useState([]);

    useEffect(() => {
        getLocationTreeList({userLoginId: userLoginId, companyId: selectedCompany});
    }, [userLoginId, selectedCompany]);

    const getLocationTreeList = (params) => {
        const URL = `${process.env.REACT_APP_API_URL}/api/location-tree/locationTree/${params.userLoginId}/${params.companyId}`;
        axios.get(URL).then(response => {
            const locationTree = response.data.data;
            setLocationTree(locationTree);
        }).catch(err => {
            showError("Cannot get Location Tree data.");
        });
    }

    const selectLocationTreeNode = (node) => {
        let id = "summary-id-" + node.id;
        const getId = document.getElementById(id);
        const getElements = document.getElementsByClassName('tree-nav__item_demo tree-nav__item-title');

        for (let i = 0; i < getElements.length; i++) {
            getElements[i].classList.remove('tree-nav-item');
        }
        if (getId) {
            getId.classList.add('tree-nav-item');
            setInputs({...inputs, "locationId": node.id})
        }
        if (locationIds.length !== 0) { // for clear state
            locationIds.length = 0;
        }
        getLocationIds(node);
    }

    const getLocationIds = (node) => {
        let temp = [...locationIds]
        let index = temp.findIndex(id => id === node.id)
        if (index === -1) {
            locationIds.push(node.id)
        }
        node.children.map(nodeChild => {
            getLocationIds(nodeChild)
        })
    }

    const getSalesOfficerListByLocation = (locationId, companyId) => {
        if (locationIds.length === 0) {
            showError('Please Select Location');
            return false;
        }

        const URL = `${process.env.REACT_APP_API_URL}/auth/get-sales-officer-location/${locationId}/${companyId}`;
        axios.get(URL).then(response => {
            if (response.data.data != undefined) {
                setSalesOfficerList(response.data.data);
            } else {
                setSalesOfficerList([]);
                setSalesOfficerValue('');
            }
        }).catch(err => {
            showError("Cannot get Sales Officer data.");
        });
    }


    const getAllDistributorList = (userLoginId, locationId, companyId) => {
        if (locationIds.length === 0) {
            showError('Please Select Location');
            return false;
        }
        const URL = `${process.env.REACT_APP_API_URL}/api/distributor/distributorList/${userLoginId}/${locationId}/${companyId}`;//API call from backend
        axios.get(URL).then(response => {//get data in response
            if (response.data.data != undefined && response.data.data.length != 0) {
                setDistributorList(response.data.data);
            } else {
                setDistributorList([]);
                setDistributorValue('');
            }
        }).catch(err => {
            showError("Cannot get Distributor data.");
        });
    }

    const handleChange = (event) => {
        let name = event.target.name;
        let value = event.target.value;
        setInputs(values => ({ ...values, [name]: value }))
    }

    const validate = () => {
        if (locationIds.length === 0) {
            showError('Location is required');
            return false;
        } else if (!inputs.salesOfficerId) {
            showError('Sales Officer is required');
            return false;
        } else if (!inputs.distributorId) {
            showError('Distributor Name is required.');
            return false;
        } else if (!inputs.startDate) {
            showError("Start date  is required");
            return false;
        } else if (!inputs.endDate) {
            showError("End date is required");
            return false;
        } else if (inputs.endDate < inputs.startDate) {
            showError("End Date should be greater than Start Date");
            return false;
        } else if (!inputs.reportFormate) {
            showError('Report Format is required');
            return false;
        } else if (!inputs.reportType) {
            showError('Report Type is required.');
            return false;
        }
        return true;
    }

    const submit = () => {
        if (!validate()) {
            return false;
        }
        let queryParams = '?locationId=' + inputs.locationId;
        queryParams += '&reportFormate=' + inputs.reportFormate;
        queryParams += '&companyId=' + selectedCompany;
        queryParams += '&locationIds=' + locationIds;
        queryParams += '&salesOfficerId=' + inputs.salesOfficerId;
        queryParams += '&distributorId=' + inputs.distributorId;
        queryParams += '&startDate=' + inputs.startDate;
        queryParams += '&endDate=' + inputs.endDate;
        queryParams += '&reportType=' + inputs.reportType;
        const URL = `${process.env.REACT_APP_API_URL}/api/report/order-to-cash-cycle` + queryParams;
        axios.get(URL, { responseType: 'blob' }).then(response => {
            if (inputs.reportFormate === "PDF") {
                const url = window.URL.createObjectURL(new Blob([response.data]));
                const link = document.createElement('a');
                link.href = url;
                link.setAttribute('download', "OrderToCashCycleInvoice.pdf");
                document.body.appendChild(link);
                link.click();
                document.body.removeChild(link);
            } else {
                const url = window.URL.createObjectURL(new Blob([response.data]));
                const link = document.createElement('a');
                link.href = url;
                link.setAttribute('download', "OrderToCashCycleInvoice.xlsx");
                document.body.appendChild(link);
                link.click();
                document.body.removeChild(link);
            }
        }).catch(err => {
            showError();
        });
    }

    return (
        <>
            <div>
                <MisReportBreadCrum menuTitle="Order To Cash Cycle"/>
            </div>
            <div>
                {/* BREAD CRUM ROW */}
                {/* <InventoryBreadCrum /> */}
                {/* TODAY SALE ROW */}
                {/* <InventoryStockHeader showStockData={true} /> */}
                {/* <ReportsSubTabsHeader /> */}
            </div>
            <div>
                <Card>
                    <CardBody>
                        <div className='row'>
                            {/* LEFT SIDE TREE ROW */}
                            <div className='col-xl-3' style={{ borderRight: "1px solid #F2F2F2" }}>
                                <div style={{ borderBottom: "1px solid #F2F2F2" }}>
                                    <label>
                                        <img src={toAbsoluteUrl("/images/loc3.png")}
                                            style={{ width: "20px", height: "20px", textAlign: "center" }}
                                            alt='Company Picture' />
                                        <strong style={{ marginLeft: "10px", color: "#828282" }}>{intl.formatMessage({ id: "COMMON.LOCATION_ALL" })}</strong>
                                    </label>
                                </div>
                                {/* TREE */}
                                <LocationTreeView tree={locationTree} selectLocationTreeNode={selectLocationTreeNode} />
                            </div>
                            {/* RIGHT SIDE LIST ROW */}
                            <div className='col-xl-9'>
                                <div className='mt-5 row'>
                                    <div className='col-xl-8'>
                                        <div style={{marginTop: "5px"}}>
                                            <label className='level-title'>Sales Officer<i style={{color: "red"}}>*</i></label><br/>
                                            <Autocomplete
                                                name="salesOfficerId"
                                                options={salesOfficerList}
                                                onKeyDown={e => {
                                                    getSalesOfficerListByLocation(inputs.locationId, selectedCompany)
                                                }}
                                                getOptionLabel={(option) => option.salesOfficerName}
                                                value={salesOfficerValue}
                                                onChange={(event, newValue) => {
                                                    setSalesOfficerValue(newValue)
                                                    if (newValue !== null) {
                                                        setInputs({...inputs, salesOfficerId: newValue.id});
                                                    } else {
                                                        setInputs({...inputs, salesOfficerId: ''});
                                                    }
                                                }}
                                                renderInput={(params) => (
                                                    <TextField {...params} label="Select Sales Officer*"/>
                                                )}
                                            />
                                        </div>
                                    </div>
                                </div>
                                <div className='mt-5 row'>
                                    <div className='col-xl-8'>
                                        <label className='level-title'><span className="mr-1">Distributor<i
                                            style={{color: "red"}}>*</i></span></label>
                                        <Autocomplete
                                            name="distributorId"
                                            options={distributorList}
                                            onKeyDown={e => {
                                                getAllDistributorList(userLoginId, inputs.locationId, selectedCompany)
                                            }}
                                            getOptionLabel={(option) => option.distributorName}
                                            value={distributorValue}
                                            onChange={(event, newValue) => {
                                                setDistributorValue(newValue)
                                                if (newValue !== null) {
                                                    setInputs({...inputs, distributorId: newValue.distributorId});
                                                } else {
                                                    setInputs({...inputs, distributorId: ''});
                                                }
                                            }}
                                            renderInput={(params) => (
                                                <TextField {...params} label="Select Distributor*"/>
                                            )}
                                        />
                                    </div>
                                </div>

                                <div className='row'>
                                    <div className='form-group col-lg-8 first-level-top'>
                                        <label className='level-title'>Start Date<i style={{ color: "red" }}>*</i></label>
                                        <Flatpickr className="form-control" id="startDate" placeholder="dd-MM-yy"
                                            name='startDate'
                                            value={inputs.startDate ? moment(inputs.startDate, "YYYY-MM-DD").format("DD-MMM-YYYY") : ''}
                                            options={{ dateFormat: "d-M-Y" }}
                                            onChange={(value) => {
                                                setInputs({
                                                    ...inputs, "startDate": moment(new Date(value)).format("YYYY-MM-DD")
                                                })

                                            }}
                                        />
                                    </div>
                            </div>

                            <div className='row'>
                                    <div className='form-group col-lg-8 first-level-top'>
                                        <label className='level-title'>End Date<i style={{ color: "red" }}>*</i></label>
                                        <Flatpickr className="form-control" id="endDate" placeholder="dd-MM-yy"
                                            name='endDate'
                                            value={inputs.endDate ? moment(inputs.endDate, "YYYY-MM-DD").format("DD-MMM-YYYY") : ''}
                                            options={{ dateFormat: "d-M-Y" }} required
                                            onChange={(value) => {
                                                setInputs({
                                                    ...inputs, "endDate": moment(new Date(value)).format("YYYY-MM-DD")
                                                })
                                            }}
                                        />
                                    </div>
                            </div>

                                <div className='row'>
                                    <div className='form-group col-lg-8 first-level-top'>
                                        <div>
                                            <label className='level-title'><span className="mr-1">Report Format</span><span className="text-danger ">*</span></label>
                                            <select className="form-control" id="reportFormate" name="reportFormate"
                                                onChange={(e) => handleChange(e)}
                                            >
                                                <option value="">Select Report Format</option>
                                                <option value="PDF">PDF</option>
                                                {/*<option value="EXCEL">EXCEL</option>*/}

                                                {/* {
                                                allReport.map((report)=>(
                                                <option key={report.id} value={report.id}>
                                                    {report.name}
                                                </option> 
                                                ))
                                            }    */}
                                            </select>
                                        </div>
                                    </div>
                                </div>

                                <div className='row'>
                                    <div className='form-group col-lg-8 first-level-top'>
                                        <div>
                                            <label className='level-title'><span className="mr-1">Report Type</span><span className="text-danger ">*</span></label>
                                            <select className="form-control" id="reportType" name="reportType"
                                            onChange={(e)=>handleChange(e)}
                                            >
                                                <option value="">Select Report Type</option>
                                                <option value="byInvoice">By Invoice</option>
                                                {/*<option value="byDistributor">By Distributor</option>*/}
                                                {/*<option value="bySalesOfficer">By SalesOfficer</option>*/}
                                                {/*<option value="byArea">By Area</option>*/}
                                                {/*<option value="byZone">By Zone</option>*/}
                                            </select>
                                        </div>
                                    </div>
                                </div>

                                <Button className="float-right mt-5" id="gotItBtn" variant="contained" color="primary"
                                    onClick={submit}>Submit</Button>

                            {/*<div className="float-right">*/}
                            {/*<Button className="mt-5 mr-5" id="gotItBtn" variant="contained" color="primary"  onClick={getReportData}>*/}
                            {/*    View*/}
                            {/*</Button>                      */}
                            {/*</div> */}
                            </div> 
                            </div> 

                            {showReport === 'byInvoice' && <div>
                            <div className="report-table" >
                                <table class="center" style={{ width: "100%" }}>
                                    <caption style={{ captionSide: "top", width: "100%", fontSize: "x-large", fontWeight: "bold", color: "black" }}>Invoice</caption>

                                    <tr>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Zone</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Area</th>                               
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Territory</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Sales Officer</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Distributor</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Invoice</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Invoice Date</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Gross Invoice Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Trade Discount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Net Invoice Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Collection Date</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Slip No</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Incentive Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Collection Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Collection Days</th>                                 

                                    </tr>
                                </table>
                            </div>
                        </div>}

                        {showReport === 'byDistributor' && <div>
                            <div className="report-table" >
                                <table class="center" style={{ width: "100%" }}>
                                    <caption style={{ captionSide: "top", width: "100%", fontSize: "x-large", fontWeight: "bold", color: "black" }}>Distributor</caption>

                                    <tr>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Zone</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Area</th>                               
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Territory</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Sales Officer</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Distributor</th>                               
                                <th style={{fontWeight:"bold", textAlign:"center"}}>No of Invoice</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Gross Invoice Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Trade Discount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Net Invoice Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Collection Date</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Slip No</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Incentive Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Collection Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Avg Collection Days</th>                                 

                                    </tr>
                                </table>
                            </div>
                        </div>}


                        {showReport === 'bySalesOfficer' && <div>
                            <div className="report-table" >
                                <table class="center" style={{ width: "100%" }}>
                                    <caption style={{ captionSide: "top", width: "100%", fontSize: "x-large", fontWeight: "bold", color: "black" }}>Sales Officer</caption>

                                    <tr>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Zone</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Area</th>                               
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Territory</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Sales Officer</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Distributor</th>                               
                                <th style={{fontWeight:"bold", textAlign:"center"}}>No of Invoice</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Gross Invoice Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Trade Discount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Net Invoice Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Collection Date</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Slip No</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Incentive Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Collection Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Avg Collection Days</th>                                 

                                    </tr>
                                </table>
                            </div>
                        </div>}

                        {showReport === 'byArea' && <div>
                            <div className="report-table" >
                                <table class="center" style={{ width: "100%" }}>
                                    <caption style={{ captionSide: "top", width: "100%", fontSize: "x-large", fontWeight: "bold", color: "black" }}>Area</caption>

                                    <tr>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Zone</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Area</th>                               
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Territory</th>                                                      
                                <th style={{fontWeight:"bold", textAlign:"center"}}>No of Invoice</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Gross Invoice Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Trade Discount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Net Invoice Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Collection Date</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Slip No</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Incentive Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Collection Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Avg Collection Days</th>                                 

                                    </tr>
                                </table>
                            </div>
                        </div>}

                        {showReport === 'byZone' && <div>
                            <div className="report-table" >
                                <table class="center" style={{ width: "100%" }}>
                                    <caption style={{ captionSide: "top", width: "100%", fontSize: "x-large", fontWeight: "bold", color: "black" }}>Zone</caption>

                                    <tr>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Zone</th>                                                    
                                <th style={{fontWeight:"bold", textAlign:"center"}}>No of Invoice</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Gross Invoice Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Trade Discount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Net Invoice Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Collection Date</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Slip No</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Incentive Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Collection Amount</th>
                                <th style={{fontWeight:"bold", textAlign:"center"}}>Avg Collection Days</th>                                 

                                    </tr>
                                </table>
                            </div>
                        </div>}
                    </CardBody>
                </Card>
            </div>
        </>
    );
}
