import React, { useEffect, useMemo } from "react";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory, {
  PaginationProvider,
} from "react-bootstrap-table2-paginator";
import { shallowEqual, useDispatch, useSelector } from "react-redux";
import * as uiHelpers from "../ProductConfigureUIHelpers";
import {
  getSelectRow,
  getHandlerTableChange,
  NoRecordsFoundMessage,
  PleaseWaitMessage,
  sortCaret,
} from "../../../../../../_metronic/_helpers";
import { Pagination } from "../../../../../../_metronic/_partials/controls";
import { useProductUIContext } from "../ProductUIContext";
//import {ActionsColumnFormatter} from "./ActionsColumnFormatter";
import { Formik } from "formik";
import { isEqual } from "lodash";

const prepareFilter = (queryParams, values) => {
  const { searchText } = values;
  const newQueryParams = { ...queryParams };
  const filter = {};
  // Filter by all fields
   filter.manufacture = searchText;
   filter.VINCode = searchText;
  newQueryParams.filter = filter;
  return newQueryParams;
};


export function ProductConfigureTable() { 
  const columns = [
    {
      dataField: "#",
      text: "SKU",
    },
    {
      dataField: "#",
      text: "Product Name",
    },
    {
      dataField: "action",
      text: "Actions",
      // formatter: ActionsColumnFormatter,
      // formatExtraData: {
      //   openEditAccountingYearPage: accountingYearUIProps.openEditAccountingYearPage,
      //   openDeleteAccountingYearDialog: accountingYearUIProps.openDeleteAccountingYearDialog,
      // },
      classes: "text-right pr-0",
      headerClasses: "text-right pr-3",
      style: {
        minWidth: "100px",
      },
    },
  ];


  return (
    <>
     <BootstrapTable
                wrapperClasses="table-responsive"
                classes="table table-head-custom table-vertical-center overflow-hidden"
                bootstrap4
                bordered={false}
                remote
                keyField="id"
                data=""
                columns={columns}
                defaultSorted={uiHelpers.defaultSorted}
              />
    </>
  );
}
