import { shallowEqual, useSelector } from "react-redux";
import SalesTabsHeader from "../../common/SalesTabsHeader";
import { BreadCrum } from "../../common/BreadCrum";
import XLSX from 'sheetjs-style'; 

export function TradeDiscount() {
    const selectedCompany = useSelector((state) => state.auth.company, shallowEqual);
    const [producCategoryTree, setLocationProductCategory] = useState([]);
    const [selectedProductCategory, setSelectedProductCategory] = useState({});
    const [sessionData, setSessionData] = useState({ userLoginId: 1, companyId: 2, accountingYearId: 1 });
    const [searchParams, setSearchParams] = useState(sessionData);
    const [categoryIds, setCategoryIds] = useState([]);
    const [tradeDiscountList, setTradeDiscountList] = useState([]);
    const [breadCrumParentProductCategoryTypeName, setBreadCrumParentProductCategoryTypeName] = useState("");
    const [breadCrumParentProductCategoryName, setBreadCrumParentProductCategoryName] = useState("");
    let [singleAll, setSingleAll] = useState([]);
    const [anchorEl, setAnchorEl] = useState(null);
    const open = Boolean(anchorEl);
    const id = open ? 'simple-popper' : undefined;


    useEffect(() => {
        document.getElementById('pills-trade-discount-tab').classList.add('active')
        getProductCategoryTreeList(sessionData);
    }, []);

    const handleClick = (event) => {
        setAnchorEl(anchorEl ? null : event.currentTarget);
    }

    const handleExport = () => {
        let exportData = [];
        let data = [...singleAll]
        data.map(row => {
            let obj = {};
            obj.SKU = row.productSku;
            obj.Category = row.productCategory;
            obj.Name = row.name;
            obj.TradePrice = row.tradePrice;
            obj.Date = row.date;
            if (row.cashAmount != "" || row.cashValue != ""  || row.cashType != ""  || row.discountCashType != "") {
                obj.CashAmount = row.cashAmount
                obj.CashDiscountValue =(row.discountCashType === undefined ?'':row.discountCashType) + " " + (row.cashValue === undefined ?'':row.cashValue) + (row.cashType === undefined ?'':row.cashType)
            }
            if (row.creditAmount != "" || row.creditValue != ""  || row.creditType != ""  || row.discountCreditType != "") {
                obj.CreditAmount = row.creditAmount
                obj.CreditDiscountValue =(row.discountCreditType === undefined ?'':row.discountCreditType) + " " + (row.creditValue === undefined ?'':row.creditValue) + (row.creditType === undefined ?'':row.creditType)
            }
            exportData.push(obj);
        })
        const workbook = XLSX.utils.book_new(); 
        const Heading = [
            ["SKU", "Category", "Name", "Trade Price", "Date", "Cash Amount", "Cash Discount Value", "Credit Amount", "Credit Discount Value"],
          ];             
        const worksheet = XLSX.utils.json_to_sheet(exportData, { origin: 'A2', skipHeader: true });

        // creating sheet and adding data from 2nd row of column A.
        // Leaving first row to add Heading 
        XLSX.utils.sheet_add_aoa(worksheet, Heading, { origin: 'A1' });

        worksheet['A1'].s = {
            font: {
                name: 'arial',
                sz: 11,
                bold: true,
                color: "#F2F2F2"
            },
        }
        worksheet['B1'].s = {
            font: {
                name: 'arial',
                sz: 11,
                bold: true,
                color: "#F2F2F2"
            },
        }

        worksheet['C1'].s = {
            font: {
                name: 'arial',
                sz: 11,
                bold: true,
                color: "#F2F2F2"
            },
        }
        worksheet['D1'].s = {
            font: {
                name: 'arial',
                sz: 11,
                bold: true,
                color: "#F2F2F2"
            },
        }
        worksheet['E1'].s = {
            font: {
                name: 'arial',
                sz: 11,
                bold: true,
                color: "#F2F2F2"
            },
        }
        worksheet['F1'].s = {
            font: {
                name: 'arial',
                sz: 11,
                bold: true,
                color: "#F2F2F2"
            },
        }
        worksheet['G1'].s = {
            font: {
                name: 'arial',
                sz: 11,
                bold: true,
                color: "#F2F2F2"
            },
        }
        worksheet['H1'].s = {
            font: {
                name: 'arial',
                sz: 11,
                bold: true,
                color: "#F2F2F2"
            },
        }
        worksheet['I1'].s = {
            font: {
                name: 'arial',
                sz: 11,
                bold: true,
                color: "#F2F2F2"
            },
        }

        XLSX.utils.book_append_sheet(workbook, worksheet, "Sheet1");
        XLSX.writeFile(workbook, "TradeDiscountData.xlsx");
        setSingleAll([])
    }
    const getProductCategoryTreeList = (params) => {
        const URL = `${process.env.REACT_APP_API_URL}/api/product-category/list-info/${params.companyId}`;
        axios.get(URL).then(response => {
            if (response.data.data.childProductCategoryDtoList != "") {
                setLocationProductCategory(response.data.data.childProductCategoryDtoList);
            }
        }).catch(err => {
            showError("Can not get product category tree data.");
        });
    }

    const selectProductCategoryTreeNode = (node) => {
        setSelectedProductCategory(node);
        const URL = `${process.env.REACT_APP_API_URL}/api/product-category/${node.id}`;
        axios.get(URL).then(response => {
            setBreadCrumParentProductCategoryTypeName(response.data.data.productCategoryType.name);
            setBreadCrumParentProductCategoryName(response.data.data.name);
        }).catch(err => {
            showError("Can not get product category tree data.");
        });
        let temp = [...categoryIds]
        let index = temp.findIndex(id => id === node.id)
        if (index === - 1) {
            categoryIds.push(node.id)
        }
        node.children.map(nodeChild => {
            selectProductCategoryTreeNode(nodeChild)
        })
        const URLa = `${process.env.REACT_APP_API_URL}/api/trade-discount/get-all-product?categoryIds=${categoryIds}&accYearId=${1}`
        axios.get(URLa).then(response => {
            if (response.data.data) {
                setTradeDiscountList(response.data.data)
                setCategoryIds([]);
            }
        });
    }

    return (
        <>
            <div>
                {/* BREAD CRUM ROW */}
                <BreadCrum />
                {/* TODAY SALE ROW */}
                <SalesTabsHeader />
            </div>
            <div>
                <Card>
                    <CardBody>
                        <div className='row'>
                            {/* LEFT SIDE TREE ROW */}
                            <div className='col-xl-3' style={{ borderRight: "1px solid #F2F2F2" }}>
                                <div style={{ borderBottom: "1px solid #F2F2F2" }}>
                                    <label>
                                        <img src={toAbsoluteUrl("/images/loc3.png")}
                                            style={{ width: "20px", height: "20px", textAlign: "center" }}
                                            alt='Company Picture' />
                                        <strong style={{ marginLeft: "10px", color: "#828282" }}>Products (All)</strong>
                                    </label>
                                </div>
                                {/* TREE */}
                                <ProductCategoryTreeView tree={producCategoryTree}
                                    selectProductCategoryTreeNode={selectProductCategoryTreeNode}
                                />
                            </div>
                            {/* RIGHT SIDE LIST ROW */}
                            <div className='col-xl-9'>
                                {/* SEARCHING AND FILTERING ROW */}
                                <div className="row">
                                    <div className="col-xl-3">
                                        <div style={{ position: "absolute", padding: "7px", marginTop: "3px" }}>
                                            <img src={toAbsoluteUrl("/images/search.png")} width="20px" height="20px" />
                                        </div>
                                        <form className="form form-label-right">
                                            <input type="text" className="form-control" name="searchText"
                                                placeholder="Search Here"
                                                style={{ paddingLeft: "28px" }}
                                            />
                                        </form>
                                    </div>
                                    <div className="col-xl-9 d-flex justify-content-end">
                                        <div>
                                            <button className='btn' aria-describedby={id} onClick={handleClick}
                                                style={{ color: "#828282" }}>
                                                Fiscal Year 2022-23&nbsp;
                                                <i className="bi bi-chevron-down"></i>
                                            </button>
                                            <Popper id={id} open={open} anchorEl={anchorEl}>
                                                <div className='dropdown-content'>
                                                    <div className="row">
                                                        <div className="column">
                                                            <h6>Category 1</h6>
                                                            <a href="#">Link 1</a>
                                                            <a href="#">Link 2</a>
                                                            <a href="#">Link 3</a>
                                                        </div>
                                                        <div className="column">
                                                            <h6>Category 2</h6>
                                                            <a href="#">Link 1</a>
                                                            <a href="#">Link 2</a>
                                                            <a href="#">Link 3</a>
                                                        </div>
                                                        <div className="column">
                                                            <h6>Category 3</h6>
                                                            <a href="#">Link 1</a>
                                                            <a href="#">Link 2</a>
                                                            <a href="#">Link 3</a>
                                                        </div>
                                                    </div>
                                                </div>
                                            </Popper>
                                        </div>
                                        <div>
                                            <button className="btn" style={{ background: "rgba(130, 130, 130, 0.05)", color: "#828282" }}>
                                                <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/filter.svg")} width="15px" height="15px" />&nbsp;Filter
                                            </button>
                                        </div>
                                    </div>
                                </div>
                                {/* ALL SUMMARY ROW */}
                                <div className='row ml-2'>
                                    <div className='col-xl-8 sales-data-chip' style={{ background: "#F9F9F9", borderRadius: "5px 0px 0px 5px" }}>
                                        <div className="d-flex">
                                            <div className="dark-gray-color">
                                                <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/location.svg")} width="35px" height="35px" />
                                            </div>
                                            <div className="ml-2">
                                                <span>
                                                    <span className="dark-gray-color"
                                                        style={{ fontWeight: "500" }}>{breadCrumParentProductCategoryTypeName == "" ? "All" : breadCrumParentProductCategoryTypeName}</span>
                                                    <p><strong>{breadCrumParentProductCategoryName == "" ? "All" : breadCrumParentProductCategoryName}</strong></p>
                                                </span>
                                            </div>
                                        </div>
                                    </div>

                                    <div className='col-xl-4 sales-data-chip' style={{ background: "#F9F9F9" }}>
                                        <button onClick={handleExport}
                                            className="btn float-right" style={{ background: "white", boxShadow: "0px 3px 5px rgba(0, 0, 0, 0.03)", color: "#828282" }}>
                                            <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/export.svg")} width="15px" height="15px" />
                                        </button>
                                    </div>
                                </div>
                                {/* TABLE ROW */}
                                <div className='mt-5'>
                                    <TradeDiscountList setSingleAll={setSingleAll} singleAll={singleAll} tradeDiscountList={tradeDiscountList}
                                    />
                                </div>
                            </div>
                        </div>
                    </CardBody>
                </Card>
            </div>
        </>
    );
}
