import React, { useEffect, useState } from "react"
import SVG from "react-inlinesvg";
import { CardBody, CardHeaderToolbar } from "../../../_metronic/_partials/controls";
import { Card } from "react-bootstrap"
import MastarConfigBreadCrum from "../mastar-config/MastarConfigBreadCrum"
import { toAbsoluteUrl } from "../../../_metronic/_helpers"
import { Route, useHistory } from "react-router-dom"
import BootstrapTable from "react-bootstrap-table-next"
import paginationFactory from "react-bootstrap-table2-paginator"
import { CommonDeleteModal } from "../Common/CommonDeleteModal"
import { ActionsColumnFormatter } from "./ActionsColumnFormatter"
import { showError, showSuccess } from "../../pages/Alert"
import * as XLSX from "xlsx";
import axios from "axios"


export default function BankBranchList() {
    let history = useHistory();
    const [bankBranchList, setBankBranchList] = useState([]);
    const [selectedRows, setSelectedRows] = useState([]);
    const [deleteId, setDeleteId] = useState(null);

    const data = [
        { "id": 1, "name": "Dhaka Bank", "bank": "Bank", "email": "dhaka@gmail.com" ,"address": "dhaka@gmail.com","contactNumber": "01999954398"},
        { "id": 2, "name": "Dhaka Bank", "bank": "Bank", "email": "dhaka@gmail.com" ,"address": "dhaka@gmail.com","contactNumber": "01999954398" },
        { "id": 3, "name": "Dhaka Bank", "bank": "Bank", "email": "dhaka@gmail.com" ,"address": "dhaka@gmail.com","contactNumber": "01999954398" }
    ];

    useEffect(() => {
        getAllBankBranch();
    }, [])


    const exportData = (e) => {
        handleExport();
    }

    const handleExport = () => {
        const data = [...selectedRows];
        if (data.length === 0) {
            showError("No row is selected for export data");
            return;
        }
        let exportData = [];
        data.map(row => {
            let obj = {};
            obj.name = row.name;
            obj.bankName= row.bank.name;
            obj.email=row.email;
            obj.address=row.address;
            obj.contactNumber = row.contactNumber;
            exportData.push(obj);
            setSelectedRows([]);
        })
        const workbook = XLSX.utils.book_new();
        const Heading = [
            ["Branch Name", "Bank","EMAIL","ADDRESS", "CONTACT NUMBER"]
        ];
        const worksheet = XLSX.utils.json_to_sheet(exportData, {origin: 'A2', skipHeader: true});

        // creating sheet and adding data from 2nd row of column A.
        // Leaving first row to add Heading
        XLSX.utils.sheet_add_aoa(worksheet, Heading, {origin: 'A1'});

        XLSX.utils.book_append_sheet(workbook, worksheet, "Sheet1");
        XLSX.writeFile(workbook, "BranchList.xlsx");
    }

    const openViewPage = (data) => {
        console.log(data);
        // history.push('/mastar-config/bank-setup-new', { state: data });
    }

    const openEditPage = (data) => {
        history.push({pathname: '/mastar-config/branch-setup-new', state: { data }});
    }

    const openDeleteDialog = (data) => {
        setDeleteId(data.id)
        history.push('/mastar-config/bank-branch/delete');
    }

    const getAllBankBranch = () => {
        const URL = `${process.env.REACT_APP_API_URL}/api/bank-branch`;
        axios.get(URL).then(response => {
            console.log(response.data.data);
            setBankBranchList(response.data.data)
        });
    }

    const singleRowSelectHandler = (data, isSelect) => {
        if (isSelect == true) {
            let temp = [...selectedRows]
            temp.push(data)
            setSelectedRows(temp)
        } else {
            if (selectedRows.length >= 0) {
                let temp = [...selectedRows]
                const index = temp.findIndex(obj => obj.id == data.id);
                temp.splice(index, 1);
                setSelectedRows(temp)
            }
        }
    }

    const allRowSelectHandler = (allData, isSelect) => {
        if (isSelect == true) {
            setSelectedRows(allData)
        } else {
            setSelectedRows([])
        }
    }

    const createNewBranch = () => {
        history.push('/mastar-config/branch-setup-new');
    }

    const deleteBankBranch = () => {
        // alert('delete' + deleteId);
        const URL = `${process.env.REACT_APP_API_URL}/api/bank-branch/${deleteId}`;
        axios.delete(URL).then(response => {
            if(response.data.success == true){
                showSuccess(response.data.message)
                const tempBankBranchList = [...bankBranchList]
                const pIndex = tempBankBranchList.findIndex(obj => obj.id == deleteId);
                tempBankBranchList.splice(pIndex,1);
                setBankBranchList(tempBankBranchList);
                setDeleteId(null);
            }
            else{
                showError(response.data.message);
            }
        }).catch(err => {
            showError(err);
        });

        history.push('/mastar-config/bank-branch');
    }

    const columns = [
        {
            dataField: "name",
            text: "Branch Name",
        },

        {
            dataField: "bank.name",
            text: "Bank"
        },
        {
            dataField: "email",
            text: "Email"
        },
        {
            dataField: "address",
            text: "Address"
        },
        {
            dataField: "contactNumber",
            text: "Contact Number"
        },
        {
            dataField: "isActive",
            text: "Status",
            formatter: (cellContent, row) => {
                return cellContent ? 'Active' : 'Inactive';
            }
        },
        {
            dataField: "action",
            text: "Actions",
            formatter: ActionsColumnFormatter,
            formatExtraData: {
                openViewPage: openViewPage,
                openEditPage: openEditPage,
                openDeleteDialog: openDeleteDialog
            },
            classes: "text-center",
            headerClasses: "text-center",
            style: {
                minWidth: "100px",
            }
        }
    ];

    return (
        // <>
        //     <h1>Bank Branch Setup</h1>

        // </>



        <>
            {/* BREAD CRUM ROW  */}
            <MastarConfigBreadCrum menuTitle="BankBranch" />

            <Card>
                <CardBody>
                    <div className="row">
                        <div className="col-lg-12">

                            <div className="row">
                                <div className="col-xl-4">
                                    <span className="create-field-title">Bank Branch List</span>
                                    {/* <p style={{ color: "#B6B6B6" }}>Total {productProfileList.length} products </p> */}
                                </div>
                                <div className="col-xl-8 d-flex justify-content-end">
                                    <div className="mr-5">
                                        <CardHeaderToolbar
                                            title="Create New Branch"
                                        >

                                            <button
                                                //   tree={categoryTypeTree} 
                                                type="button"
                                                className="btn btn-primary"
                                             onClick={createNewBranch}
                                            >
                                                + New Branch
                                            </button>
                                        </CardHeaderToolbar>
                                    </div>

                                    <div style={{ marginRight: "20px", background: "#F9F9F9" }}>
                                        <button title="Download excel data" className="btn float-right export-btn"
                                        onClick={exportData}
                                        >
                                            <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/download.svg")} width="15px" height="15px" />
                                        </button>
                                    </div>
                                </div>
                            </div>

                        </div>
                    </div>

                    <Route path="/mastar-config/bank-branch/delete">
                        {({ history, match }) => (
                            <CommonDeleteModal
                                show={match != null}
                                id={deleteId}
                                deleteAction={deleteBankBranch}
                                onHide={() => {
                                    history.push("/mastar-config/bank-branch");
                                }}
                            />
                        )}
                    </Route>

                    <BootstrapTable
                        wrapperClasses="table-responsive"
                        classes="table table-head-custom table-vertical-center overflow-hidden"
                        bootstrap4
                        bordered={false}
                        keyField="id"
                        data={bankBranchList}
                        columns={columns}
                        selectRow={
                            {
                                mode: 'checkbox',
                                onSelect: (row, isSelect, rowIndex, e) => {
                                    singleRowSelectHandler(row, isSelect);
                                },
                                onSelectAll: (isSelect, rows, e) => {
                                      allRowSelectHandler(rows, isSelect);
                                }

                            }
                        }

                        pagination={paginationFactory({ sizePerPage: 10, showTotal: true })}
                    />
                </CardBody>
            </Card>
        </>
    )

}
