                        </div>

                        {/* TABLE ROW */}
                        <div className='mt-5'>
                            <StoreMovementList setSingleAll={setSingleAll} singleAll={singleAll} interStoreMovementDetails={interStoreMovementDetails}/>
                        </div>
                    </CardBody>
                </Card>
            </div>
        </>
    );
