import React from "react";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
const data = [
  {
    "id": 1,
    "time": "10:23 AM",
    "ref": "FT220650K5HG\MTJ",
    "date": "10 APR 2022 10:23 AM",
    "description":"Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer nec odio.",
    "debit": "570,168,000",
    "credit": "",
    "balance": "570,168,000"
  },
  {
    "id": 2,
    "time": "10:23 AM",
    "ref": "FT220650K5HG\MTJ",
    "date": "10 APR 2022 10:23 AM",
    "description":"Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer nec odio.",
    "debit": "",
    "credit": "570,168,000",
    "balance": "570,168,000"
  },
  {
    "id": 3,
    "time": "10:23 AM",
    "ref": "FT220650K5HG\MTJ",
    "date": "10 APR 2022 10:23 AM",
    "description":"Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer nec odio.",
    "debit": "",
    "credit": "570,168,000",
    "balance": "570,168,000"
  },
]
export default function DistributorLedgerList({setSingleAll, singleAll}) {
  const singleWiseSelectHandler = (data, isSelect) => {
    if (isSelect == true) {
      let temp = [...singleAll]
      temp.push(data)
      setSingleAll(temp)
    } else {
      if (singleAll.length >= 0) {
        let temp = [...singleAll]
        const index = temp.findIndex(obj => obj.id == data.id);
        temp.splice(index, 1);
        setSingleAll(temp)
      }
    }

  }
  const allSelectHandler = (allData, isSelect) => {
    if (isSelect == true) {
      setSingleAll(allData)
    }
    else {
      if (allData.length >= 0) {
        for (let i = 0; i < allData.length; i++) {
          const index = singleAll.findIndex(obj => obj.id == allData[i].id);
          singleAll.splice(index, 1);
          setSingleAll(singleAll)
        }
      }
    }

  }
  const columns = [
    {
      dataField: "date",
      text:"DATE",
      formatter: (cellContent, row) => (
        <>
           <span><strong>{row.date}</strong></span><br />
           <span className="text-muted">{row.time}</span>
        </>
      )
    },
    {
        dataField: "ref",
        text:"REF.",
        formatter: (cellContent, row) => (
          <>
             <span className="dark-gray-color"><strong>{row.ref}</strong></span>
          </>
        )
    },
    {
        dataField: "description",
        text:"DESCRIPTION",
        style:{
            width:"300px"
        },
        formatter: (cellContent, row) => (
          <>
             <span className="dark-gray-color">{row.description}</span>
          </>
        )
    },
    {
        dataField: "debit",
        text:"DEBIT",
        formatter: (cellContent, row) => (
          <>
             <span className="text-danger"><strong>{row.debit}</strong></span>
          </>
        )
    },
    {
        dataField: "credit",
        text:"CREDIT",
        formatter: (cellContent, row) => (
          <>
             <span className="dark-success-color"><strong>{row.credit}</strong></span>
          </>
        )
    },
    {
      dataField: "balance",
      text: "BALANCE",
      formatter: (cellContent, row) => (
        <>
          <strong>{row.balance}</strong><br />
        </>
      )
    },
  ];

  return (
    <>
      <BootstrapTable
        wrapperClasses="table-responsive"
        classes="table table-head-custom table-vertical-center overflow-hidden"
        bootstrap4
        bordered={false}
        keyField="id"
        data={data}
        columns={columns}
        selectRow={
          {
            mode: 'checkbox',
            onSelect: (row, isSelect, rowIndex, e) => {
              singleWiseSelectHandler(row, isSelect);
            },
            onSelectAll: (isSelect, rows, e) => {
              allSelectHandler(rows, isSelect);

            }

          }
        }

        pagination={paginationFactory({ sizePerPage: 10, showTotal: true })}
      />
    </>
  );
}
