import React from "react";
import { useHistory } from "react-router-dom";
import {
    Card,
    CardBody,
} from "../../../../../_metronic/_partials/controls";
export default function CollectionTodaySales() {
    const history = useHistory();
    const handleOverviewChange = () => {
        history.push("/salescollection/payment-collection/overview")
    }
    const handleCollectionDataChange = () => {
        history.push("/salescollection/payment-collection/collection-data")
    }
    const handleOrdChange = () => {
        history.push("/salescollection/payment-collection/overview")
    }
    const handleOrdCalculatorChange = () => {
        history.push("/salescollection/payment-collection/overview")
    }
    const handleCollectionBudgetChange = () =>{
        history.push("/salescollection/payment-collection/overview")
    }

    return (
        <>
            {/* TODAY SALE ROW */}
            <div className="mt-3">
                <Card>
                    <CardBody style={{ marginBottom: "-36px" }}> 
                        <div>
                            <p className="create-field-title">Today’s Payment Collection</p>
                        </div>
                        <div className="mt-5 ml-2 row">
                            <span className="sales-chip mt-5 mr-5"><i class="bi bi-arrow-up-short text-primary"></i>&nbsp;428,030&nbsp;<span className='text-muted'>Collection</span></span>
                            <span className="sales-chip mt-5 mr-5"><i class="bi bi-arrow-down-short text-danger"></i>&nbsp;428,030&nbsp;<span className='text-muted'>ORD</span></span>
                            <span className="sales-chip mt-5 mr-5"><i class="bi bi-arrow-up-short text-primary"></i>&nbsp;99,567,428,030&nbsp;<span className='text-muted'>Receivable</span></span>
                        </div>
                        <div className="mt-5">
                            <ul class="nav nav-pills mb-3" id="pills-tab" role="tablist">
                                <li class="nav-item" role="presentation">
                                    <a class="nav-link" id="pills-payment-collection-overview-tab" data-toggle="pill" href="#pills-payment-collection-overview" role="tab" aria-controls="pills-payment-collection-overview" aria-selected="false" onClick={handleOverviewChange}>Overview</a>
                                </li>
                                <li class="nav-item" role="presentation">
                                    <a class="nav-link" id="pills-payment-collection-data-tab" data-toggle="pill" href="#pills-payment-collection-data" role="tab" aria-controls="pills-payment-collection-data" aria-selected="false" onClick={handleCollectionDataChange}>Collection Data</a>
                                </li>
                                <li class="nav-item" role="presentation">
                                    <a class="nav-link" id="pills-payment-ord-tab" data-toggle="pill" href="#pills-payment-ord" role="tab" aria-controls="pills-payment-ord" aria-selected="false" onClick={handleOrdChange}>ORD</a>
                                </li>
                                <li class="nav-item" role="presentation">
                                    <a class="nav-link" id="pills-payment-ord-calculator-tab" data-toggle="pill" href="#pills-payment-ord-calculator" role="tab" aria-controls="pills-payment-ord-calculator" aria-selected="false" onClick={handleOrdCalculatorChange}>ORD Calculator</a>
                                </li>
                                <li class="nav-item" role="presentation">
                                    <a class="nav-link" id="pills-payment-collection-budget-tab" data-toggle="pill" href="#pills-payment-collection-budget" role="tab" aria-controls="pills-payment-collection-budget" aria-selected="false" onClick={handleCollectionBudgetChange}>Collection Budget</a>
                                </li>
                                <li class="nav-item" role="presentation">
                                    <a class="nav-link" id="pills-trade-price-tab" data-toggle="pill" href="#pills-trade-price" role="tab" aria-controls="pills-trade-price" aria-selected="false">Report</a>
                                </li>
                            </ul>
                        </div>
                    </CardBody>
                </Card>
            </div>
        </>
    );
}
