import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../../_metronic/_helpers";
import {
    Card,
    CardBody,
} from "../../../../../_metronic/_partials/controls";
import { useIntl } from "react-intl";
import Chart from "react-apexcharts";

export default function InventoryStockHeader() {
    const intl = useIntl();
    const history = useHistory();
    const [totals, setTotals] = useState([60, 20, 15, 5]);

    const handleStockDataChange = () => {
        history.push("/inventory/stock/stock-list")
    }
    const handleSalesOrderChange = () => {
        history.push("/inventory/stock/sales-order/sales-booking-list")
    }
    const handleSalesReturnChange = () => {
        history.push("/inventory/stock/sales-return/sales-return-list")
    }
    const state = {
        options: {
            labels: ["Regular", "In Transit", "Quarantine", "Restricted"],
            chart: {
                show: false,
                type: "donut",
            },
            colors: ['#6FCF97', '#56CCF2', '#F2C94C', '#EB5757'],
            plotOptions: {
                pie: {
                    donut: {
                        labels: {
                            show: true,
                            total: {
                                showAlways: true,
                                fontSize: "15px",
                                label: "Total",
                                color: '#BDBDBD',
                                show: true
                            }
                        }
                    }
                }
            },
            dataLabels: {
                enabled: false,
            },
            legend: {
                labels: { useSeriesColors: true },
                fontSize: '12px',
                fontFamily: 'Helvetica, Arial',
                fontWeight: 400
            },
            responsive: [
                {
                    breakpoint: 480,
                    options: {
                        chart: {
                            width: 200
                        },
                    }
                }
            ]
        }
    };
    return (
        <>
            {/* TODAY SALE ROW */}
            <div className="mt-3">
                <Card>
                    <CardBody style={{ marginBottom: "-36px" }}>
                        <div className="d-flex">
                            <div>
                                <div className="mixed-chart">
                                    <Chart
                                        options={state.options}
                                        series={totals}
                                        type="donut"
                                        width="300"
                                    />
                                </div>
                            </div>
                            <div className="row w-100">
                                <div className="col-xl-10">
                                    <span className="create-field-title display-inline-block">Chittagong Depot
                                        <span
                                            className="card-div-icon dark-gray-color edit-icon"
                                            style={{ marginLeft: "10px" }}
                                            data-toggle="tooltip" data-placement="bottom" title="Edit"
                                        >
                                            <SVG
                                                className="pen-edit-icon"
                                                src={toAbsoluteUrl("/media/svg/icons/project-svg/eva_edit.svg")} data-toggle="tooltip" data-placement="bottom" title="Edit" />
                                        </span>
                                    </span><br />
                                    <span className="display-inline-block mt-5">
                                        <span className="text-muted">
                                            <SVG
                                                className="pen-edit-icon"
                                                src={toAbsoluteUrl("/media/svg/icons/project-svg/location.svg")} />
                                            &nbsp;Area
                                        </span>&nbsp;
                                        <strong className="dark-gray-color">
                                            Chittagong, Cumilla
                                        </strong>&nbsp;&nbsp;&nbsp;
                                    </span>
                                </div>
                                <div className="col-xl-2 font-12">
                                    <span className="display-inline-block stock-approval-btn mt-5">
                                        <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/white-approved.svg")} width="15px" height="15px" />&nbsp;
                                        Stock Approvals&nbsp;&nbsp;&nbsp;
                                        {/* <span className="bg-danger pl-1 pr-1 text-white rounded">
                                            <small>99+</small>
                                        </span> */}
                                    </span>
                                </div>
                            </div>
                        </div>
                        <div className="mt-5">
                            <ul class="nav nav-pills mb-3" id="pills-tab" role="tablist">
                                <li class="nav-item" role="presentation">
                                    <a class="nav-link" id="pills-inventory-stock-overview-tab" data-toggle="pill" href="#pills-inventory-stock-overview" role="tab" aria-controls="pills-inventory-stock-overview" aria-selected="false">Overview</a>
                                </li>
                                <li class="nav-item" role="presentation">
                                    <a class="nav-link" id="pills-inventory-stock-stock-data-tab" data-toggle="pill" href="#pills-inventory-stock-stock-data" role="tab" aria-controls="pills-inventory-stock-stock-data" aria-selected="false" onClick={handleStockDataChange}>Stock Data</a>
                                </li>
                                <li className="nav-item" role="presentation">
                                    <a className="nav-link" id="pills-inventory-stock-sales-order-tab" data-toggle="pill" href="#pills-inventory-stock-sales-order" role="tab" aria-controls="pills-inventory-stock-sales-order" aria-selected="false" onClick={handleSalesOrderChange}>Sales Order&nbsp;
                                        {/* <span className="bg-danger pl-1 pr-1 text-white rounded">
                                            <small>99+</small>
                                        </span> */}
                                    </a>
                                </li>
                                <li className="nav-item" role="presentation">
                                    <a className="nav-link" id="pills-inventory-stock-sales-return-tab" data-toggle="pill" href="#pills-inventory-stock-sales-return" role="tab" aria-controls="pills-inventory-stock-sales-return" aria-selected="false" onClick={handleSalesReturnChange}>Sales Return&nbsp;
                                        {/* <span className="bg-danger pl-1 pr-1 text-white rounded">
                                            <small>99+</small>
                                        </span> */}
                                    </a>
                                </li>
                                <li className="nav-item" role="presentation">
                                    <a className="nav-link" id="pills-inventory-stock-stock-transfer-tab" data-toggle="pill" href="#pills-inventory-stock-stock-transfer" role="tab" aria-controls="pills-inventory-stock-stock-transfer" aria-selected="false">Stock Transfer&nbsp;
                                        {/* <span className="bg-danger pl-1 pr-1 text-white rounded">
                                            <small>99+</small>
                                        </span> */}
                                    </a>
                                </li>
                                <li className="nav-item" role="presentation">
                                    <a className="nav-link" id="pills-inventory-stock-store-movement-tab" data-toggle="pill" href="#pills-inventory-stock-store-movement" role="tab" aria-controls="pills-inventory-stock-store-movement" aria-selected="false">Store Movement&nbsp;
                                        {/* <span className="bg-danger pl-1 pr-1 text-white rounded">
                                            <small>99+</small>
                                        </span> */}
                                    </a>
                                </li>
                                <li className="nav-item" role="presentation">
                                    <a className="nav-link" id="pills-inventory-stock-report-tab" data-toggle="pill" href="#pills-inventory-stock-report" role="tab" aria-controls="pills-inventory-stock-report" aria-selected="false">Report</a>
                                </li>
                            </ul>
                        </div>
                    </CardBody>
                </Card>
            </div>
        </>
    );
}
