import React from "react";
import { useHistory } from "react-router-dom";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import { ActionsColumnFormatter } from "./ActionsColumnFormatter";
import {useIntl} from "react-intl";
const data = [
  {
    "id": 1,
    "code": "12535689",
    "depot":"Production to Central Wirehose",
    "dateTime": "10 APR 2022 10:23 AM",
    "user": "Mmh Shohagh",
    "designation": "Depot Incharge, Dhaka Depot",
    "qty": "55,570",
    "balance": "2,555,570",
    "status": "PENDING",
  } 
]
export default function ProductionReceiveList({setSingleAll, singleAll}) {
  const intl = useIntl();
  let history = useHistory();

  const openReceivePage = (data) => {
    //history.push('/proposal-received', { state: data.productList });
  }


  const singleWiseSelectHandler = (data, isSelect) => {
    if (isSelect == true) {
      let temp = [...singleAll]
      temp.push(data)
      setSingleAll(temp)
    } else {
      if (singleAll.length >= 0) {
        let temp = [...singleAll]
        const index = temp.findIndex(obj => obj.id == data.id);
        temp.splice(index, 1);
        setSingleAll(temp)
      }
    }

  }
  const allSelectHandler = (allData, isSelect) => {
    if (isSelect == true) {
      setSingleAll(allData)
    }
    else {
      if (allData.length >= 0) {
        for (let i = 0; i < allData.length; i++) {
          const index = singleAll.findIndex(obj => obj.id == allData[i].id);
          singleAll.splice(index, 1);
          setSingleAll(singleAll)
        }
      }
    }

  }
  const columns = [
    {
      dataField: "sales_officer",
      text: "BATCH INFO",
      formatter: (cellContent, row) => (
        <>
          <strong>000113-000005-24-05-2021</strong><br />
          <span className="text-muted">QTY: 500</span>
        </>
      )
    },
    {
      dataField: "code",
      text:"PRODUCT INFO",
      formatter: (cellContent, row) => (
        <>
          <span className="text-muted">SKU X1253689</span><br />
          <strong>Seaconazole&nbsp;5SC 50 ml&nbsp;*&nbsp;40</strong><br />
          <span className="text-muted">Fungicide/</span>
        </>
      )
    },
    {
      dataField: "collection_amount",
      text: "QTY.",
      headerClasses:"text-right",
      classes:"text-right",
      formatter: (cellContent, row) => (
        <>
          <strong>{row.qty}</strong><br />
          <span className="text-muted">{row.balance+"(MC.)"}</span>
        </>
      )
    },
    {
      dataField: "action",
      text: intl.formatMessage({id: "MENU.ACTION"}),
      formatter: ActionsColumnFormatter,
      formatExtraData: {
        openReceivePage: openReceivePage,
      },
      classes: "text-center",
      headerClasses: "text-center",
      style: {
        minWidth: "100px",
      },
    },
  ];

  return (
    <>
      <BootstrapTable
        wrapperClasses="table-responsive"
        classes="table table-head-custom table-vertical-center overflow-hidden"
        bootstrap4
        bordered={false}
        keyField="id"
        data={data}
        columns={columns}
        selectRow={
          {
            mode: 'checkbox',
            onSelect: (row, isSelect, rowIndex, e) => {
              singleWiseSelectHandler(row, isSelect);
            },
            onSelectAll: (isSelect, rows, e) => {
              allSelectHandler(rows, isSelect);

            }

          }
        }

        pagination={paginationFactory({ sizePerPage: 10, showTotal: true })}
      />
    </>
  );
}
