import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import { ActionsColumnFormatter } from "./ActionsColumnFormatter";
import { useIntl } from "react-intl";
import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../../../../../../_metronic/_helpers";
import { shallowEqual, useSelector } from "react-redux";
import axios from "axios";

const dataOld = [
    {
        "id": 1,
        "code": "12535689",
        "duration": "12 Days Ago",
        "date": "10 APR 2022 10:23 AM",
        "distributor": "Bhai Bhai Enterprize",
        "balance": "570,168,000",
        "user": "Mmh Shohagh",
        "designation": "Sales Officer, Cox’s Bazar",
        "payments": "500,000,000",
        "collectionType": "Cheque",
        "status": "Verify",
        "nature": "Advance(14)",
        "overdue": "Overdue(12)"
    },
    {
        "id": 2,
        "code": "12535689",
        "duration": "12 Days Ago",
        "date": "10 APR 2022 10:23 AM",
        "distributor": "Bhai Bhai Enterprize",
        "balance": "570,168,000",
        "user": "Mmh Shohagh",
        "designation": "Sales Officer, Cox’s Bazar",
        "payments": "0",
        "collectionType": "Cash",
        "status": "Verified",
        "nature": "Advance(14)",
        "overdue": "Overdue(12)"
    },
    {
        "id": 3,
        "code": "12535689",
        "duration": "12 Days Ago",
        "date": "10 APR 2022 10:23 AM",
        "distributor": "Bhai Bhai Enterprize",
        "balance": "570,168,000",
        "user": "Mmh Shohagh",
        "designation": "Sales Officer, Cox’s Bazar",
        "payments": "500,000,000",
        "collectionType": "Online",
        "status": "Rejected",
        "nature": "Advance(14)",
        "overdue": "Overdue(12)"
    },
]

export default function InvoicesMapList({ setSingleAll, singleAll, setTotalAdjustableAmount }) {
    const intl = useIntl();
    let history = useHistory();

    const companyId = useSelector((state) => state.auth.company, shallowEqual);
    const [data, setData] = useState([]);    

    useEffect(() => {        
        getDataList();
    }, []);

    useEffect(() => {        
        getDataList();
    }, [companyId]);

    const openAdjustPage = (data) => {
        history.push('/salescollection/payment-adjustment/payment-adjustment/invoices-adjust-view', { state: data });
    }

    const singleWiseSelectHandler = (data, isSelect) => {
        if (isSelect == true) {
            let temp = [...singleAll]
            temp.push(data)
            setSingleAll(temp)
        } else {
            if (singleAll.length >= 0) {
                let temp = [...singleAll]
                const index = temp.findIndex(obj => obj.id == data.id);
                temp.splice(index, 1);
                setSingleAll(temp)
            }
        }

    }
    const allSelectHandler = (allData, isSelect) => {
        if (isSelect == true) {
            setSingleAll(allData)
        }
        else {
            if (allData.length >= 0) {
                for (let i = 0; i < allData.length; i++) {
                    const index = singleAll.findIndex(obj => obj.id == allData[i].id);
                    singleAll.splice(index, 1);
                    setSingleAll(singleAll)
                }
            }
        }

    }

    const getDataList = () => {
        let URL = `${process.env.REACT_APP_API_URL}/api/payment-collection-adjustment/distributor-wise-payment-collection-info-list`;
        URL += '?companyId=' + companyId 
        // params.paymentNature && (URL += '&paymentNature=' + params.paymentNature)  
        // params.paymentType && (URL += '&paymentType=' + params.paymentType)  
        // params.location && (URL += '&location=' + params.location)  
        // params.fiscalYear && (URL += '&fiscalYear=' + params.fiscalYear)  
        // status && (URL += '&status=' + status)
        // URL += '&fromDate=' + params.fromDate  
        // URL += '&toDate=' + params.toDate  
        axios.get(URL).then(response => {
            setData(response.data.data);

            console.log(response.data.data);

            const sum = response.data.data.reduce((accumulator, object) => {
                return accumulator + object.adjustable_amount;
            }, 0);

            setTotalAdjustableAmount(sum);
        });
    }

    const columns = [
        {
            dataField: "distributor",
            text: intl.formatMessage({ id: "PAYMENT.COLLECTION.ORD_DISTRIBUTOR" }),
            formatter: (cellContent, row) => (
                <>
                    <div className="d-flex" style={{ marginBottom: "-10px" }}>
                        <div className="mt-2">
                            <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/Lays.svg")} width="30px" height="30px" />
                        </div>
                        <div className="ml-3 mt-2">
                            <span>
                                <span style={{ fontWeight: "500" }}><strong>{row.distributor_name}</strong></span>
                                <p className="dark-gray-color">
                                    <span className="text-muted">
                                        <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/CreditLimit.svg")} width="15px" height="15px" />&nbsp;
                                        Balance</span>&nbsp;{row.ledger_balance}
                                </p>
                            </span>
                        </div>
                    </div>
                </>
            )
        },
        {
            dataField: "balance",
            text: intl.formatMessage({ id: "COMMON.INVOICES_CAPITAL" }),
            formatter: (cellContent, row) => (
                <>
                    <strong>{row.invoice_amount}</strong><br />
                    <span className="text-muted">Overdue({row.overdu_count})</span>
                </>
            )
        },
        {
            dataField: "nature",
            text: "PAYMENTS",
            formatter: (cellContent, row) => (
                <>
                    <strong>{row.collection_amount}</strong><br />
                    <span className="text-muted">Advance({row.advance_count})</span>
                </>
            )
        },
        {
            dataField: "collection_amount",
            text: "ADJUSTABLE AMOUNT",
            formatter: (cellContent, row) => (
                <>                    
                    <strong className="text-muted">{row.adjustable_amount}</strong><br />
                    <div className="progress mt-2" style={{height:"5px"}}>
                        <div className="progress-bar bg-primary" role="progressbar" 
                        style={{width: (row.adjustable_amount/row.collection_amount)*100 + "%"}} 
                        aria-valuenow={(row.adjustable_amount/row.collection_amount)*100} aria-valuemin="0" aria-valuemax="100"></div>
                    </div>
                </>
            )
        },
        {
            dataField: "action",
            text: intl.formatMessage({ id: "MENU.ACTION" }),
            formatter: ActionsColumnFormatter,
            formatExtraData: {
                openAdjustPage: openAdjustPage,
            },
            classes: "text-center",
            headerClasses: "text-center",
            style: {
                minWidth: "100px",
            },
        },
    ];

    return (
        <>
            <BootstrapTable
                wrapperClasses="table-responsive"
                classes="table table-head-custom table-vertical-center overflow-hidden"
                bootstrap4
                bordered={false}
                keyField="id"
                data={data}
                columns={columns}
                selectRow={
                    {
                        mode: 'checkbox',
                        onSelect: (row, isSelect, rowIndex, e) => {
                            singleWiseSelectHandler(row, isSelect);
                        },
                        onSelectAll: (isSelect, rows, e) => {
                            allSelectHandler(rows, isSelect);

                        }

                    }
                }

                pagination={paginationFactory({ sizePerPage: 10, showTotal: true })}
            />
        </>
    );
}
