import React, { useState, useEffect } from 'react';
import Popper from '@material-ui/core/Popper';
import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../../../_metronic/_helpers";
import { TradeDiscountList } from '../table/TradeDiscountList';
import { Card, CardBody } from "../../../../../../_metronic/_partials/controls";
import { useHistory } from 'react-router-dom';
import { showError } from '../../../../../pages/Alert';
import axios from 'axios';
import ProductCategoryTreeView from '../../../CommonComponents/ProductCategoryTreeView';
import { shallowEqual, useSelector } from "react-redux";
import SalesTabsHeader from "../../common/SalesTabsHeader";
import { BreadCrum } from "../../common/BreadCrum";

export function TradeDiscount() {
    const selectedCompany = useSelector((state) => state.auth.company, shallowEqual);
    const [producCategoryTree, setLocationProductCategory] = useState([]);
    const [selectedProductCategory, setSelectedProductCategory] = useState({});
    const [sessionData, setSessionData] = useState({ userLoginId: 1, companyId: selectedCompany, accountingYearId: 1 });
    const [searchParams, setSearchParams] = useState(sessionData);
    let [singleAll, setSingleAll] = useState([]);
    const [anchorEl, setAnchorEl] = useState(null);
    const open = Boolean(anchorEl);
    const id = open ? 'simple-popper' : undefined;

    useEffect(() => {
        document.getElementById('pills-trade-discount-tab').classList.add('active')
        getProductCategoryTreeList(sessionData);
    }, []);

    const handleClick = (event) => {
        setAnchorEl(anchorEl ? null : event.currentTarget);
    };

    const handleExport = () => {
        const exportData = [...singleAll]
    }
    const getProductCategoryTreeList = (params) => {
        const URL = `${process.env.REACT_APP_API_URL}/api/product-category/list-info/${params.companyId}`;
        axios.get(URL).then(response => {
            if (response.data.data.childProductCategoryDtoList != "") {
                setLocationProductCategory(response.data.data.childProductCategoryDtoList);
            }
        }).catch(err => {
            showError("Can not get product category tree data.");
        });
    }

    const selectTreeNode = (node) => {
        setSelectedProductCategory(node);
    }

    return (
        <>
            <div>
                {/* BREAD CRUM ROW */}
                <BreadCrum />
                {/* TODAY SALE ROW */}
                <SalesTabsHeader />
            </div>
            <div>
                <Card>
                    <CardBody>
                        <div className='row'>
                            {/* LEFT SIDE TREE ROW */}
                            <div className='col-xl-3' style={{ borderRight: "1px solid #F2F2F2" }}>
                                <div style={{ borderBottom: "1px solid #F2F2F2" }}>
                                    <label>
                                        <img src={toAbsoluteUrl("/images/loc3.png")}
                                            style={{ width: "20px", height: "20px", textAlign: "center" }}
                                            alt='Company Picture' />
                                        <strong style={{ marginLeft: "10px", color: "#828282" }}>Products (All)</strong>
                                    </label>
                                </div>
                                {/* TREE */}
                                <ProductCategoryTreeView tree={producCategoryTree} selectTreeNode={selectTreeNode}
                                />
                            </div>
                            {/* RIGHT SIDE LIST ROW */}
                            <div className='col-xl-9'>
                                {/* SEARCHING AND FILTERING ROW */}
                                <div className="row">
                                    <div className="col-xl-3">
                                        <div style={{ position: "absolute", padding: "7px", marginTop: "3px" }}>
                                            <img src={toAbsoluteUrl("/images/search.png")} width="20px" height="20px" />
                                        </div>
                                        <form className="form form-label-right">
                                            <input type="text" className="form-control" name="searchText"
                                                placeholder="Search Here"
                                                style={{ paddingLeft: "28px" }}
                                            />
                                        </form>
                                    </div>
                                    <div className="col-xl-9 d-flex justify-content-end">
                                        <div>
                                            <button className='btn' aria-describedby={id} onClick={handleClick}
                                                style={{ color: "#828282" }}>
                                                Fiscal Year 2022-23&nbsp;
                                                <i className="bi bi-chevron-down"></i>
                                            </button>
                                            <Popper id={id} open={open} anchorEl={anchorEl}>
                                                <div className='dropdown-content'>
                                                    <div className="row">
                                                        <div className="column">
                                                            <h6>Category 1</h6>
                                                            <a href="#">Link 1</a>
                                                            <a href="#">Link 2</a>
                                                            <a href="#">Link 3</a>
                                                        </div>
                                                        <div className="column">
                                                            <h6>Category 2</h6>
                                                            <a href="#">Link 1</a>
                                                            <a href="#">Link 2</a>
                                                            <a href="#">Link 3</a>
                                                        </div>
                                                        <div className="column">
                                                            <h6>Category 3</h6>
                                                            <a href="#">Link 1</a>
                                                            <a href="#">Link 2</a>
                                                            <a href="#">Link 3</a>
                                                        </div>
                                                    </div>
                                                </div>
                                            </Popper>
                                        </div>
                                        <div>
                                            <button className="btn" style={{ background: "rgba(130, 130, 130, 0.05)", color: "#828282" }}>
                                                <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/filter.svg")} width="15px" height="15px" />&nbsp;Filter
                                            </button>
                                        </div>
                                    </div>
                                </div>
                                {/* ALL SUMMARY ROW */}
                                <div className='row ml-2'>
                                    <div className='col-xl-8 sales-data-chip' style={{ background: "#F9F9F9", borderRadius: "5px 0px 0px 5px" }}>
                                        <div className="d-flex">
                                            <div className="dark-gray-color">
                                                <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/location.svg")} width="35px" height="35px" />
                                            </div>
                                            <div className="ml-2">
                                                <span>
                                                    <span className="dark-gray-color"
                                                          style={{fontWeight: "500"}}>{selectedProductCategory.typeName ? selectedProductCategory.typeName : "All"}</span>
                                                    <p><strong>{selectedProductCategory.name ? selectedProductCategory.name : "All"}</strong></p>
                                                </span>
                                            </div>
                                        </div>
                                    </div>

                                    <div className='col-xl-4 sales-data-chip' style={{ background: "#F9F9F9" }}>
                                        <button onClick={handleExport}
                                            className="btn float-right" style={{ background: "white", boxShadow: "0px 3px 5px rgba(0, 0, 0, 0.03)", color: "#828282" }}>
                                            <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/export.svg")} width="15px" height="15px" />
                                        </button>
                                    </div>
                                </div>
                                {/* TABLE ROW */}
                                <div className='mt-5'>
                                    <TradeDiscountList setSingleAll={setSingleAll} singleAll={singleAll}
                                    />
                                </div>
                            </div>
                        </div>
                    </CardBody>
                </Card>
            </div>
        </>
    );
}
