import React, { useState, useEffect } from 'react';
import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../../../../../_metronic/_helpers";
import { useHistory, useLocation } from "react-router-dom";
import {
    Card,
    CardBody,
} from "../../../../../../../../_metronic/_partials/controls";
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import clsx from 'clsx';
import ProposalSummaryInfo from "./ProposalSummaryInfo";
import CreditDebitNoteTabs from '../../../../common/CreditDebitNoteTabs';
import PaymentAdjustmentBreadCrum from '../../../../common/PaymentAdjustmentBreadCrum';
import PaymentAdjustmentHeader from "../../../../common/PaymentAdjustmentHeader";
import ProposalSummaryReason from "./ProposalSummaryReason";
import ProposalActivities from "./ProposalActivities";
import ProposalReports from "./ProposalReports";

const useStyles = makeStyles(() => ({
    root: {
        backgroundColor: 'white',
        boxShadow: "0px 3px 5px rgba(0, 0, 0, 0.03)",
        padding: 8,
        borderRadius: '50%'
    },
}));
export default function ApprovalViewPage() {
    const history = useHistory();
    const location = useLocation();
    const [activeStep, setActiveStep] = React.useState(0);
    const [creditNoteProposalApproval, setCreditNoteProposalApproval] = React.useState("Done");
    useEffect(() => {
        document.getElementById('pills-payment-adjustment-credit-debit-note-tab').classList.add('active');
        document.getElementById('pills-payment-adjustment-credit-debit-note-approval-tab').classList.add('active');
    }, []);


    const CustomStepIcon = (props) => {
        const classes = useStyles();
        const stepIcons = {
            1: <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/ok.svg")} width="15px" height="15px" />,

            2: creditNoteProposalApproval == "In Progress" ? 
                <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/status.svg")}  width="15px" height="15px" /> :
                    creditNoteProposalApproval == "Reject" ? 
                        <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/reject.svg")}  width="15px" height="15px" /> :
                            <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/ok.svg")} width="15px" height="15px" />,

            3: creditNoteProposalApproval == "Done" ?
                <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/ok.svg")} width="15px" height="15px" />:
                    <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/gray-doller.svg")} width="15px" height="15px" />,      
        };

        return (
            <div className={clsx(classes.root)}>
                {stepIcons[String(props.icon)]}
            </div>
        );
    };
    const creditNoteProposalDiv = <div className='text-center' style={{ fontSize: "0.83rem" }}>
        <span style={{ color: "#828282" }}>Credit Note Proposal</span><br />
        <span style={{ color: "#828282" }}>Applied On</span>&nbsp;<strong>15 April 2022 10:23AM</strong><br />
        <span style={{ color: "#828282" }}>Reason</span>&nbsp;<strong>Product Damaged</strong>
    </div>

    const creditNoteProposalApprovalDiv = <div className='text-center' style={{ fontSize: "0.83rem" }}>
        <span style={{ color: "#828282" }}>Credit Note Proposal Approval</span><br />
        <strong><span style={{ color: "#828282" }}>Status</span>&nbsp;
        <span 
        className={
            creditNoteProposalApproval == "In progress" ? 'text-success' : 
            creditNoteProposalApproval == "Reject"? "text-danger" :
            creditNoteProposalApproval == "Done"? "dark-success-color" :
            "dark-gray-color"}
        >{creditNoteProposalApproval}</span>
        </strong>
    </div>

    const balanceAdjustmentDiv = <div className='text-center' style={{ fontSize: "0.83rem" }}>
        <span style={{ color: "#828282" }}>Balance Adjustment</span><br />
        <span style={{ color: "#828282" }}><strong>Ledger Adjustment</strong> </span>&nbsp;
        <strong className={
            creditNoteProposalApproval == "Done"? "dark-success-color" :
            "dark-gray-color"
        }>{
        creditNoteProposalApproval == "Done" ? "Done" :
        creditNoteProposalApproval == "Reject" ? "" :
        "Pending"
        }</strong>
    </div>
    const getSteps = () => {
        return [creditNoteProposalDiv, creditNoteProposalApprovalDiv, balanceAdjustmentDiv];
    }
    const steps = getSteps();
    const handleBackApprovalListPageChange = () => {
        history.push("/salescollection/payment-adjustment/credit-debit-note/approval-list")
    }

    return (
        <>
            <div>
                {/* BREAD CRUM ROW */}
                <PaymentAdjustmentBreadCrum />
                {/* TODAY  ROW */}
                <PaymentAdjustmentHeader />
            </div>
            <div>
                <CreditDebitNoteTabs />
            </div>
            {/* CONTENT ROW */}
            <div>
                <Card>
                    <CardBody>
                        {/* HEADER ROW */}
                        <div className='row'>
                            <div className='col-xl-8 row'>
                                <span className=" mr-5">
                                    <button className='btn'
                                        onClick={handleBackApprovalListPageChange}>
                                        <strong>
                                            <i class="bi bi-arrow-left-short sales-booking-view-icon"></i></strong>
                                    </button>
                                </span>
                                <span className="sales-booking-view-span mr-5">
                                    <span className="dark-gray-color"
                                        style={{ fontWeight: "500" }}>Credit Note Proposal No.</span>
                                    <p><strong>13213141</strong></p>
                                </span>
                                <span className="sales-booking-view-span  mr-5">
                                    <span className="dark-gray-color"
                                        style={{ fontWeight: "500" }}>Depot</span>
                                    <p><strong>Chittagong</strong></p>
                                </span>

                                <span className="sales-booking-view-span mr-5">
                                    <span className="dark-gray-color"
                                        style={{ fontWeight: "500" }}>Territory</span>
                                    <p><strong>Cox’s Bazar</strong></p>
                                </span>
                            </div>
                            <div className='col-xl-4 d-flex justify-content-end'>
                                <div className="d-flex  mr-5">
                                    <div>
                                        {/* {distributorLedger.distributorLogo} */}
                                        <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/Lays.svg")} width="35px" height="35px" />
                                    </div>
                                    <div className="ml-3">
                                        <span>
                                            <span style={{ fontWeight: "500" }}><strong>Lay’s Seed Enterprize</strong></span>
                                            <p className="dark-gray-color"><i class="bi bi-telephone-fill" style={{ fontSize: "10px" }}>
                                            </i>&nbsp;+880 1779 911 488</p>
                                        </span>
                                    </div>
                                </div>
                                <div>
                                    <button className='btn sales-credit-btn' style={{ padding: "0px 15px", borderRadius: "13px" }}>
                                        <span className='text-white' style={{ fontSize: "0.83rem" }}>
                                            Balance<br />
                                            55,606,521
                                        </span>
                                    </button>
                                </div>
                            </div>
                        </div>
                        {/* STEPPER ROW */}
                        <div className='sales-booking-view-stepper row'>
                            <div className='col-xl-12'>
                                <Stepper activeStep={activeStep} alternativeLabel>
                                    {steps.map(label => (
                                        <Step key={label}>
                                            <StepLabel StepIconComponent={CustomStepIcon}>{label}</StepLabel>
                                        </Step>
                                    ))}
                                </Stepper>
                            </div>
                        </div>

                        {/* TABS ROW */}
                        <div className="mt-5">
                            <ul class="nav nav-pills mb-3" id="pills-tab-order" role="tablist">
                                <li class="nav-item" role="presentation">
                                    <a class="nav-link active" id="pills-proposal-summary-tab" data-toggle="pill" href="#pills-proposal-summary" role="tab" aria-controls="pills-proposal-summary" aria-selected="true">Proposal Summary</a>
                                </li>
                                <li class="nav-item" role="presentation">
                                    <a class="nav-link" id="pills-payment-activities-reports-tab" data-toggle="pill" href="#pills-payment-activities-reports" role="tab" aria-controls="pills-payment-activities-reports" aria-selected="false">Activities & Reports</a>
                                </li>
                            </ul>
                            <div class="tab-content" id="pills-tab-orderContent">
                                {/* PROPOSAL SUMMARY */}
                                <div class="tab-pane fade show active" id="pills-proposal-summary" role="tabpanel" aria-labelledby="pills-proposal-summary-tab">
                                    <div className='row order-table'>
                                        <div className='col-xl-6'>
                                            <ProposalSummaryInfo  />
                                        </div>
                                        <div className='col-xl-6 mt-5'>
                                            <Card style={{ borderRadius: "25px" }}>
                                                <CardBody>
                                                    <ProposalSummaryReason  />
                                                </CardBody>
                                            </Card>
                                        </div>
                                    </div>
                                </div>

                                {/* ACTIVITIES AND REPORTS */}
                                <div class="tab-pane fade" id="pills-payment-activities-reports" role="tabpanel" aria-labelledby="pills-payment-activities-reports-tab">
                                    <div className='order-table row p-5'>
                                        <div className='col-xl-6'>
                                        <ProposalActivities  />
                                        </div>
                                        <div className='col-xl-6'>
                                            <div>
                                                <div className='mt-3'>
                                                    <p className='report-title'>Reports</p>
                                                </div>
                                                <ProposalReports  />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </CardBody>
                </Card>
            </div>


        </>
    );
}
