import React from "react";
import { showError } from "../../../../pages/Alert";
export function AccountYearEditForm({
  saveAccountingYear,
}) {
  const resetForm = () => { // RESET VALUS
    document.getElementById("fiscalYearName").value = "";
    document.getElementById("startDate").value = "";
    document.getElementById("endDate").value = "";
  }
  const handleSubmit = (e) => {
    let obj = new Object();
    obj.fiscalYearName = document.getElementById("fiscalYearName").value;
    obj.startDate = document.getElementById("startDate").value;
    obj.endDate = document.getElementById("endDate").value;
    obj.organizationId = 1;
    if (!obj.fiscalYearName) {
      showError('Please Enter Fiscal Year Name.');
      document.getElementById("fiscalYearName").focus();
      return;
    }
    if (! obj.startDate) {
      showError('Please Select Start Date.');
      document.getElementById("startDate").focus();
      return;
    }
    if (!obj.endDate) {
      showError('Please Select End Date.');
      document.getElementById("endDate").focus();
      return;
    }
    saveAccountingYear(obj,e);
  }
  return (
    <>
    <div>
      <form className="form form-label-right">
        <div className="form-group row">
          <div className="col-lg-4">
            <label>Fiscal Year Name<span className="text-danger">*</span></label>
            <input
              id="fiscalYearName"
              name="fiscalYearName"
              placeholder="Fiscal Year Name"
              className="form-control"
            />
          </div>
          <div className="col-lg-4">
            <label>Start Date<span className="text-danger">*</span></label>
            <input
              className="form-control"
              id="startDate"
              name="startDate"
              placeholder="sdjhsd"
              type="date"
            />
          </div>
          <div className="col-lg-4">
            <label>End Date<span className="text-danger">*</span></label>
            <input
              className="form-control"
              id="endDate"
              name="endDate"
              placeholder="ghsdg"
              type="date"
            />
          </div>
        </div>
      </form>
      </div>
      <div style={{ float: "inline-end" }}>
        <button className="btn btn-light ml-2"
          onClick={resetForm}
        >
          <i className="fa fa-redo"></i>
              Reset
              </button>

              <button
                className="btn btn-primary ml-2"
                onClick={(e) => handleSubmit(e)}
              >
                Save
              </button>
      </div>
    </>
  );
}
