/* eslint-disable no-restricted-imports */
import React from "react";
import clsx from "clsx";
import {makeStyles, withStyles, lighten} from "@material-ui/core/styles";
import {
  CircularProgress,
  Fab,
  Button,
  LinearProgress,
  Fade,
  Typography
} from "@material-ui/core";
import CheckIcon from "@material-ui/icons/Check";
import SaveIcon from "@material-ui/icons/Save";
import {green} from "@material-ui/core/colors";
import {Notice, KTCodeExample} from "../../../../_metronic/_partials/controls";

// Example 1
const useStyles1 = makeStyles(theme => ({
  progress: {
    margin: theme.spacing(2)
  }
}));

// Example 2
const useStyles2 = makeStyles(theme => ({
  root: {
    display: "flex",
    alignItems: "center"
  },
  wrapper: {
    margin: theme.spacing(1),
    position: "relative"
  },
  buttonSuccess: {
    backgroundColor: green[500],
    "&:hover": {
      backgroundColor: green[700]
    }
  },
  fabProgress: {
    color: green[500],
    position: "absolute",
    top: -6,
    left: -6,
    zIndex: 1
  },
  buttonProgress: {
    color: green[500],
    position: "absolute",
    top: "50%",
    left: "50%",
    marginTop: -12,
    marginLeft: -12
  }
}));

// Example 3
const useStyles3 = makeStyles(theme => ({
  progress: {
    margin: theme.spacing(2)
  }
}));

// Example 4
const useStyles4 = makeStyles(theme => ({
  progress: {
    margin: theme.spacing(2)
  }
}));

// Example 5
const useStyles5 = makeStyles({
  root: {
    flexGrow: 1
  }
});

// Example 6
const useStyles6 = makeStyles({
  root: {
    flexGrow: 1
  }
});

// Example 7
const useStyles7 = makeStyles({
  root: {
    flexGrow: 1
  }
});

// Example 8
const useStyles8 = makeStyles({
  root: {
    flexGrow: 1
  }
});

// Example 9
const ColorCircularProgress9 = withStyles({
  root: {
    color: "#00695c"
  }
})(CircularProgress);

const ColorLinearProgress9 = withStyles({
  colorPrimary: {
    backgroundColor: "#b2dfdb"
  },
  barColorPrimary: {
    backgroundColor: "#00695c"
  }
})(LinearProgress);

const BorderLinearProgress9 = withStyles({
  root: {
    height: 10,
    backgroundColor: lighten("#ff6c5c", 0.5)
  },
  bar: {
    borderRadius: 20,
    backgroundColor: "#ff6c5c"
  }
})(LinearProgress);

// Inspired by the Facebook spinners.
const useStylesFacebook9 = makeStyles({
  root: {
    position: "relative"
  },
  top: {
    color: "#eef3fd"
  },
  bottom: {
    color: "#6798e5",
    animationDuration: "550ms",
    position: "absolute",
    left: 0
  }
});

function FacebookProgress9(props) {
  const classes = useStylesFacebook9();

  return (
    <div className={classes.root}>
      <CircularProgress
        variant="determinate"
        value={100}
        className={classes.top}
        size={24}
        thickness={4}
        {...props}
      />
      <CircularProgress
        variant="indeterminate"
        disableShrink
        className={classes.bottom}
        size={24}
        thickness={4}
        {...props}
      />
    </div>
  );
}

const useStyles9 = makeStyles(theme => ({
  root: {
    flexGrow: 1
  },
  margin: {
    margin: theme.spacing(1)
  }
}));

// Example 10
const useStyles10 = makeStyles(theme => ({
  root: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center"
  },
  button: {
    margin: theme.spacing(2)
  },
  placeholder: {
    height: 40
  }
}));

export default function ProgressExamplesPage() {
  // Example 1
  const classes1 = useStyles1();

  // Example 2
  const classes2 = useStyles2();
  const [loading2, setLoading2] = React.useState(false);
  const [success2, setSuccess2] = React.useState(false);
  const timer2 = React.useRef();

  const buttonClassname2 = clsx({
    [classes2.buttonSuccess]: success2
  });

  React.useEffect(() => {
    return () => {
      clearTimeout(timer2.current);
    };
  }, []);

  function handleButtonClick2() {
    if (!loading2) {
      setSuccess2(false);
      setLoading2(true);
      timer2.current = setTimeout(() => {
        setSuccess2(true);
        setLoading2(false);
      }, 2000);
    }
  }

  // Example 3
  const classes3 = useStyles3();
  const [progress3, setProgress3] = React.useState(0);

  React.useEffect(() => {
    function tick() {
      // reset when reaching 100%
      setProgress3(oldProgress => (oldProgress >= 100 ? 0 : oldProgress + 1));
    }

    const timer3 = setInterval(tick, 20);
    return () => {
      clearInterval(timer3);
    };
  }, []);

  // Example 4
  const classes4 = useStyles4();
  const [completed4, setCompleted4] = React.useState(0);

  React.useEffect(() => {
    function progress() {
      setCompleted4(prevCompleted =>
        prevCompleted >= 100 ? 0 : prevCompleted + 10
      );
    }

    const timer4 = setInterval(progress, 1000);
    return () => {
      clearInterval(timer4);
    };
  }, []);

  // Example 5
  const classes5 = useStyles5();

  // Example 6
  const classes6 = useStyles6();
  const [completed6, setCompleted6] = React.useState(0);

  React.useEffect(() => {
    function progress() {
      setCompleted6(oldCompleted => {
        if (oldCompleted === 100) {
          return 0;
        }
        const diff = Math.random() * 10;
        return Math.min(oldCompleted + diff, 100);
      });
    }

