import React, { useEffect, useMemo, useState } from 'react';
import { toAbsoluteUrl } from "../../../../../_metronic/_helpers";
import { Card, CardBody } from "../../../../../_metronic/_partials/controls";
import { useHistory } from 'react-router-dom';
import LocationTreeView from '../../../SalesCollection/CommonComponents/LocationTreeView';
import { showError } from '../../../../pages/Alert';
import axios from "axios";
import { useIntl } from "react-intl";
import { shallowEqual, useSelector } from "react-redux";
import ProductCategoryTreeView from '../../../SalesCollection/CommonComponents/ProductCategoryTreeView';
import Autocomplete from "@material-ui/lab/Autocomplete";
import { TextField } from "@material-ui/core";
import Button from '@material-ui/core/Button';
import { typeOf } from 'react-is';
import moment from "moment";
import Flatpickr from "react-flatpickr";
import "flatpickr/dist/themes/material_green.css";
import MisReportBreadCrum from '../MisReportBreadCrum';
import { getDayDiff, getDaysCount } from "../../../Util"


export default function DepotToDepotMovementReport() {

    const fields = {
        categoryIds: "",
        productCategoryId: "",
        startDate: "",
        endDate: "",
        reportFormat: "",
    };

    const [inputs, setInputs] = useState(fields);
    const [producCategoryTree, setProductCategoryTree] = useState([]);
    const selectedCompany = useSelector((state) => state.auth.company, shallowEqual);
    const userLoginId = useSelector((state) => state.auth.user.userId, shallowEqual);
    const companyId = useSelector((state) => state.auth.company, shallowEqual);
    const intl = useIntl();
    const [productCategoryId, setProductCategoryId] = useState('');
    const [categoryIds, setCategoryIds] = useState([]);

    //const [childCategoryList, setChildCategoryList] = useState([]);
    //const [locationId, setLocationId] = useState('');
    //const [locationTree, setLocationTree] = useState([]);
    //const [accountingYearId, setAccountingYearId] = useState('');
    //const [productList, setProductList] = useState([]);
    //const [allAccountingYear, setAllAccountingYear] = useState([]);
    //const [categoryName, setCategoryName] = useState('All');
    //const [salesOfficer, setSalesOfficer] = useState([]);
    //const [inputs, setInputs] = useState({});
    //const [salesOfficerValue, setSalesOfficerValue] = useState(null);
    //const [showReport, setShowReport] = useState('');
    //const [reportData, setReportData] = useState([]);
    //const [showProductTradePriceChangeHistory, setProductTradePriceChangeHistory] = useState(false);
    //const [searchProductList, setSearchProductList] = useState([]);

    const searchParams = useMemo(() => {
        return {
            userLoginId: userLoginId, companyId: selectedCompany,
            categoryIds: categoryIds ,productCategoryId: productCategoryId
        }
    }, [userLoginId, selectedCompany, productCategoryId]);

    const [selectedLocation, setSelectedLocation] = useState({});
    const [selectProductCategory, setSelectProductCategory] = useState({});
    //const [total, setTotal] = useState({ totalQuantity: 0, totlaSalesAmount: 0 });
    //const [salesDataList, setProductTradePriceChangeHistoryList] = useState([]);

    let history = useHistory();
    const [anchorEl, setAnchorEl] = React.useState(null);
    let [singleAll, setSingleAll] = React.useState([]);

    const handleClick = (event) => {
        setAnchorEl(anchorEl ? null : event.currentTarget);
    };
    const open = Boolean(anchorEl);
    const id = open ? 'simple-popper' : undefined;

    useEffect(() => {
        getProductCategoryTreeList(selectedCompany);
    }, [userLoginId, selectedCompany]);


    const getProductCategoryTreeList = (params) => {
        const URL = `${process.env.REACT_APP_API_URL}/api/product-category/list-info/` + params;
        axios.get(URL).then(response => {
            setProductCategoryTree(response.data.data.childProductCategoryDtoList);
        }).catch(err => {
            showError("Can not get product category tree data.");
        });
    }

    const selectTreeNode = (node) => {
        let id = "summary-id-" + node.id;
        const getId = document.getElementById(id);
        const getElements = document.getElementsByClassName('tree-nav__item_demo tree-nav__item-title');

        for (var i = 0; i < getElements.length; i++) {
            getElements[i].classList.remove('tree-nav-item');
        }
        if (getId) {
            getId.classList.add('tree-nav-item');
             setSelectProductCategory(node);
             setProductCategoryId(node.id);
             setInputs({ ...inputs, "productCategoryId": node.id })
        }
        if (categoryIds.length !== 0) { // for clear state
            categoryIds.length = 0;
        }
        getProductCategoryIds(node);
    }

    // const getSalesOfficerListByLocation = (locationId, companyId) => {
    //     const URL = `${process.env.REACT_APP_API_URL}/auth/get-sales-officer-location/${locationId}/${companyId}`;
    //     axios.get(URL).then(response => {
    //         if (response.data.data != undefined) {
    //             console.log(response.data.data)
    //             setSalesOfficer(response.data.data);
    //         }
    //     }).catch(err => {
    //         showError("Cannot get Company data.");
    //     });
    // }

    const handleChange = (event) => {
        let name = event.target.name;
        let value = event.target.value;
        setInputs(values => ({ ...values, [name]: value }))
    }

    const validate = () => {

        // if (!inputs.productCategoryId) {
        //     showError('Product Category is required.');
        //     return false;
        // }
        if (!inputs.startDate) {
            showError("Start date  is required");
            return false;
        }
        else if (!inputs.endDate) {
            showError("End date is required");
            return false;
        }

        else if (inputs.endDate < inputs.startDate) {
            showError("End Date should be greater than Start Date.");
            return false;
        }

        else if (!inputs.reportFormat) {
            showError('Report Format is required.');
            return false;
        }
        return true;
    }

    const submit = () => {  
        console.log("inputs", categoryIds);    
        producCategoryTree.map(aaa => {            
            getProductCategoryIds(aaa);
        })  

        if (!validate()) {
            return false;
        }
        let queryString = '?';
        queryString += 'companyId=' + parseInt(selectedCompany);
        queryString += categoryIds.length !== 0 ? '&categoryIds=' + categoryIds : '';
        queryString += inputs.productCategoryId ? '&productCategoryId=' + inputs.productCategoryId : '';
        queryString += '&startDate=' + inputs.startDate;
        queryString += '&endDate=' + inputs.endDate;
        queryString += '&reportFormat=' + inputs.reportFormat;

        const URL = `${process.env.REACT_APP_API_URL}/api/reports/depot-to-depot-movement-history` + queryString;
        axios.get(URL, { responseType: 'blob' }).then(response => {
            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            if(inputs.report == "pdf") {
                link.setAttribute('download', "depotToDepotMovement.pdf");
            }
            else {
                link.setAttribute('download', "depotToDepotMovement.xlsx");
            }
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);

        }).catch(err => {
            showError();
        });
    }

    const getProductCategoryIds = (node) => {
        let temp = [...categoryIds]
        let index = temp.findIndex(id => id === node.id)
        if (index === -1) {
            categoryIds.push(node.id)
        }

        node.children.map(nodeChild => {
            getProductCategoryIds(nodeChild)
        })
    }

    return (
        <>
            <div>
                <MisReportBreadCrum menuTitle="Depot To Depot Movement Report" />
            </div>
            <div>
                <Card>
                    <CardBody>
                        <div className='row'>
                            {/* LEFT SIDE TREE ROW */}
                            <div className='col-xl-3' style={{ borderRight: "1px solid #F2F2F2" }}>
                                <div style={{ borderBottom: "1px solid #F2F2F2" }}>
                                    <label>
                                        <img src={toAbsoluteUrl("/images/loc3.png")}
                                            style={{ width: "20px", height: "20px", textAlign: "center" }}
                                            alt='Company Picture' />

                                        <strong style={{
                                            marginLeft: "10px",
                                            color: "#828282"
                                        }}>Product Category (All)</strong>
                                    </label>
                                </div>
                                {/* TREE */}
                                <ProductCategoryTreeView
                                    tree={producCategoryTree}
                                    selectProductCategoryTreeNode={selectTreeNode}
                                />
                            </div>
                            {/* RIGHT SIDE LIST ROW */}
                            <div className='col-xl-9'>
                                <div className='mt-5 row'>

                                </div>

                                <div className='row'>
                                    <div className='form-group col-lg-8 first-level-top'>
                                        <label className='level-title'>Start Date<i style={{ color: "red" }}>*</i></label>
                                        <Flatpickr className="form-control" id="startDate" placeholder="dd-MM-yy"
                                            name='startDate'
                                            value={inputs.startDate ? moment(inputs.startDate, "YYYY-MM-DD").format("DD-MMM-YYYY") : ''}
                                            options={{ dateFormat: "d-M-Y" }}
                                            onChange={(value) => {
                                                setInputs({
                                                    ...inputs, "startDate": moment(new Date(value)).format("YYYY-MM-DD")
                                                })

                                            }}
                                        />
                                    </div>
                                </div>


                                <div className='row'>
                                    <div className='form-group col-lg-8 first-level-top'>
                                        <label className='level-title'>End Date<i style={{ color: "red" }}>*</i></label>
                                        <Flatpickr className="form-control" id="endDate" placeholder="dd-MM-yy"
                                            name='endDate'
                                            value={inputs.endDate ? moment(inputs.endDate, "YYYY-MM-DD").format("DD-MMM-YYYY") : ''}
                                            options={{ dateFormat: "d-M-Y" }} required
                                            onChange={(value) => {
                                                setInputs({
                                                    ...inputs, "endDate": moment(new Date(value)).format("YYYY-MM-DD")
                                                })
                                            }}
                                        />
                                    </div>
                                </div>


                                <div className='row'>
                                    <div className='form-group col-lg-8 first-level-top'>
                                        <div>
                                            <label className='level-title'><span className="mr-1">Report Format</span><span className="text-danger ">*</span></label>
                                            <select className="form-control" id="reportFormat" name="reportFormat"
                                                onChange={(e) => handleChange(e)}
                                            >
                                                <option value="">Select Report Format</option>
                                                <option value="pdf">Pdf</option>
                                                <option value="excel">Excel</option>
                                            </select>
                                        </div>
                                    </div>
                                </div>
                                <Button className="float-right mt-5" id="gotItBtn" variant="contained"
                                    color="primary"
                                    onClick={submit}
                                >
                                    Submit
                                </Button>
                            </div>
                        </div>
                    </CardBody>
                </Card>
            </div>
        </>
    );
}
