import React from "react";
import { useHistory } from "react-router-dom";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import { ActionsColumnFormatter } from "./ActionsColumnFormatter";
import {useIntl} from "react-intl";
const data = [
  {
    "id": 1,
    "code": "12535689",
    "duration": "12 Days Ago",
    "date": "10 APR 2022 10:23 AM",
    "distributor":"Bhai Bhai Enterprize",
    "balance": "570,168,000",
    "user": "Mmh Shohagh",
    "designation": "Accountant, Babylon Group",
    "collectionamount": "570,168,000",
    "collectionType": "CREDIT",
    "status": "Verify",
    "nature":"APPROVED"
  },
  {
    "id": 2,
    "code": "12535689",
    "duration": "12 Days Ago",
    "date": "10 APR 2022 10:23 AM",
    "distributor":"Bhai Bhai Enterprize",
    "balance": "570,168,000",
    "user": "Mmh Shohagh",
    "designation": "Accountant, Babylon Group",
    "collectionamount": "570,168,000",
    "collectionType": "DEBIT",
    "status": "Verified",
    "nature":"REJECTED"
  },
  {
    "id": 3,
    "code": "12535689",
    "duration": "12 Days Ago",
    "date": "10 APR 2022 10:23 AM",
    "distributor":"Bhai Bhai Enterprize",
    "balance": "570,168,000",
    "user": "Mmh Shohagh",
    "designation": "Accountant, Babylon Group",
    "collectionamount": "570,168,000",
    "collectionType": "DEBIT",
    "status": "Rejected",
    "nature":"PENDING"
  },
]
export function ApprovalList({setSingleAll, singleAll}) {
  const intl = useIntl();
  let history = useHistory();

  const openViewPage = (data) => {
    history.push('/salescollection/payment-adjustment/credit-debit-note/approval-view', { state: data });
  }

  const singleWiseSelectHandler = (data, isSelect) => {
    if (isSelect == true) {
      let temp = [...singleAll]
      temp.push(data)
      setSingleAll(temp)
    } else {
      if (singleAll.length >= 0) {
        let temp = [...singleAll]
        const index = temp.findIndex(obj => obj.id == data.id);
        temp.splice(index, 1);
        setSingleAll(temp)
      }
    }

  }
  const allSelectHandler = (allData, isSelect) => {
    if (isSelect == true) {
      setSingleAll(allData)
    }
    else {
      if (allData.length >= 0) {
        for (let i = 0; i < allData.length; i++) {
          const index = singleAll.findIndex(obj => obj.id == allData[i].id);
          singleAll.splice(index, 1);
          setSingleAll(singleAll)
        }
      }
    }

  }
  const columns = [
    {
      dataField: "code",
      text:"CREDIT/DEBIT INFO",
      formatter: (cellContent, row) => (
        <>
          <span className="text-muted">{row.code}</span>
          <span className={row.nature == "APPROVED"?"dark-success-color":row.nature == "REJECTED"?"text-danger":"dark-gray-color"}><strong>({row.nature})</strong></span>
          <br />
          <strong>{row.duration}</strong><br />
          <span className="text-muted">{row.date}</span>
        </>
      )
    },
    {
      dataField: "sales_officer",
      text: "DISTRIBUTORS",
      formatter: (cellContent, row) => (
        <>
          <strong>{row.distributor}</strong><br />
          <span className="text-muted">Balance&nbsp;{row.balance}</span>
        </>
      )
    },
    {
      dataField: "collection_amount",
      text: "ENTRY BY",
      formatter: (cellContent, row) => (
        <>
          <strong>{row.user}</strong><br />
          <span className="text-muted">{row.designation}</span>
        </>
      )
    },
    {
      dataField: "collection_amount",
      text: "AMOUNT",
      formatter: (cellContent, row) => (
        <>
          <strong className={row.collectionType == "CREDIT" ? "dark-success-color":"text-danger"}>{row.collectionamount}</strong><br />
          <span className="text-muted" style={{marginLeft:"30px"}}>{row.collectionType}</span>
        </>
      )
    },
    {
      dataField: "action",
      text: intl.formatMessage({id: "MENU.ACTION"}),
      formatter: ActionsColumnFormatter,
      formatExtraData: {
        openViewPage: openViewPage,
      },
      classes: "text-center",
      headerClasses: "text-center",
      style: {
        minWidth: "100px",
      },
    },
  ];

  return (
    <>
      <BootstrapTable
        wrapperClasses="table-responsive"
        classes="table table-head-custom table-vertical-center overflow-hidden"
        bootstrap4
        bordered={false}
        keyField="id"
        data={data}
        columns={columns}
        selectRow={
          {
            mode: 'checkbox',
            onSelect: (row, isSelect, rowIndex, e) => {
              singleWiseSelectHandler(row, isSelect);
            },
            onSelectAll: (isSelect, rows, e) => {
              allSelectHandler(rows, isSelect);

            }

          }
        }

        pagination={paginationFactory({ sizePerPage: 10, showTotal: true })}
      />
    </>
  );
}
