import { useHistory } from "react-router-dom";
import React, { useState, useEffect } from 'react';
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import { ActionsColumnFormatter } from "./ActionsColumnFormatter";

export function TradeDiscountList({ setSingleAll, singleAll }) {

    const datas = [
        {
            "id": 1,
            "name": "Seaconazole 5SC 50 ml * 40 ",
            "sku": "SKU X1253689",
            "type": "Fungicide/",
            "date": "12/06/2022",
            "tp": "৳142",
            "salesOfficerDetails": "Sales Officer, Cox’s Bazar",
            "subQty": "5000+",
            "discre": "৳127.8",
            "disCas": "৳127.8",
            "disper": "10% Discount"
        },
        {
            "id": 2,
            "name": "Seaconazole 5SC 50 ml * 40 ",
            "sku": "SKU X1253689",
            "type": "Fungicide/",
            "date": "12/06/2022",
            "tp": "৳142",
            "salesOfficerDetails": "Sales Officer, Cox’s Bazar",
            "subQty": "5000+",
            "discre": "৳127.8",
            "disCas": "৳127.8",
            "disper": "10% Discount"
        },
        {
            "id": 3,
            "name": "Seaconazole 5SC 50 ml * 40 ",
            "sku": "SKU X1253689",
            "type": "Fungicide/",
            "date": "12/06/2022",
            "tp": "৳142",
            "salesOfficerDetails": "Sales Officer, Cox’s Bazar",
            "subQty": "5000+",
            "discre": "৳127.8",
            "disCas": "৳127.8",
            "disper": "10% Discount"
        },
        {
            "id": 4,
            "name": "Seaconazole 5SC 50 ml * 40 ",
            "sku": "SKU X1253689",
            "type": "Fungicide/",
            "date": "12/06/2022",
            "tp": "৳142",
            "salesOfficerDetails": "Sales Officer, Cox’s Bazar",
            "subQty": "5000+",
            "discre": "৳127.8",
            "disCas": "৳127.8",
            "disper": "10% Discount"
        },
        {
            "id": 5,
            "name": "Seaconazole 5SC 50 ml * 40 ",
            "sku": "SKU X1253689",
            "type": "Fungicide/",
            "date": "12/06/2022",
            "tp": "৳142",
            "salesOfficerDetails": "Sales Officer, Cox’s Bazar",
            "subQty": "5000+",
            "discre": "৳127.8",
            "disCas": "৳127.8",
            "disper": "10% Discount"
        },

    ];
    let history = useHistory();

    const openViewPage = (data) => {

        history.push('/salescollection/sales/sales-order', { state: data });
    }
    const openDeleteDialog = (data) => {

        history.push('/salescollection/sales/sales-order', { state: data });
    }
    const openEditPage = (data) => {

        history.push('/salescollection/sales/sales-order', { state: data });
    }

    const singleWiseSelectHandler = (data, isSelect) => {
        if (isSelect == true) {
            let temp = [...singleAll]
            temp.push(data)
            setSingleAll(temp)
        } else {
            if (singleAll.length >= 0) {
                let temp = [...singleAll]
                const index = temp.findIndex(obj => obj.id == data.id);
                temp.splice(index, 1);
                setSingleAll(temp)
            }
        }

    }
    const allSelectHandler = (allData, isSelect) => {
        if (isSelect == true) {
            setSingleAll(allData)
        }
        else {
            if (allData.length >= 0) {
                for (let i = 0; i < allData.length; i++) {
                    const index = singleAll.findIndex(obj => obj.id == allData[i].id);
                    singleAll.splice(index, 1);
                    setSingleAll(singleAll)
                }
            }
        }

    }

    const columns = [
        {
            dataField: "productInfo",
            text: "PRODUCTS INFO",
            formatter: (cellContent, row) => (
                <>
                    <span className="text-muted">{row.sku}</span><br />
                    <strong>{row.name}</strong><br />
                    <span className="text-muted">{row.type}</span>

                </>
            )
        },
        {
            dataField: "tp",
            text: "Trdae Price",
            formatter: (cellContent, row) => (
                <>
                    <strong>{row.tp}</strong><br />
                    <span className="text-muted">{row.date}</span>
                </>
            )
        },
        {
            dataField: "discount(cash)",
            text: "Discount(Cash)",
            formatter: (cellContent, row) => (
                <>
                    <strong>{row.disCas}</strong><br />
                    <span className="text-muted">{row.disper}</span>
                </>
            )
        },
        {
            dataField: "subSalesAmount",
            text: "Discount(Credit)",
            formatter: (cellContent, row) => (
                <>
                    <strong>{row.discre}</strong><br />
                    <span className="text-muted">{row.disper}</span>
                </>
            )
        },
        {
            dataField: "action",
            text: "Action",
            formatter: ActionsColumnFormatter,
            formatExtraData: {
                openViewPage: openViewPage,
                openEditPage: openEditPage,
                openDeleteDialog: openDeleteDialog,
            },
            classes: "text-center",
            headerClasses: "text-center",
            style: {
                minWidth: "100px",
            },
        }
    ];

    return (
        <>
            <BootstrapTable
                wrapperClasses="table-responsive"
                classes="table table-head-custom table-vertical-center overflow-hidden"
                bootstrap4
                bordered={false}
                keyField="id"
                data={datas}
                columns={columns}
                selectRow={
                    {
                        mode: 'checkbox',
                        onSelect: (row, isSelect, rowIndex, e) => {
                            singleWiseSelectHandler(row, isSelect);
                        },
                        onSelectAll: (isSelect, rows, e) => {
                            allSelectHandler(rows, isSelect);
                        }
                    }
                }
                pagination={paginationFactory({ sizePerPage: 10, showTotal: true })}
            />

        </>

    );
}
