import React from "react";
import { useHistory } from "react-router-dom";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import { ActionsColumnFormatter } from "./ActionsColumnFormatter";
import {useIntl} from "react-intl";
const data = [
  {
    "id": 1,
    "code": "12535689",
    "duration": "12 Days Left",
    "date": "10 APR 2022",
    "dateTime": "10 APR 2022 10:23 AM",
    "distributor":"Bhai Bhai Enterprize",
    "balance": "570,168,000",
    "user": "Mmh Shohagh",
    "designation": "Sales Officer, Cox’s Bazar",
    "collectionamount": "570,168,000",
    "collectionType": "Cheque",
    "status": "PENDING",
    "invoiceNo": "Cash"
  },
  {
    "id": 2,
    "code": "12535689",
    "duration": "12 Days Left",
    "date": "10 APR 2022",
    "dateTime": "10 APR 2022 10:23 AM",
    "distributor":"Bhai Bhai Enterprize",
    "balance": "570,168,000",
    "user": "Mmh Shohagh",
    "designation": "Sales Officer, Cox’s Bazar",
    "collectionamount": "570,168,000",
    "collectionType": "Online",
    "status": "REVIEW",
    "invoiceNo": "Cash"
  },
  {
    "id": 3,
    "code": "12535689",
    "duration": "12 Days Left",
    "date": "10 APR 2022",
    "dateTime": "10 APR 2022 10:23 AM",
    "distributor":"Bhai Bhai Enterprize",
    "balance": "570,168,000",
    "user": "Mmh Shohagh",
    "designation": "Sales Officer, Cox’s Bazar",
    "collectionamount": "570,168,000",
    "collectionType": "Online",
    "status": "WAITING",
    "invoiceNo": "Cash"
  },
  {
    "id": 4,
    "code": "12535689",
    "duration": "12 Days Left",
    "date": "10 APR 2022",
    "dateTime": "10 APR 2022 10:23 AM",
    "distributor":"Bhai Bhai Enterprize",
    "balance": "570,168,000",
    "user": "Mmh Shohagh",
    "designation": "Sales Officer, Cox’s Bazar",
    "collectionamount": "570,168,000",
    "collectionType": "Cash",
    "status": "APPROVED",
    "invoiceNo": "Credit"
  },
  {
    "id": 5,
    "code": "12535689",
    "duration": "12 Days Left",
    "date": "10 APR 2022",
    "dateTime": "10 APR 2022 10:23 AM",
    "distributor":"Bhai Bhai Enterprize",
    "balance": "570,168,000",
    "user": "Mmh Shohagh",
    "designation": "Sales Officer, Cox’s Bazar",
    "collectionamount": "570,168,000",
    "collectionType": "Online",
    "status": "REJECTED",
    "invoiceNo": "Cash"
  },

]
export default function SalessBookingList({setSingleAll, singleAll}) {
  const intl = useIntl();
  let history = useHistory();
  const userId = useSelector((state) => state.auth.user.userId, shallowEqual);
  const companyId = useSelector((state) => state.auth.company, shallowEqual);
  const [allStatus, setAllStatus] = useState([]);

  useEffect(() => {
    document.getElementById('pills-distributors-credit-limit-proposal-tab').classList.add('active');
    getStatus();
}, [companyId]);

  const openApprovePage = (data) => {
    history.push('/sales-booking-proposal-approval', { state: data });
  }
  const openWaitingPage = (data) => {
    //history.push('/salescollection/payment-adjustment/payment-verify/view-verify', { state: data });
  }
  const openReviewPage = (data) => {
    //history.push('/salescollection/payment-adjustment/payment-verify/view-verify', { state: data });
  }

  const singleWiseSelectHandler = (data, isSelect) => {
    if (isSelect == true) {
      let temp = [...singleAll]
      temp.push(data)
      setSingleAll(temp)
    } else {
      if (singleAll.length >= 0) {
        let temp = [...singleAll]
        const index = temp.findIndex(obj => obj.id == data.id);
        temp.splice(index, 1);
        setSingleAll(temp)
      }
    }

  }
  const allSelectHandler = (allData, isSelect) => {
    if (isSelect == true) {
      setSingleAll(allData)
    }
    else {
      if (allData.length >= 0) {
        for (let i = 0; i < allData.length; i++) {
          const index = singleAll.findIndex(obj => obj.id == allData[i].id);
          singleAll.splice(index, 1);
          setSingleAll(singleAll)
        }
      }
    }
  }

  const getStatus = () =>{
    const URL = `${process.env.REACT_APP_API_URL}/api/constants/approval-status`;
    axios.get(URL).then(response => {
        setAllStatus(response.data.data);
    }).catch(err => {
        showError(intl.formatMessage({ id: "COMMON.ERROR_STATUS" }));
    });
}

  const columns = [
    {
      dataField: "code",
      text:"BOOKING INFO",
      formatter: (cellContent, row) => (
        <>
          <span className="text-muted">{row.code}</span>
          <strong className=
          {row.status === "PENDING" ? "dark-gray-color" : 
          row.status === "APPROVED" ? "dark-success-color" : 
          row.status === "REJECTED" ? "dark-danger-color" : "dark-gray-color"}
          >({row.status})</strong><br />
          <strong>{row.distributor}</strong><br />
          <span className="text-muted">{row.date}</span>
        </>
      )
    },
    {
      dataField: "sales_officer",
      text: "BY",
      headerClasses:"text-center",
      formatter: (cellContent, row) => (
        <>
          <strong>{row.user}</strong><br />
          <span className="text-muted">Balance&nbsp;{row.designation}</span>
        </>
      )
    },
    {
      dataField: "collection_amount",
      text: "EST. Delivery date",
      headerClasses:"text-right",
      classes:"text-right",
      formatter: (cellContent, row) => (
        <>
          <strong>{row.duration}</strong><br />
          <span className="text-muted">{row.date}</span>
        </>
      )
    },
    {
      dataField: "collection_amount",
      text: "booking amount",
      headerClasses:"text-right",
      formatter: (cellContent, row) => (
        <>
          <strong className="float-right">{row.collectionamount}</strong><br />
          <span className="text-muted float-right" >{row.invoiceNo}</span>
        </>
      )
    },
    {
      dataField: "action",
      text: intl.formatMessage({id: "MENU.ACTION"}),
      formatter: ActionsColumnFormatter,
      formatExtraData: {
        openApprovePage: openApprovePage,
        openWaitingPage: openWaitingPage,
        openReviewPage: openReviewPage,
      },
      classes: "text-center",
      headerClasses: "text-center",
      style: {
        minWidth: "100px",
      },
    },
  ];

  return (
    <>
      <BootstrapTable
        wrapperClasses="table-responsive"
        classes="table table-head-custom table-vertical-center overflow-hidden"
        bootstrap4
        bordered={false}
        keyField="id"
        data={data}
        columns={columns}
        selectRow={
          {
            mode: 'checkbox',
            onSelect: (row, isSelect, rowIndex, e) => {
              singleWiseSelectHandler(row, isSelect);
            },
            onSelectAll: (isSelect, rows, e) => {
              allSelectHandler(rows, isSelect);

            }

          }
        }

        pagination={paginationFactory({ sizePerPage: 10, showTotal: true })}
      />
    </>
  );
}
