import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import { Card, CardBody } from "../../../../../../../_metronic/_partials/controls";
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { NavLink } from 'react-router-dom';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import IOSSwitch from '../../../../../../pages/IOSSwitch';
import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../../../../_metronic/_helpers";
import PaymentBookSetupBreadCrum from "../../common/PaymentBookSetupBreadCrum";

const useStyles = makeStyles(theme => ({
    root: {
        width: '90%',
    },
    button: {
        marginTop: theme.spacing(1),
        marginRight: theme.spacing(1),
    },
    actionsContainer: {
        marginBottom: theme.spacing(2),
    },
    resetContainer: {
        padding: theme.spacing(3),
    },
}));

const getSteps = () => {
    return ['Location', 'Payment Book', 'Completed'];
}

export function PaymentBookAdd(props) {
    const classes = useStyles();
    const [successImg, setSuccessImg] = useState(toAbsoluteUrl("/images/success2.png"));
    const [activeStep, setActiveStep] = useState(0);
    const steps = getSteps();
    const subsTitle = ["Insert company info", "Insert book info", "Woah, we are here"];
    const [ordCount, setORDCount] = useState(["Day 1 - 30 ORD 15%", "Day 31 - 60 ORD 10%", "Day 61 - 90 ORD 5%"])
    const [totalDays, setTotalDays] = useState(0)
    const [preIndex, setPreIndex] = useState(0)
    const [assignOrdSetup, setAssignOrdSetup] = useState([
        {
            startDay: '',
            endDay: '',
            totalDays: totalDays,
            ord: ''
        }
    ]);

    useEffect(() => {
        if (activeStep === 0) {
            document.getElementById('backBtn').style.visibility = 'hidden';
        }
    }, []);
    const handleNext = () => {
        if (activeStep === 1) {
            document.getElementById('nextBtn').style.visibility = 'hidden';
            document.getElementById('backBtn').style.display = 'none';
            document.getElementById('gotItBtn').style.visibility = 'visible';
        }

        if (activeStep >= 0) {
            document.getElementById('backBtn').style.visibility = 'visible';
        }
        setActiveStep(prevActiveStep => prevActiveStep + 1);
    }

    const handleBack = () => {
        if (activeStep === 1) {
            document.getElementById('backBtn').style.visibility = 'hidden';
        }
        setActiveStep(prevActiveStep => prevActiveStep - 1);
    }

    const getStepContent = (step) => {
        switch (step) {
            case 0:
                return <div className='steper-height ml-5'>
                    <div>
                        <span className="create-field-title">Location Info</span>
                        <p style={{ color: "#B6B6B6" }}>If you need more info, please check out <NavLink to="/salescollection/configure/payment-book/payment-book-list" className="faq-title"><u>FAQ Page</u></NavLink></p>
                    </div>

                    {/* COMPANY ROW */}
                    <div>
                        <label className='level-title'>Company</label>
                        <select id="company" className='form-control' name="company">
                            <option value="1">Babylon Resources Ltd(BRL)</option>
                            <option value="2">Babylon Resources Ltd(BRL)</option>
                            <option value="3">Babylon Resources Ltd(BRL)</option>
                        </select>
                    </div>

                    {/* LOCATION ROW */}
                    <div className='mt-5'>
                        <label className='level-title'>Location</label>
                        <select id="location" className='form-control' name="location">
                            <option value="1">Territory - Cox’s Bazar</option>
                            <option value="2">Territory - Cox’s Bazar</option>
                            <option value="3">Territory - Cox’s Bazar</option>
                        </select>
                    </div>
                </div>;
            case 1:
                return <div className='steper-height ml-5'>
                    <div>
                        <span className="create-field-title">Payment Book</span>
                        <p style={{ color: "#B6B6B6" }}>If you need more info, please check out <NavLink to="/salescollection/configure/payment-book/payment-book-list" className="faq-title"><u>FAQ Page</u></NavLink></p>
                    </div>

                    {/* PAYMENT BOOK NUMBER ROW */}
                    <div className='mt-5'>
                        <label className='level-title'>Payment Book Number</label>
                        <input placeholder='Ex. 994' id="paymentBookNumber" type="text" className='form-control' name="paymentBookNumber" />
                    </div>

                    <div className='mt-5 row'>
                        {/* FROM MPR NO ROW */}
                        <div className='col-xl-6'>
                            <label className='level-title'>From MPR No</label>
                            <input placeholder='Ex. 994650' id="fromMPRNo" type="text" className='form-control' name="fromMPRNo" />
                        </div>

                        {/* TO MPR NO ROW */}
                        <div className='col-xl-6'>
                            <label className='level-title'>TO MPR No</label>
                            <input placeholder='Ex. 994700' id="toMPRNo" type="text" className='form-control' name="toMPRNo" />
                        </div>
                    </div>

                    {/* STATUS ROW */}
                    <div className='mt-5'>
                        <label className='level-title'>Active Status</label><br />
                        <FormControlLabel
                            label="Active"
                            labelPlacement="end"
                            control={<IOSSwitch checked />}
                        />
                    </div>
                </div>;
            default:
                return <div className='row steper-height'>
                    <div className='col-lg-12' style={{ textAlign: "center" }}>
                        <img
                            src={successImg}
                            style={{ width: "84px", height: "84px", textAlign: "center" }} alt='Company Picture' />
                        <div className='mt-5'>
                            <p style={{ marginBottom: "0px" }} className="create-field-title">Thank You</p>
                            <span className='text-muted'>New Payment Book Setup Successfully</span>
                        </div>
                    </div>
                    <div className='col-xl-12'>
                        <div className='thanks-sub-title'>
                            <strong style={{ fontSize: "17px" }}>New Payment Book Info</strong>
                        </div>
                    </div>
                    <div className='col-lg-12'>
                        <div className='row'>
                            <div className='offset-xl-4 col-xl-4 text-center fiscal-year-thanks-div'>
                                <div>
                                    <span className="fiscal-year-title">Payment Book No. 994</span>
                                </div>
                                <div className="mt-1">
                                    <span className="daysCount mr-3">MRP No. 44850 - 44900(50)</span>
                                </div>
                                <div className='mt-2'>
                                    <div>
                                        <span className="text-muted">MRP No. 44850 - 44900(50)</span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>;
        }
    }
    const backToListPage = () => {
        props.history.push("/salescollection/configure/payment-book/payment-book-list");
    }
    return (
        <>
            {/* BREAD CRUM ROW */}
            <div>
                <PaymentBookSetupBreadCrum />
            </div>
            <Card>
                <CardBody>
                    <div className="row">
                        <div className="col-sm-12 col-xl-3  col-lg-3 col-md-3border border-left-0 border border-light border-primary border-bottom-0 border-top-0">
                            <div className="d-flex">
                                <div className="card-div-icon dark-gray-color">
                                    <i className="bi bi-chevron-left"></i>
                                </div>
                                <div className="ml-2 mt-1">
                                    <p className="dark-gray-color" style={{ fontWeight: "500" }}>
                                        <NavLink to="/salescollection/configure/payment-book/payment-book-list">&nbsp; Back to Payment Book Setup
                                        </NavLink>
                                    </p>
                                </div>
                            </div>

                            <div className={classes.root}>
                                <Stepper activeStep={activeStep} orientation="vertical">
                                    {steps.map((label, index) => (
                                        <Step key={label}>
                                            <StepLabel optional={<Typography variant="caption">{subsTitle[index]}</Typography>}>{label}</StepLabel>
                                        </Step>
                                    ))
                                    }
                                </Stepper>
                            </div>
                        </div>
                        <div className="col-sm-12 col-xl-9 col-lg-9 col-md-9">
                            <Typography>{getStepContent(activeStep)}</Typography>
                            <div className={classes.actionsContainer}>
                                <div className='mt-5'>
                                    <Button
                                        id="backBtn"
                                        onClick={handleBack}
                                        className={classes.button}
                                    >
                                        Back
                                    </Button>
                                    <Button
                                        id="nextBtn"
                                        disabled={activeStep === steps.length}
                                        variant="contained"
                                        color="primary"
                                        onClick={handleNext}
                                        className="float-right btn-gradient-blue"
                                    >
                                        {activeStep === steps.length - 2 ? 'Save' : 'Next'}
                                    </Button>
                                    <div style={{ textAlign: 'center', marginTop: '119px', marginLeft: "50px" }}>
                                        <Button
                                            style={{ visibility: "hidden" }}
                                            className='gotIt-btn'
                                            id="gotItBtn"
                                            variant="contained"
                                            color="primary"
                                            onClick={backToListPage}
                                        >
                                            Got it
                                        </Button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </CardBody>
            </Card>
        </>
    );
}
