import React, { useState, useEffect } from 'react';
import Popper from '@material-ui/core/Popper';
import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../_metronic/_helpers";
import { SalesOrderList } from './sales-table/SalesOrderList';
import {Card, CardBody} from "../../../../_metronic/_partials/controls";
import { useHistory } from 'react-router-dom';
import { showError } from '../../../pages/Alert';
import axios from 'axios';
import LocationTreeView from '../../SalesCollection/CommonComponents/LocationTreeView';


export function SalesOrder() {
    const [locationTree, setLocationTree] = useState([]);
    const [selectedLocation, setSelectedLocation] = useState({});
    const [sessionData, setSessionData] = useState({userLoginId: 1, companyId: 1, accountingYearId: 1});
    const [searchParams, setSearchParams] = useState(sessionData);
    const [salesOverView, setSalesOverView] = useState([]);
    let history = useHistory();
    let [singleAll, setSingleAll] = useState([]);
    const [anchorEl, setAnchorEl] = useState(null);
    const open = Boolean(anchorEl);
    const id = open ? 'simple-popper' : undefined;

    useEffect(() => {
        getLocationTreeList(sessionData);
    }, []);

    useEffect(() => {
        getSalesOrderOverViewList(searchParams);
    }, [searchParams]);

    const handleClick = (event) => {
        setAnchorEl(anchorEl ? null : event.currentTarget);
    };

    const handleSalesDataChange = () => {
        history.push("/salescollection/sales/slaes-data")
    }
    const handleBookingChange = () => {
        history.push("/salescollection/sales/slaes-booking")
    }

    const handleExport = () => {
        const exportData = [...singleAll]
        console.log(exportData)
    }

    const getSalesOrderOverViewList = (params) => {
        let queryString = '?';
        queryString += 'userLoginId=' + params.userLoginId;
        queryString += '&companyId=' + params.companyId;
        queryString += '&accountingYearId=' + params.accountingYearId;
        queryString += params.locationId ? '&locationId=' + params.locationId : '';
        const URL = `${process.env.REACT_APP_API_URL}/api/sales-order-data/overView`+ queryString;
        axios.get(URL).then(response => {
            const salesOrderOverView = response.data.data;
            setSalesOverView(salesOrderOverView);
        }).catch(err => {

        });
    }

    const getLocationTreeList = (params) => {
        const URL = `${process.env.REACT_APP_API_URL}/api/location-tree/locationTree/${params.userLoginId}/${params.companyId}`;
        axios.get(URL).then(response => {
            const locationTree = response.data.data;
            setLocationTree(locationTree);
        }).catch(err => {
            showError("Cannot get Location Tree data.");
        });
    }

    const selectLocationTreeNode = (node) => {
        setSelectedLocation(node);
        setSearchParams({...searchParams, locationId: node.id});
    }

    return (
        <>
            <div>
                {/* BREAD CRUM ROW */}
                <div className="row" style={{ marginTop: "-30px", marginLeft: "-18px" }}>
                    <div className="col-xl-6">
                        <nav aria-label="breadcrumb">
                            <ol className="breadCrum-bg-color">
                                <li aria-current="page" className='breadCrum-main-title'>Google</li>
                                <li aria-current="page" className='mt-1 breadCrum-sub-title'>&nbsp;Sales & Collection&nbsp;&nbsp;&nbsp;&nbsp;</li>
                                <li aria-current="page" className='mt-1 breadCrum-sub-title text-primary'><span className='font-weight-bolder'>.</span>&nbsp; Sales &nbsp;&nbsp;</li>
                            </ol>
                        </nav>
                    </div>
                    <div className="col-xl-6 d-flex justify-content-end">
                        <div className="mr-5">
                            <button
                                type="button"
                                className="btn approval-config-btn"
                                data-toggle="tooltip" data-placement="bottom" title="Approval Path Config"
                            >
                                <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/approval.svg")} width="15px" height="15px" />
                                &nbsp;&nbsp;Approval Path Config
                            </button>
                        </div>
                        <div>
                            <button
                                type="button"
                                className="btn approval-config-btn"
                                data-toggle="tooltip" data-placement="bottom" title="Product Configure"
                            >
                                <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/configure.svg")} width="15px" height="15px" />
                                &nbsp;&nbsp;Product Configure
                            </button>
                        </div>
                    </div>
                </div>
                {/* TODAY SALE ROW */}
                <div className="mt-3">
                    <Card>
                        <CardBody style={{ marginBottom: "-36px" }}>
                            <div>
                                <p className="create-field-title">Today’s Sale</p>
                            </div>
                            <div className="mt-5 ml-2 row">
                                <span className="sales-chip mt-5 mr-5"><i className="bi bi-arrow-up-short text-primary"></i>&nbsp;৳ 428,030&nbsp;<span className='text-muted'>Sale</span></span>
                                <span className="sales-chip mt-5 mr-5"><i className="bi bi-arrow-down-short text-danger"></i>&nbsp;৳ 428,030&nbsp;<span className='text-muted'>Booking</span></span>
                                <span className="sales-chip mt-5 mr-5"><i className="bi bi-arrow-up-short text-primary"></i>&nbsp;৳ 428,030&nbsp;<span className='text-muted'>Sales Order</span></span>
                                <span className="sales-chip mt-5 mr-5"><i className="bi bi-arrow-up-short text-primary"></i>&nbsp;QTY. 428&nbsp;<span className='text-muted'>Sales Return</span></span>
                            </div>
                            <div className="mt-5">
                                <ul className="nav nav-pills mb-3" id="pills-tab" role="tablist">
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link" id="pills-overview-tab" data-toggle="pill" href="#pills-overview" role="tab" aria-controls="pills-overview" aria-selected="true">Overview</a>
                                    </li>
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link" id="pills-sales-data-tab" data-toggle="pill" href="#pills-sales-data" role="tab" aria-controls="pills-sales-data" aria-selected="false" onClick={handleSalesDataChange}>Sales Data</a>
                                    </li>
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link" id="pills-sales-booking-tab" data-toggle="pill" href="#pills-sales-booking" role="tab" aria-controls="pills-sales-booking" aria-selected="false" onClick={handleBookingChange}>Sales Booking</a>
                                    </li>
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link active" id="pills-sales-order-tab" data-toggle="pill" href="#pills-sales-order" role="tab" aria-controls="pills-sales-order" aria-selected="false">Sales Order</a>
                                    </li>
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link" id="pills-sales-return-tab" data-toggle="pill" href="#pills-sales-return" role="tab" aria-controls="pills-sales-return" aria-selected="false">Sales Return</a>
                                    </li>
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link" id="pills-trade-price-tab" data-toggle="pill" href="#pills-trade-price" role="tab" aria-controls="pills-trade-price" aria-selected="false">Trade Price</a>
                                    </li>
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link" id="pills-trade-discount-tab" data-toggle="pill" href="#pills-trade-discount" role="tab" aria-controls="pills-trade-discount" aria-selected="false">Trade Discount</a>
                                    </li>
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link" id="pills-sales-budget-tab" data-toggle="pill" href="#pills-sales-budget" role="tab" aria-controls="pills-sales-budget" aria-selected="false">Sales Budget</a>
                                    </li>
                                    <li className="nav-item" role="presentation">
                                        <a className="nav-link" id="pills-report-tab" data-toggle="pill" href="#pills-report" role="tab" aria-controls="pills-report" aria-selected="false">Report</a>
                                    </li>
                                </ul>
                            </div>
                        </CardBody>
                    </Card>
                </div>
            </div>
            <div>
                <Card>
                    <CardBody>
                        <div className='row'>
                            {/* LEFT SIDE TREE ROW */}
                            <div className='col-xl-3' style={{ borderRight: "1px solid #F2F2F2" }}>
                                <div style={{ borderBottom: "1px solid #F2F2F2" }}>
                                    <label>
                                        <img src={toAbsoluteUrl("/images/loc3.png")}
                                            style={{ width: "20px", height: "20px", textAlign: "center" }}
                                            alt='Company Picture' />
                                        <strong style={{ marginLeft: "10px", color: "#828282" }}>Location (All)</strong>
                                    </label>
                                </div>
                                {/* TREE */}
                                <LocationTreeView tree={locationTree} selectLocationTreeNode={selectLocationTreeNode}/>
                            </div>
                            {/* RIGHT SIDE LIST ROW */}
                            <div className='col-xl-9'>
                                {/* SEARCHING AND FILTERING ROW */}
                                <div className="row">
                                    <div className="col-xl-3">
                                        <div style={{ position: "absolute", padding: "7px", marginTop: "3px" }}>
                                            <img src={toAbsoluteUrl("/images/search.png")} width="20px" height="20px" />
                                        </div>
                                        <form className="form form-label-right">
                                            <input type="text" className="form-control" name="searchText"
                                                placeholder="Search Here"
                                                style={{ paddingLeft: "28px" }}
                                            />
                                        </form>
                                    </div>
                                    <div className="col-xl-9 d-flex justify-content-end">
                                        <div>
                                            <button className='btn' aria-describedby={id} onClick={handleClick} style={{ color: "#828282" }}>
                                                Fiscal Year 2022-23&nbsp;
                                                <i className="bi bi-chevron-down"></i>
                                            </button>
