import React, {  useEffect, useState } from 'react';
import { showError } from "../../pages/Alert";
export default function CommonDateComponent() {

    const fields = {
        startDate: "",
        endDate: "",
        startMonth: "",
        endMonth: "",
        fromYear:"",
        toYear:""
    };

    const [inputs, setInputs] = useState(fields);
    const [type, setType] = useState("");
    const currentDate = new Date();
    let currentYear = currentDate.getFullYear();

    useEffect(() => {
        document.getElementById('dateOrMonth').style.display = "none";
        document.getElementById('fromToyear').style.display = "none";
    }, []);

    const handleChange = (event) => {
        let name = event.target.id;
        let value = event.target.value;
        setInputs(values => ({ ...values, [name]: value }))
    }


    const handleSelectTypeRange = (e) => {
        const getRadioId = document.getElementById(e.target.id);
        var radioElements = document.getElementsByClassName("all-radio");
        for (var i = 0; i < radioElements.length; i++) {
            radioElements[i].checked = false;
        }
        if (getRadioId) {

            if( e.target.id === "Date" || e.target.id === "Month" ){
                setInputs({
                    startDate: "",
                    endDate: "",
                    startMonth: "",
                    endMonth: "",
                    fromYear:"",
                    toYear:""
                })
                setType(e.target.id)
                getRadioId.checked = true;
                document.getElementById('dateOrMonth').style.display = "block";
                document.getElementById('fromToyear').style.display = "none";
            }else{
                setInputs({
                    startDate: "",
                    endDate: "",
                    startMonth: "",
                    endMonth: "",
                    fromYear:"",
                    toYear:""
                })
                setType("")
                getRadioId.checked = true;
                document.getElementById('dateOrMonth').style.display = "none";
                document.getElementById('fromToyear').style.display = "block";
            }

        }
    }
    const handleYear = (e) => {
        if(e.target.id === "fromYear"){
        let text = e.target.value;
        if(text.match(/^\d{5}$/)){
            showError('Please type 4 digits of year');
            document.getElementById('fromYear').value='';
            return false;
        }
        }else{
            let text = e.target.value;
        if(text.match(/^\d{5}$/)){
            showError('Please type 4 digits of year');
            document.getElementById('toYear').value='';
            return false;
        } 
        }
        let name = e.target.name;
        let value = e.target.value;
        setInputs(values => ({ ...values, [name]: value }))
    }
    const save = () =>{
        console.log(type)
        console.log(inputs)
    }
    return (
        <>

            <div className='row mt-5'>
                <div className='col-xl-2'>
                    <input className="all-radio mr-5" type="radio" name="day" id="Date" onClick={(e) => handleSelectTypeRange(e)} />
                    <label className='level-title'>Day</label>
                </div>
                <div className='col-xl-2'>
                    <input className="all-radio mr-5" type="radio" name="month" id="Month" onClick={(e) => handleSelectTypeRange(e)} />
                    <label className='level-title'>Month</label>
                </div>
                <div className='col-xl-2'>
                    <input className="all-radio mr-5" type="radio" name="year" id="year" onClick={(e) => handleSelectTypeRange(e)} />
                    <label className='level-title'>Year</label>
                </div>
            </div>
            <div id="dateOrMonth">
            <div className='mt-5'>
            <label className='level-title'>Start {type}</label>
                    <input className='form-control' type={type} id={"start"+type}
                        onChange={handleChange}  />
            </div>
            <div className='mt-5'>
            <label className='level-title'>End {type}</label>
                    <input className='form-control' type={type}  id={"end"+type}
                         onChange={handleChange} />
            </div>
            </div>

           <div id="fromToyear">
           <div className='mt-5'>
            <label className='level-title'>Start Year</label>
                    <input className='form-control' type="number" id="fromYear"
                        name="fromYear" min="1900" max={currentYear}  onChange={(e)=>handleYear(e)}/>
            </div>
            <div className='mt-5'>
            <label className='level-title'>End Year</label>
                    <input className='form-control' type="number" id="toYear"
                        name="toYear" min="1900" max={currentYear}   onChange={(e)=>handleYear(e)}/>
            </div>
           </div>
           {/* <button onClick={save}>save</button> */}
        </>
    );
}
