import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../../../_metronic/_helpers";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import { useIntl } from "react-intl";
import ReactStars from "react-rating-stars-component";
import { ActionsColumnFormatter } from "./ActionsColumnFormatter";
import {
  sortCaret,
  headerSortingClasses,
} from "../../../../../../_metronic/_helpers";
const data = [
    {
        "id": 1,
        "code": "Lay's Seed Enterprize",
        "name": "Bhai Bhai Enterprise",
        "status": "active",
        "user": "Mmh Shohagh",
        "type":"silver",
        "collectionamount": "৳570,168,000",
      },
      {
        "id": 2,
        "code": "Lay's Seed Enterprize",
        "name": "Bhai Bhai Enterprise",
        "status": "active",
        "user": "Mmh Shohagh",
        "type":"gold",
        "collectionamount": "৳570,168,000",
      },
      {
        "id": 3,
        "code": "Lay's Seed Enterprize",
        "name": "Bhai Bhai Enterprise",
        "status": "inactive",
        "user": "Mmh Shohagh",
        "type":"platinum",
        "collectionamount": "৳570,168,000",
      },

]


export function DistributorsListView({setSingleAll, singleAll, }) {
  let history = useHistory();
  const intl = useIntl();
  const [deleteId, setDeleteId] = useState(0);
  const openViewPage = (data) => {
    history.push('/salescollection/sales/sales-return-view', { state: data });
  }
  const openEditPage = (data) => {

        history.push('/salescollection/sales/sales-order', { state: data });
    }
  const singleWiseSelectHandler = (data, isSelect) => {
    if (isSelect == true) {
      let temp = [...singleAll]
      temp.push(data)
      setSingleAll(temp)
    } else {
      if (singleAll.length >= 0) {
        let temp = [...singleAll]
        const index = temp.findIndex(obj => obj.id == data.id);
        temp.splice(index, 1);
        setSingleAll(temp)
      }
    }

  }

  const allSelectHandler = (allData, isSelect) => {
    if (isSelect == true) {
      setSingleAll(allData)
    }
    else {
      if (allData.length >= 0) {
        for (let i = 0; i < allData.length; i++) {
          const index = singleAll.findIndex(obj => obj.id == allData[i].id);
          singleAll.splice(index, 1);
          setSingleAll(singleAll)
        }
      }
    }
  }

  const columns = [
    {
      dataField: "distributor",
      text: intl.formatMessage({id: "PAYMENT.COLLECTION.ORD_DISTRIBUTOR"}),
      sort: true,
      sortCaret: sortCaret,
      headerSortingClasses,
      formatter: (cellContent, row) => (
        <>
          <>
          <div className="d-flex" style={{ marginBottom: "-10px" }}>
            <div>
              <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/Lays.svg")} width="50px" height="50px" />
            </div>
            {row.status=="active"?<div className="circel-active" ></div>:""}
            <div className="ml-3">
              <span>
                <span style={{ fontWeight: "500" }}><strong>{row.code}</strong></span>
                <p className="dark-gray-color"> 
                  <span className="text-muted">
                  <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/CreditLimit.svg")} width="15px" height="15px" />&nbsp;
                    Credit Limit</span>&nbsp;{row.collectionamount}
                </p>
              </span>
            </div>
          </div>
        </>
        </>
      )
    },

    {
      dataField: "rating",
      text: intl.formatMessage({id: "COMMON.RATING"}),
      sort: true,
      sortCaret: sortCaret,
      headerSortingClasses,
      formatter: (cellContent, row) => (
        <>
        <span className="text-warning">{row.type}</span>
          <ReactStars  value={row.type == "silver"?3:row.type=="gold"?4:5} edit={false} readonly/>
        </>
      )
    },
    {
      dataField: "action",
      text: intl.formatMessage({id: "MENU.ACTIONS"}),
      formatter: ActionsColumnFormatter,
      formatExtraData: {
        openViewPage: openViewPage,
        openEditPage: openEditPage,
      },
      classes: "text-center",
      headerClasses: "text-center",
      style: {
        minWidth: "100px",
      },
    },
  ];

  return (
    <>
      <BootstrapTable
        wrapperClasses="table-responsive"
        classes="table table-head-custom table-vertical-center overflow-hidden"
        bootstrap4
        bordered={false}
        keyField="id"
        data={data}
        columns={columns}
        sort={{ dataField: 'distributor_name', order: 'asc' }}
        selectRow={
          {
            mode: 'checkbox',
            onSelect: (row, isSelect, rowIndex, e) => {
              singleWiseSelectHandler(row, isSelect);
            },
            onSelectAll: (isSelect, rows, e) => {
              allSelectHandler(rows, isSelect);

            }

          }
        }

        pagination={paginationFactory({ sizePerPage: 10, showTotal: true })}
      />
    </>
  );
}
