                            </div>

                            {/* SELECTED ROW */}
                            <div className="col-xl-8 text-right">
                                {/* STATUS DROPDOWN */}
                                <span className="mr-3 display-inline-block">
                                    <div className="row">
                                        <div className="col-3 mt-3">
                                            <label className="dark-gray-color">Status</label>
                                        </div>
                                        <div className="col-9">
                                            {/* <select className="border-0 form-control">
                                                    <option value="1" selected>Pending</option>
                                                    <option value="2">Pending</option>
                                                    <option value="3">Pending</option>
                                                </select> */}
                                            <select className="border-0 form-control" name="status" onChange={(e) => selectStatus(e)}>
                                                <option value="" className="fs-1">All</option>
                                                {
                                                    allStatus.map((status) => (
                                                        <option key={status.code} value={status.code} className="fs-1">{status.name}</option>
                                                    ))
                                                }
                                            </select>
                                        </div>
                                    </div>
                                </span>
                                {/* LOCATION DROPDOWN */}
                                <span className="mr-3 display-inline-block">
                                    <div className="row">
                                        <div className="col-3 mt-3">
                                            <label className="dark-gray-color">Location</label>
                                        </div>
                                        <div className="col-9">
                                            <select className="border-0 form-control" name="location" id="location" onChange={(e) => selectLocation(e)}>
                                                <option value="" selected>All</option>
                                                {location && location.map((data) =>
                                                    (<option key={data.id} value={data.id}>{data.name}</option>)
                                                )}
                                            </select>
                                            {/* <select className="border-0 form-control">
                                                <option value="1" selected>Chittangong</option>
                                                <option value="2">Chittangong</option>
                                                <option value="3">Chittangong</option>
                                            </select> */}
                                        </div>
                                    </div>
                                </span>
                                {/* TIMELINE DROPDOWN */}
                                <span className="mr-3 display-inline-block">
                                    <div className="row">
                                        <div className="col-3 mt-3">
                                            <label className="dark-gray-color">Timeline</label>
                                        </div>
                                        <div className="col-9">
                                            <select className="border-0 form-control" name="fiscalYear" onChange={(e)=>selectFiscalYear(e)}>
                                                <option value="" className="fs-1">Select Fiscal Year</option>
                                                {
                                                    allFiscalYear.map((fiscalYear)=>(
                                                        <option key={fiscalYear.id} value={fiscalYear.id} className="fs-1">{fiscalYear.fiscalYearName}</option>
                                                    ))
                                                }
                                            </select>
                                            {/* <select className="border-0 form-control">
                                                <option value="1" selected>Fiscal Year 2022-23</option>
                                                <option value="2">Fiscal Year 2022-23</option>
                                                <option value="3">Fiscal Year 2022-23</option>
                                            </select> */}
                                        </div>
                                    </div>
                                </span>
                            </div>

                            {/* FILTER BUTTON ROW */}
                            <div className="col-xl-1">
                                <button className="btn filter-btn">
                                    <i className="bi bi-funnel" style={{ fontSize: "11px" }}></i>&nbsp;Filter
                                </button>
                            </div>
                        </div>

