import React, {useEffect, useState} from 'react';
import Popper from '@material-ui/core/Popper';
import SVG from "react-inlinesvg";
import {toAbsoluteUrl} from "../../../../_metronic/_helpers";
import {SalesList} from "./sales-table/SalesList";
import {Card, CardBody} from "../../../../_metronic/_partials/controls";
import {useHistory} from 'react-router-dom';
import LocationTreeView from '../../SalesCollection/CommonComponents/LocationTreeView';
import {showError} from '../../../pages/Alert';
import axios from "axios";
import {useIntl} from "react-intl";

export function SalesData() {
    const intl = useIntl();
    const [locationTree, setLocationTree] = useState([]);
    const [sessionData, setSessionData] = useState({userLoginId: 1, companyId: 1, accountingYearId: 1});
    const [searchParams, setSearchParams] = useState(sessionData);
    const [selectedLocation, setSelectedLocation] = useState({});
    const [total, setTotal] = useState({totalQuantity: 0, totlaSalesAmount: 0});
    const [salesDataList, setSalesDataList] = useState([]);

    let history = useHistory();
    const [anchorEl, setAnchorEl] = React.useState(null);
    let [singleAll, setSingleAll] = React.useState([]);

    const handleClick = (event) => {
        setAnchorEl(anchorEl ? null : event.currentTarget);
    };
    const open = Boolean(anchorEl);
    const id = open ? 'simple-popper' : undefined;

    useEffect(() => {
        getLocationTreeList(sessionData);
    }, []);

    useEffect(() => {
        getSalesDataList(searchParams);
    }, [searchParams]);


    const handleSalesDataChange = () => {
        history.push("/salescollection/sales/slaes-data")
    }
    const handleBookingChange = () => {
        history.push("/salescollection/sales/slaes-booking")
    }
    const handleOrderChange = () => {
        history.push("/salescollection/sales/slaes-order")
    }
    const handleExport = () => {
        const exportData = [...singleAll]
        console.log(exportData)
    }

    const getSalesDataList = (params) => {
        let queryString = '?';
        queryString += 'userLoginId=' + params.userLoginId;
        queryString += '&companyId=' + params.companyId;
        queryString += '&accountingYearId=' + params.accountingYearId;
        queryString += params.locationId ? '&locationId=' + params.locationId : '';
        const URL = `${process.env.REACT_APP_API_URL}/api/sales-data/locationWise` + queryString;
        axios.get(URL).then(response => {
            const dataList = response.data.data.salesData;
            getTotal(dataList);
            setSalesDataList(dataList);
        }).catch(err => {
            showError("Cannot get Sales Data.");
        });
    }

    const getLocationTreeList = (params) => {
        const URL = `${process.env.REACT_APP_API_URL}/api/location-tree/locationTree/${params.userLoginId}/${params.companyId}`;
        axios.get(URL).then(response => {
            const locationTree = response.data.data;
            setLocationTree(locationTree);
        }).catch(err => {
            showError("Cannot get Location Tree data.");
        });
    }

    const getTotal = (dataList) => {
        let totalQuantity = 0;
        let totlaSalesAmount = 0;
        dataList.map(sale=>{
            totalQuantity += sale.quantity;
            totlaSalesAmount += sale.sale_amount;
        });
        setTotal({totalQuantity: totalQuantity, totlaSalesAmount: totlaSalesAmount})
    }

    const selectLocationTreeNode = (node) => {
        setSelectedLocation(node);
        setSearchParams({...searchParams, locationId: node.id});
    }
    return (
        <>
            <div>
                {/* BREAD CRUM ROW */}
                <div className="row" style={{marginTop: "-30px", marginLeft: "-18px"}}>
                    <div className="col-xl-6">
                        <nav aria-label="breadcrumb">
                            <ol className="breadCrum-bg-color">
                                <li aria-current="page" className='breadCrum-main-title'>Google</li>
                                <li aria-current="page" className='mt-1 breadCrum-sub-title'>&nbsp;Sales &
                                    Collection&nbsp;&nbsp;&nbsp;&nbsp;</li>
