import React, { useState, useEffect } from 'react';
import Chart from "react-apexcharts";
import { useHistory } from "react-router-dom";
import { useIntl } from 'react-intl';
import CollectionBreadCrum from "../../common/CollectionBreadCrum";
import CollectionTodaySale from "../../common/CollectionTodaySales";
import {
  Card,
  CardBody,
} from "../../../../../../_metronic/_partials/controls";
import OrdCalculatorList from "./table/OrdCalculatorList"
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../../../_metronic/_helpers";

const state = {
  series: [33000000, 888000000],
  options: {
    labels: ["Save", "Total Suggested Payment"],
    chart: {
      show: true,
      type: "donut",
    },
    colors: ['#6FCF97', '#56CCF2'],
    plotOptions: {
      pie: {
        donut: {
          labels: {
            show: true,
            total: {
              showAlways: true,
              label: "Total Sales Amount",
              color: '#56CCF2',
              show: true
            }
          }
        }
      }
    },
    dataLabels: {
      enabled: false,
    },
    fill: {
      type: "gradient"
    },
    legend: {
      offsetY: 50,
      fontSize: '14px',
      fontFamily: 'Helvetica, Arial',
      fontWeight: 400,
      labels: {
        colors: ['#6FCF97', '#56CCF2'],
        useSeriesColors: true
      },
      formatter: function (val, opts) {
        let data = opts.w.globals.series[opts.seriesIndex];
        return val + " <br /> " + data;
      },
    },
    title: {
      text: "12 January,2022(THU)",
      align: 'right',
      offsetX: -30,
      offsetY: 80,
    },
    responsive: [
      {
        breakpoint: 480,
        options: {
          chart: {
            width: 200
          },
          legend: {
            position: "bottom"
          }
        }
      }
    ]
  }
};
export function Calculator() {
  const history = useHistory();
  const intl = useIntl();
  const [selectDate, setSelectDate] = useState("");
  const [area, setArea] = useState("Chittagong");
  const [depotName, setDepotName] = useState("Chittagong");
  const [locationTeritory, setLocationTeritory] = useState("Cox’s Bazar");
  const [distributorName, setDistributorName] = useState("Bhai Bhai Enterprise");
  const [distributorContactNo, setDistributorContactNo] = useState("+880 1779 911 488");
  const [balance, setBalance] = useState(55606521);

  useEffect(() => {
    document.getElementById('pills-payment-ord-calculator-tab').classList.add('active')
  }, []);
  const onChange = (value) => {
    setSelectDate(value);
  };
  const handleBackToOrdCalculatorPage = () => {
    history.push("/salescollection/payment-collection/ord-calculator")
  }
  return (
    <>
      <div>
        {/* BREAD CRUM ROW */}
        <CollectionBreadCrum />
        {/* TODAY  ROW */}
        <CollectionTodaySale />
      </div>
      {/* CONTENT ROW */}
      <Card style={{ borderRadius: "25px" }}>
        <CardBody>
          <div className='row'>
            {/* CONTENT LEFT SIDE ROW */}
            <div className='col-xl-8'>
              {/* TITLE ROW */}
              <div>
                <span className=" mr-5">
                  <button className='btn' onClick={handleBackToOrdCalculatorPage}>
                    <strong>
                      <i className="bi bi-arrow-left-short sales-booking-view-icon"></i>
                    </strong>
                  </button>
                </span>
                <span className="sales-booking-view-span mr-5">
                  <strong style={{ fontSize: "13px" }}>{intl.formatMessage({ id: "PAYMENT.ORD.CALCULATOR.TITLE" })}</strong>
                </span>
              </div>
              {/* CHART ROW */}
              <div>
                <div className="app">
                  <div className="row ord-calculator">
                    <div className="mixed-chart">
                      <Chart
                        options={state.options}
                        series={state.series}
                        type="donut"
                        width="500"
                      />
                    </div>
                  </div>
                </div>
              </div>
              {/* CARD ROW */}
              <div className='mt-5 order-table'>
                <OrdCalculatorList />
              </div>
              <div className='mt-5 order-table'>
                <OrdCalculatorList />
              </div>
              <div className='mt-5 order-table'>
                <OrdCalculatorList />
              </div>
            </div>
            {/* CONTENT RIGHT SIDE ROW */}
            <div className='col-xl-4 mt-5 order-table ord-calculator-calendar p-5'>
              {/* TITLE ROW */}
              <div className='mt-5'>
                <strong className="text-success">Pick an Expected Payment Date*</strong>
              </div>
              {/* CALENDAR ROW */}
              <div className="react-calendar mt-5">
                <Calendar onClickDay={onChange} value={selectDate} required />
              </div>
              {/* INFO ROW */}
              <div className='ord-calculator-info mt-5 p-5'>
                <div className='row mt-5'>
                  <div className='col-xl-8'>
                    <div className="d-flex mr-5">
                      <div>
                        <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/Lays.svg")} width="35px" height="35px" />
                      </div>
                      <div className="ml-3">
                        <span>
                          <span style={{ fontWeight: "500" }}><strong>{distributorName}</strong></span>
                          <p className="dark-gray-color">
