import React, { createContext, useContext, useState, useCallback } from "react";
import { isEqual, isFunction } from "lodash";
import { initialFilter } from "./AccountingYearUIHelpers";

const AccountingYearUIContext = createContext();

export function useAccountingYearUIContext() {
  return useContext(AccountingYearUIContext);
}

export const AccountingYearUIConsumer = AccountingYearUIContext.Consumer;

export function AccountingYearUIProvider({ accountingYearUIEvents, children }) {
  const [queryParams, setQueryParamsBase] = useState(initialFilter); 
  const setQueryParams = useCallback((nextQueryParams) => {
    setQueryParamsBase((prevQueryParams) => {
      if (isFunction(nextQueryParams)) {
        nextQueryParams = nextQueryParams(prevQueryParams);
      }

      if (isEqual(prevQueryParams, nextQueryParams)) {
        return prevQueryParams;
      }

      return nextQueryParams;
    });
  }, []);

  const value = {
    queryParams,
    setQueryParamsBase,
    setQueryParams,
    newAccountingYearButtonClick: accountingYearUIEvents.newAccountingYearButtonClick,
    openEditAccountingYearPage: accountingYearUIEvents.openEditAccountingYearPage,
    openDeleteAccountingYearDialog: accountingYearUIEvents.openDeleteAccountingYearDialog,
  };

  return (
    <AccountingYearUIContext.Provider value={value}>
      {children}
    </AccountingYearUIContext.Provider>
  );
}
