import { Card } from "@material-ui/core";
import React, { useState } from "react";
import { CardBody } from "../../../../_metronic/_partials/controls";
import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../_metronic/_helpers";
import ApprovalPathBreadCrum from "../bread-crum/ApprovalPathBreadCrum";

export default function AuthorizationFeature() {
    const [approvalNextList, setApprovalNextList] = useState([])
    const [approvalPreList, setApprovaPreList] = useState([])
    const [approvalList, setApprovalList] = useState([
        {
            "id": 1,
            "name": "Proposer"
        },
        {
            "id": 2,
            "name": "Recommender"
        },
        {
            "id": 3,
            "name": "Recommender Orther"
        },
        {
            "id": 4,
            "name": "Reason"
        },
        {
            "id": 5,
            "name": "Approver"
        }
    ])

    const [approvalObj, setApprovalObj] = useState([
        {
            id: '',
            companyId: '',
            locationId: '',
            salesOfficerId: ''
        },
    ]);

    const handleChange = (e) =>{
       let temp = [...approvalList]
       let index = temp.findIndex((obj)=>obj.id === parseInt(e.target.value))
       temp.splice(index,1)
       setApprovalNextList(temp)
    }

    const handleNextState = (e) => {
       const temp = [...approvalNextList]
       const index = temp.findIndex((obj)=>obj.id === parseInt(e.target.value))
       temp.splice(index,1)
       setApprovaPreList(temp)
    }

    const removeApproval = (i) => {
        // const id = "final-state-id-"+i
        // const getId = document.getElementById(id).checked
        // console.log(getId)
        let newFormValues = [...approvalObj];
        newFormValues.splice(i, 1);
        setApprovalObj(newFormValues)
    }

    const addApprovalFields = () => {
        setApprovalObj([...approvalObj, {
            company: '',
            location: '',
            representative: ''
        }])
    }

    const handleFinalState = (e,i) => {
        if(e.target.checked){
            document.getElementById("next-layer-id"+i).classList.add('d-none')
            document.getElementById("more-btn-id").classList.add('d-none')
            document.getElementById("save-btn-id").classList.remove('d-none')
        }else{
            document.getElementById("next-layer-id"+i).classList.remove('d-none')
            document.getElementById("more-btn-id").classList.remove('d-none')
            document.getElementById("save-btn-id").classList.add('d-none') 
        }
    }
    return (
        <>
        {/* BREAD CRUM ROW */}
        <ApprovalPathBreadCrum menuTitle="Authorization Feature" />
            <Card>
                <CardBody>
                    {approvalObj.map((element, index) => (
                        <div className="row mt-5" key={index}>
                            <div className='col-xl-12 guarantee-cheque-div'>
                                <span>Approval to({index + 1})</span>
                            </div>
                            <div className="form-group col-xl-12 mt-5 float-right">
                                {
                                    index === 0 ? ""
                                        :
                                        <button
                                            type="button"
                                            className="btn btn-sm float-right"
                                            onClick={() => removeApproval(index)}
                                        >
                                            <i style={{ marginLeft: "-15px", fontSize: "16px" }} class="bi bi-trash3-fill text-danger"></i>
                                        </button>
                                }
                            </div>

                            {/* COMPANY AND LOCATION ROW */}
                            <div className="col-xl-12">
                            <div className="row">
                        <div className="col-xl-3">
                            <select id={"approval-id-"+index} className="form-control" onChange={(e)=>handleChange(e)}>
                                <option value="">Select Approval Steps</option>
                                {
                                   approvalList.map((approval,index)=>(
                                    <option key={index} value={approval.id}>{approval.name}</option>
                                   )) 
                                }
                            </select>
                        </div>
                        <div className="col-xl-3 mt-3 text-center">
                            {
                                index === 0 ?
                                <div class="form-check form-check-inline mr-5">
                                <input class="form-check-input" type="checkbox" id="first-state-id" value="FIRST"  checked/>
                                <label class="form-check-label" for="first-state-id">First State</label>
                            </div>:""
                            }
                            {
                                approvalObj.length-1 === index?
                                <div class="form-check form-check-inline">
                                <input class="form-check-input" type="checkbox" id={"final-state-id-"+index} value="FINAL" onChange={(e)=>handleFinalState(e,index)}/>
                                <label class="form-check-label" for={"final-state-id-"+index}>Final State</label>
                            </div>:""
                            }

                        </div>
                        <div className="col-xl-3">
                            <select id={"next-layer-id"+index} className="form-control" onClick={(e)=> handleNextState(e)}>
                                <option value="">Select Approval Next Steps</option>
                                {
                                    approvalNextList.map((next,index)=>(
                                        <option value={next.id}>{next.name}</option>
                                    ))
                                }
                            </select>
                        </div>
                        <div className="col-xl-3">
                            {
                                index === 0 ? "":
                                <select id={"pre-layer-id"+index} className="form-control">
                                <option value="">Select Approval Previous Steps</option>
                                {
                                    approvalPreList.map((pre,index)=>(
                                        <option value={pre.id}>{pre.name}</option>
                                    ))
                                }
                            </select>
                            }
                        </div>
                    </div>
                            </div>
                        </div>
                    ))}
                    <div className='col-xl-12 text-center mt-5'>
                        <button
                            id="more-btn-id"
                            type="button"
                            className="btn text-white bg-success w-100"
                            onClick={() => addApprovalFields()}
                        >
                            <SVG className="mr-2" src={toAbsoluteUrl("/media/svg/icons/project-svg/more.svg")} />More Approval Set
                        </button>
                        <button
                            id="save-btn-id"
                            type="button"
                            className="btn bg-primary w-100 text-white d-none"
                        >
                            <SVG className="mr-2" src={toAbsoluteUrl("/media/svg/icons/project-svg/more.svg")} />Save
                        </button>
                    </div>
                </CardBody>
            </Card>
        </>
    );
}
