import React, { useEffect, useState } from "react";
import { Card } from "react-bootstrap";
import Button from '@material-ui/core/Button';
import { CardBody } from "../../../../../_metronic/_partials/controls";
import SalesCollectionConfigureBreadCrum from "../common/SalesCollectionConfigureBreadCrum";
import Autocomplete from "@material-ui/lab/Autocomplete";
import { TextField } from "@material-ui/core";
import { NavLink } from "react-router-dom";
import { showError, showSuccess } from "../../../../pages/Alert";
import { shallowEqual, useSelector } from 'react-redux';
import axios from "axios";
import { allowOnlyNumericWithPeriod } from '../../../Util';
const fields = {
    distributorId: "",
    semesterId: "",
    creditLimit: "",
    companyId:""
}
export default function CreditLimitAdd() {
    const companyId = useSelector((state) => state.auth.company, shallowEqual);
    const [distributors, setDistributors] = useState([]);
    const [inputs, setInputs] = useState(fields);
    const [allFiscalYear, setAllFiscalYear] = useState([]);
    const [allSemester, setAllSemester] = useState([]);
    const [distributorValue, setDistributorValue] = React.useState(null);

    useEffect(() => {
        getAllFiscalYear(companyId);
    }, [companyId])

    const getAllFiscalYear = (companyId) => {
        const URL = `${process.env.REACT_APP_API_URL}/api/accounting-year/companyWise/${companyId}`;
        axios.get(URL).then(response => {
            setAllFiscalYear(response.data.data);
        });
    }

    const getAllSemester = (fiscalYearId) => {
        console.log(fiscalYearId)
        const URL = `${process.env.REACT_APP_API_URL}/api/accounting-year/get-with-semesters-by-id/${fiscalYearId}`;
        axios.get(URL).then(response => {
            setAllSemester(response.data.data.semesterList);
        });
    }
    const getAllDistributorList = () => {
        const URL = `${process.env.REACT_APP_API_URL}/api/distributor/get-distributor-without-credit-limit/${companyId}`;
        axios.get(URL).then(response => {
            console.log(response.data.data)
            setDistributors(response.data.data);
        });
    }

    const handleFiscalYearChange = (e) =>{
        if(e.target.value === ""){
            setAllSemester([]) 
        }else{
            getAllSemester(e.target.value)
        }
    }

    const handleSemesterChange = (e) =>{
        if (e.target.value !== null || e.target.value !== "") {
            setInputs({ ...inputs, semesterId: parseInt(e.target.value) });
        }
    }

    const handleChangeDistributor = (distributorId) => {
        if (distributorId !== null || distributorId !== "") {
            setInputs({ ...inputs, distributorId: distributorId });
        }
    }

    const handleChangeCreditLimit = (e) => {
        if (e.target.value !== null || e.target.value !== "") {
            setInputs({ ...inputs, creditLimit: e.target.value });
        }

    }
    const validate = () => {
        const getFiscalYearId = document.getElementById("fiscalYearId").value;
        if (!getFiscalYearId) {
            showError('Fiscal Year is required.');
            return false;
        }

        if (!inputs.semesterId) {
            showError('Semester is required.');
            return false;
        }

        if (!inputs.distributorId) {
            showError('Distributor is required.');
            return false;
        }

        if (!inputs.creditLimit) {
            showError('Credit limit amount is required.');
            return false;
        }

        return true;
    }
    const saveLimit = () =>{
        if(!validate()){
            return false;
        }
        let obj = {}
        obj = {...inputs, companyId:companyId}
        const URL = `${process.env.REACT_APP_API_URL}/api/credit-limit`;
        axios.post(URL, JSON.stringify(obj), { headers: { "Content-Type": "application/json" } }).then(response => {
            if (response.data.success === true) {
                document.getElementById("fiscalYearId").value = "";
                document.getElementById("semesterId").value = "";
                document.getElementById("distributorId").value = "";
                document.getElementById("creditLimit").value = "";
                getAllDistributorList();
                setDistributorValue(null)
                showSuccess(response.data.message);
            } else {
                showError(response.data.message);
            }
        }).catch(err => {
            showError(err);
        });

    }
    const handlePaste = (e) => {
        e.preventDefault()
        return false;
    }
    return (
        <>
            {/* BREAD CRUM ROW */}
            <SalesCollectionConfigureBreadCrum menuTitle="Credit Limit Setup" />

            <Card>
                <CardBody>
                    <div className="row">
                        <div className="col-lg-12">
                            <span className="create-field-title">Credit Limit Setup</span>

                            <NavLink className="float-right" to="/salescollection/configure/credit-limit-setup/credit-limit-setup">
                                <span className="mt-5"><i className="bi bi-chevron-left"></i></span>Back
                            </NavLink>

                            <div className='mt-5 row'>
                                <div className='col-xl-6'>
                                    <div>
                                    <label className='level-title'><span className="mr-1">Fiscal Year</span><span className="text-danger ">*</span></label>
                                        <select className="form-control" id="fiscalYearId"  name="fiscalYearId" onChange={(e)=>handleFiscalYearChange(e)}>
                                            <option value="">Please Select Fiscal Year</option>
                                            {
                                                allFiscalYear.map((fiscalYear)=>(
                                                <option key={fiscalYear.id} value={fiscalYear.id}>{fiscalYear.fiscalYearName}</option> 
                                                ))
                                            }   
                                        </select>
                                    </div>
                                </div>

                                <div className='col-xl-6'>
                                    <div>
                                    <label className='level-title'><span className="mr-1">Semester</span><span className="text-danger ">*</span></label>
                                        <select className="form-control" id="semesterId"  name="semesterId" onChange={(e)=>handleSemesterChange(e)}>
                                            <option value="">Please Select Semester</option> 
                                            {
                                                allSemester.map((semester)=>(
                                                <option key={semester.id} value={semester.id}>{semester.semesterName}</option> 
                                                ))
                                            } 
                                        </select>
                                    </div>
                                </div>
                            </div>

                            <div className='mt-5 row'>
                                <div className='col-xl-6'>
                                    <Autocomplete
                                        id="distributorId"
                                        name="distributorId"
                                        options={distributors}
                                        onKeyDown={getAllDistributorList}
                                        value={distributorValue}
                                        getOptionLabel={(option) => option.distributorName}
                                        onChange={(event, newValue) => {
                                            handleChangeDistributor(newValue.distributorId)
                                            setDistributorValue(newValue)
                                        }}
                                        renderInput={(params) => (
                                            <TextField {...params} label="Select Distributor*" />
                                        )}
                                    />
                                </div>

                                <div className='col-xl-6'>
                                    <label className='level-title'><span className="mr-1">Credit Limit Amount</span><span className="text-danger ">*</span></label>
                                    <input maxLength={15} onPaste={(e)=>handlePaste(e)}  onChange={(e)=>handleChangeCreditLimit(e)} onKeyPress={(e) => allowOnlyNumericWithPeriod(e)} id="creditLimit" name="creditLimit" type="text" className='form-control' />
                                </div>
                            </div>

                            <Button className="float-right mt-5" id="gotItBtn" variant="contained" color="primary"  onClick={saveLimit}>
                                Save
                            </Button>

                        </div>
                    </div>
                </CardBody>
            </Card>
        </>);
}
