import React from "react";
import { useHistory } from "react-router-dom";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import { ActionsColumnFormatter } from "./ActionsColumnFormatter";
import {useIntl} from "react-intl";
const data = [
  {
    "id": 1,
    "code": "12535689",
    "store":"Regular to Restricted",
    "dateTime": "10 APR 2022 10:23 AM",
    "user": "Mmh Shohagh",
    "designation": "Depot Incharge, Dhaka Depot",
    "qty": "55,570",
    "uom": "UOM 8,578 Kg",
    "status": "WAITING"
  },
  {
    "id": 2,
    "code": "12535689",
    "store":"Quarantine to Restricted",
    "dateTime": "10 APR 2022 10:23 AM",
    "user": "Mmh Shohagh",
    "designation": "Depot Incharge, Dhaka Depot",
    "qty": "55,570",
    "uom": "UOM 8,578 Kg",
    "status": "MOVED"
  },
  {
    "id": 3,
    "code": "12535689",
    "store":"Restricted to Regular",
    "dateTime": "10 APR 2022 10:23 AM",
    "user": "Mmh Shohagh",
    "designation": "Depot Incharge, Dhaka Depot",
    "qty": "55,570",
    "uom": "UOM 8,578 Kg",
    "status": "REJECT"
  } 
]
export default function StoreMovementList({setSingleAll, singleAll}) {
  const intl = useIntl();
  let history = useHistory();

  const openWaitingPage = (data) => {
    // history.push('/proposal-received', { state: data.batchList });
  }

  const singleWiseSelectHandler = (data, isSelect) => {
    if (isSelect == true) {
      let temp = [...singleAll]
      temp.push(data)
      setSingleAll(temp)
    } else {
      if (singleAll.length >= 0) {
        let temp = [...singleAll]
        const index = temp.findIndex(obj => obj.id == data.id);
        temp.splice(index, 1);
        setSingleAll(temp)
      }
    }

  }

  const allSelectHandler = (allData, isSelect) => {
    if (isSelect == true) {
      setSingleAll(allData)
    }
    else {
      if (allData.length >= 0) {
        for (let i = 0; i < allData.length; i++) {
          const index = singleAll.findIndex(obj => obj.id == allData[i].id);
          singleAll.splice(index, 1);
          setSingleAll(singleAll)
        }
      }
    }
  }

  const columns = [
    {
      dataField: "sales_officer",
      text: "STOCK MOVEMENT INFO",
      formatter: (cellContent, row) => (
        <>
          <span className="text-muted">{row.code}</span>
          <span 
          className={
            row.status === "WAITING"?"dark-gray-color":
            row.status === "MOVED"?"dark-success-color":
            row.status === "REJECT"?"dark-danger-color":""
          }
          >({row.status})</span><br />
          <strong>{row.store}</strong><br />
          <span className="text-muted">{row.dateTime}</span>
        </>
      )
    },
    {
      dataField: "code",
      text:"BY",
      formatter: (cellContent, row) => (
        <>
          <strong>{row.user}</strong><br />
          <span className="text-muted">{row.designation}</span>
        </>
      )
    },
    {
      dataField: "collection_amount",
      text: "MOVED QTY.",
      headerClasses:"text-right",
      classes:"text-right",
      formatter: (cellContent, row) => (
        <>
          <strong>{row.qty}</strong><br />
          <span className="text-muted">{row.uom}</span>
        </>
      )
    },
    {
      dataField: "action",
      text: intl.formatMessage({id: "MENU.ACTION"}),
      formatter: ActionsColumnFormatter,
      formatExtraData: {
        openWaitingPage: openWaitingPage,
      },
      classes: "text-center",
      headerClasses: "text-center",
      style: {
        minWidth: "100px",
      },
    },
  ];

  return (
    <>
      <BootstrapTable
        wrapperClasses="table-responsive"
        classes="table table-head-custom table-vertical-center overflow-hidden"
        bootstrap4
        bordered={false}
        keyField="id"
        data={data}
        columns={columns}
        selectRow={
          {
            mode: 'checkbox',
            onSelect: (row, isSelect, rowIndex, e) => {
              singleWiseSelectHandler(row, isSelect);
            },
            onSelectAll: (isSelect, rows, e) => {
              allSelectHandler(rows, isSelect);

            }

          }
        }

        pagination={paginationFactory({ sizePerPage: 10, showTotal: true })}
      />
    </>
  );
}
