import React from "react";
import { toAbsoluteUrl } from "../../../../../../../../../_metronic/_helpers";
import SVG from "react-inlinesvg";
import { Card } from "react-bootstrap";
import { CardBody } from "../../../../../../../../../_metronic/_partials/controls";
import { allowOnlyNumeric } from "../../../../../../../Util";
import { showError } from "../../../../../../../../pages/Alert";
export default function BatchList({batchList, handleTransfer, selectedProductInfo, handleTransferQty, cartList, setCartList}) {

    const handleTransferQuantity = (batch,i) => {

        const getValue = document.getElementById('batch-qty-id'+i).value

        if(getValue > batch.stock_quantity) {
            showError("Transfer Quantity Exceeds Stock Quantity")
            document.getElementById('batch-qty-id'+i).value='';
            return false;
        }
        if(getValue === undefined || getValue === "") {

            batch.addToCart = false
        } else {
            batch.transferQuantity =getValue
            //batch.addToCart = true
        }

    } 
    return (
        <>
            {batchList.map((batch, index) => (
                <Card className="mt-5  pt-3 ">
                    <CardBody>
                        {/* BATCH INFO ROW */}
                        <div>
                            <span className="float-right">
                                {
                                    batch.addToCart ?
                                    <span id={"transferred-id-" + index} className="float-right light-success-bg dark-success-color p-3 mt-n3 rounded">Transferred</span> :
                                    <span id={"transfer-id-" + index} className="float-right">
                                        <button className="btn text-white float-right mt-n3" style={{ background: "#6FCF97" }} onClick={(e) => handleTransfer(batch, selectedProductInfo,index,e)}>
                                            <SVG src={toAbsoluteUrl("/media/svg/icons/project-svg/white-receive.svg")} />
                                            &nbsp;Transfer
                                        </button>
                                    </span>
                                }
                            </span>
                            <span>
                                <span className="text-muted mr-2">Batch No:</span>
                                <strong>{batch.batch_no}</strong>
                            </span><br />
                            <span>
                                <span className="text-muted mr-2">Batch Qty:</span>
                                <strong>{batch.batchQuantity}</strong>
                            </span><br />
                            <span>
                                <span className="text-muted mr-2">Stock Qty:</span>
                                <strong>{batch.stock_quantity}</strong>
                            </span>
                        </div>
                        {/* PROPOSED QTY ROW */}
                        <div className="mt-5 row">
                            <div className="col-6 mt-5">
                                <span>
                                    <input type="text" required id={"batch-qty-id"+index} onKeyPress={(e) => allowOnlyNumeric(e)}  onChange={(e) => handleTransferQuantity(batch,index)} className="mt-n5 border w-100 rounded p-3" placeholder="Transfer QTY." />
                                </span>
                            </div>
                                {/* <div className="col-6 mt-1">
                                    <span className="rounded p-3 float-right">
                                        <SVG className="mr-2" src={toAbsoluteUrl("/media/svg/icons/project-svg/price-gray.svg")} />
                                        <span className="text-muted mr-2">Rate</span>
                                        <strong>500</strong>
                                    </span>
                                </div> */}
                        </div>
                    </CardBody>
                </Card>
            ))}
        </>
    );
}
