import React from "react";
import { OverlayTrigger, Tooltip } from "react-bootstrap";
import SVG from "react-inlinesvg";
import { toAbsoluteUrl } from "../../../../../../../../../../_metronic/_helpers";

export const ActionsColumnFormatter = (cellContent, row, rowIndex, { openConfirmPage, openViewPage }) => (
  <>
    <OverlayTrigger
      overlay={<Tooltip id="products-edit-tooltip">
        Confirm
      </Tooltip>}
    >
      {
        row.status === "PENDING" ?
          <button
            className="btn light-blue-bg text-success mr-5"
            onClick={() => openConfirmPage(row)}
          >
            Confirm
          </button> :
          <button
            className="btn light-green-bg text-primary mr-5"
            style={{ cursor: "text" }}
          >
            Confirmed
          </button>
      }

    </OverlayTrigger>
  </>
);
