import React,{ useState, useEffect } from "react";
import axios from "axios";
import {
  Card,
  CardBody,
  CardHeader,
  CardHeaderToolbar,
} from "../../../../../_metronic/_partials/controls";
import { useSubheader } from "../../../../../_metronic/layout";
import { AccountYearEditForm } from "./AccountYearEditForm";
import { createAccountingYear } from "../integrate-data/Crud";

const initAccountingYear = { // Initial Account Year Object
  id: undefined,
  fiscalYearName: "",
  startDate: "",
  endDate: "",
  organizationId:"" 
};

export function AccountingYearEdit({
  history,
  id
}) {
// Subheader
const suhbeader = useSubheader();

// Title
const [title, setTitle] = useState("");
  
  useEffect(() => {
    let _title = id ? "" : "New Accounting Year";
    setTitle(_title);
    suhbeader.setTitle(_title);
  }, [id]);

// Data save
const saveAccountingYear = async (values,e) => {
  e.preventDefault();
  createAccountingYear(values);
};


const backToAccoutingYearList = () => {
    history.push(`/master-configure/accounting-year`);
  };

  return (
    <Card>
      <CardHeader title={title}>
        <CardHeaderToolbar>
          <button
            type="button"
            onClick={backToAccoutingYearList}
            className="btn btn-light"
          >
            <i className="fa fa-arrow-left"></i>
            Back
          </button>
        </CardHeaderToolbar>
      </CardHeader>
      <CardBody>
        <div className="mt-5">
            <AccountYearEditForm
              saveAccountingYear={saveAccountingYear}
            />
        </div>
      </CardBody>
    </Card>
  );
}
